def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    """
    Build a CART decision tree on (X_train, y_train) and predict labels for X_test.

    Parameters
    ----------
    X_train : np.ndarray
        Training feature matrix of shape (n_samples, n_features).
    y_train : np.ndarray
        Integer class labels for the training data, shape (n_samples,).
    X_test : np.ndarray
        Feature matrix to classify, shape (m_samples, n_features).
    max_depth : int | None, optional
        Maximum allowed depth of the tree. If None the depth is unlimited.

    Returns
    -------
    list[int]
        Predicted class label for each row in X_test.
    """
    root = Node(X_train, y_train, max_depth)
    root.build_tree()
    predictions = []
    for x in X_test:
        predictions.append(root.predict(x))
    return predictions
class Node:

    def __init__(self, X, y, max_depth, parent=None):
        self.X = X
        self.y = y
        self.max_depth = max_depth
        self.parent = parent
        self.left = None
        self.right = None
        self.label = None

    def build_tree(self):
        if len(np.unique(self.y)) == 1:
            self.label = np.bincount(self.y).argmax()
            return
        if self.max_depth is not None and self.max_depth == 0:
            self.label = np.bincount(self.y).argmax()
            return
        (best_feature, best_threshold) = self.find_best_split()
        self.split(best_feature, best_threshold)
        if self.left is not None:
            self.left.build_tree()
        if self.right is not None:
            self.right.build_tree()

    def find_best_split(self):
        best_feature = None
        best_threshold = None
        best_gini = 0
        for feature in range(self.X.shape[1]):
            thresholds = self.get_thresholds(feature)
            for threshold in thresholds:
                (left_gini, right_gini) = self.gini(self.X[:, feature], self.y, threshold)
                gini_gain = self.gini_gain(left_gini, right_gini)
                if gini_gain > best_gini:
                    best_gini = gini_gain
                    best_feature = feature
                    best_threshold = threshold
        return (best_feature, best_threshold)

    def get_thresholds(self, feature):
        thresholds = []
        for i in range(self.X.shape[0] - 1):
            if self.X[i, feature] != self.X[i + 1, feature]:
                thresholds.append((self.X[i, feature] + self.X[i + 1, feature]) / 2)
        return thresholds

    def gini(self, X, y, threshold):
        left_indices = X < threshold
        right_indices = X >= threshold
        left_y = y[left_indices]
        right_y = y[right_indices]
        left_gini = 1 - np.sum((np.bincount(left_y) / len(left_y)) ** 2)
        right_gini = 1 - np.sum((np.bincount(right_y) / len(right_y)) ** 2)
        return (left_gini, right_gini)

    def gini_gain(self, left_gini, right_gini):
        return self.gini_parent - (len(self.y) * left_gini + len(self.y) * right_gini)

    def gini_parent(self):
        return 1 - np.sum((np.bincount(self.y) / len(self.y)) ** 2)

    def split(self, feature, threshold):
        left_indices = self.X[:, feature] < threshold
        right_indices = self.X[:, feature] >= threshold
        self.left = Node(self.X[left_indices], self.y[left_indices], self.max_depth, self)
        self.right = Node(self.X[right_indices], self.y[right_indices], self.max_depth, self)

    def predict(self, x):
        if self.label is not None:
            return self.label
        if self.left is None and self.right is None:
            return self.parent.predict(x)
        if x[self.parent.X[:, 0] < x[0]].any():
            return self.left.predict(x)
        return self.right.predict(x)

import numpy as np
def metric(name: str):
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """

    def _validate(x, y):
        if not (isinstance(x, (list, tuple, np.ndarray)) and isinstance(y, (list, tuple, np.ndarray))):
            return False
        if len(x) != len(y) or len(x) == 0:
            return False
        if not all((isinstance(i, (int, float)) for i in x)) or not all((isinstance(i, (int, float)) for i in y)):
            return False
        return True

    def _euclidean(x, y):
        return round(np.sqrt(np.sum((np.array(x) - np.array(y)) ** 2)), 4)

    def _manhattan(x, y):
        return round(np.sum(np.abs(np.array(x) - np.array(y))), 4)

    def _chebyshev(x, y):
        return round(np.max(np.abs(np.array(x) - np.array(y))), 4)

    def _cosine(x, y):
        x_norm = np.linalg.norm(np.array(x))
        y_norm = np.linalg.norm(np.array(y))
        if x_norm == 0 or y_norm == 0:
            return -1
        return round(1 - np.dot(np.array(x), np.array(y)) / (x_norm * y_norm), 4)
    metrics = {'euclidean': _euclidean, 'manhattan': _manhattan, 'chebyshev': _chebyshev, 'cosine': _cosine}
    if name not in metrics:
        return lambda x, y: -1

    def d(x, y):
        if not _validate(x, y):
            return -1
        return metrics[name](x, y)
    return d

import numpy as np
def softsign(z):
    """Compute the Softsign activation function.

    Softsign(z) = z / (1 + |z|)

    The function accepts either a scalar or a list/NumPy array. For a scalar it
    returns a float; for an array-like input it returns a list of floats, with
    every element rounded to 4 decimal places.
    """
    if isinstance(z, (int, float)):
        return round(z / (1 + abs(z)), 4)
    elif isinstance(z, np.ndarray):
        return [round(x / (1 + abs(x)), 4) for x in z]
    elif isinstance(z, list):
        return [round(x / (1 + abs(x)), 4) for x in z]
    else:
        raise TypeError('Input must be a scalar, NumPy array, or list')

import functools
from typing import Callable, Any
def doublewrap(decorator_func: Callable[..., Callable[..., Any]]) -> Callable[..., Any]:
    """Return a decorator that can be used *with* or *without* parentheses.

    Args:
        decorator_func: A callable that expects the function to decorate as
            its first positional argument followed only by *optional*
            positional or keyword arguments.

    Returns:
        A new decorator that forwards the call in the correct form so that
        ``decorator_func`` can be applied either directly or after receiving
        its own optional parameters.
    """

    @functools.wraps(decorator_func)
    def wrapper(*args, **kwargs):
        if len(args) == 1 and callable(args[0]):
            return functools.partial(decorator_func, args[0], **kwargs)
        else:
            return decorator_func(*args, **kwargs)
    return wrapper

from collections import deque
def extract_realtime_model(graph: dict[str, list[str]]) -> list[str]:
    """Find a shortest path from 'input' to 'output_realtime'."""
    queue = deque([('input', ['input'])])
    visited = set()
    while queue:
        (node, path) = queue.popleft()
        if node == 'output_realtime':
            return path
        visited.add(node)
        for neighbor in graph.get(node, []):
            if neighbor not in visited:
                queue.append((neighbor, path + [neighbor]))
    return []

import numpy as np
def leaky_relu(z, a=0.01):
    """Apply the Leaky ReLU activation to every element in *z*.

    Args:
        z: A scalar, list (possibly nested) or ``numpy.ndarray`` of numbers.
        a: Optional float in [0,1) — the slope for negative inputs. Defaults to 0.01.

    Returns:
        A Python list with the same structure as *z* where each value has been transformed
        by the Leaky ReLU activation.
    """
    if isinstance(z, np.ndarray):
        return np.where(z >= 0, z, a * z).tolist()
    elif isinstance(z, list):
        return [leaky_relu(i, a) if isinstance(i, (list, np.ndarray)) else leaky_relu(i, a) if isinstance(i, (int, float)) else i for i in z]
    else:
        return a * z if z < 0 else z

