def king_loss_fn(x: int | float) -> float:
    """Compute King’s piece-wise loss for a scalar value.

    The loss function L(x) is defined by four segments:
      • x ≤ 250:   L(x) = –0.25 * x + 82.50372665317208
      • 250 < x ≤ 600: L(x) = 20.00372665317208
      • 600 < x ≤ 700: L(x) = –0.2 * x + 140.00372665317207
      • x > 700:  L(x) = 0.003726653172066108

    The result must be rounded to 4 decimal places.

    Args:
        x: An integer or float input.

    Returns:
        A float rounded to four decimal places.
    """
    if x <= 250:
        return round(-0.25 * x + 82.50372665317208, 4)
    elif 250 < x <= 600:
        return round(20.00372665317208, 4)
    elif 600 < x <= 700:
        return round(-0.2 * x + 140.00372665317207, 4)
    else:
        return round(0.003726653172066108, 4)

import numpy as np
def average_pool2d(X: np.ndarray, pool_shape: tuple[int, int], stride: tuple[int, int], accum_grad: np.ndarray) -> tuple[list, list]:
    """
    Performs forward and backward passes of a 2-D average-pooling layer.

    Args:
    X (np.ndarray): Input feature map with shape (N, C, H, W)
    pool_shape (tuple[int, int]): Pooling window size (p_h, p_w)
    stride (tuple[int, int]): Stride (s_h, s_w)
    accum_grad (np.ndarray): Accumulated gradient with shape identical to forward output

    Returns:
    tuple[list, list]: (pooled_output, grad_input) as lists
    """
    (N, C, H, W) = X.shape
    (p_h, p_w) = pool_shape
    (s_h, s_w) = stride
    pooled_output = np.zeros((N, C, H // s_h, W // s_w))
    for n in range(N):
        for c in range(C):
            for i in range(0, H, s_h):
                for j in range(0, W, s_w):
                    pooled_output[n, c, i // s_h, j // s_w] = np.mean(X[n, c, i:i + p_h, j:j + p_w])
    grad_input = np.zeros_like(X)
    for n in range(N):
        for c in range(C):
            for i in range(0, H, s_h):
                for j in range(0, W, s_w):
                    grad_input[n, c, i:i + p_h, j:j + p_w] += accum_grad[n, c, i // s_h, j // s_w] / (p_h * p_w)
    pooled_output = pooled_output.round(4).tolist()
    grad_input = grad_input.round(4).tolist()
    return (pooled_output, grad_input)

import numpy as np
def squared_error(actual, predicted):
    """Calculate the squared error or mean squared error.

    If *actual* and *predicted* are scalars, return the squared difference.
    If they are one-dimensional iterables (same length), return the mean squared
    error.  Return -1 for length mismatches or empty iterables.

    All results must be rounded to 4 decimal places.
    """
    if isinstance(actual, (int, float)) and isinstance(predicted, (int, float)):
        return round((actual - predicted) ** 2, 4)
    if isinstance(actual, (list, tuple, np.ndarray)) and isinstance(predicted, (list, tuple, np.ndarray)):
        if len(actual) != len(predicted):
            return -1
        if len(actual) == 0:
            return -1
        mse = np.mean((np.array(actual) - np.array(predicted)) ** 2)
        return round(mse, 4)
    return -1

