        lr = warmup_lr * step / warmup_steps
    else:
        lr = decay_lr
    return round(lr, 9)

import numpy as np
def get_initializer(name: str):
    """Return a weight-initialisation callable associated with *name*.

    The callable must have the signature
        initializer(shape: tuple[int,...], scale: float = 1.0) -> np.ndarray
    and create a numpy array filled according to the requested strategy.

    Supported strategies (case-insensitive):
        - "zeros"
        - "ones"
        - "normal"
        - "glorot_uniform"

    If *name* is not recognised the function must raise `ValueError`.
    """
    name = name.lower()

    def zeros_initializer(shape: tuple[int, ...], scale: float=1.0) -> np.ndarray:
        return np.zeros(shape)

    def ones_initializer(shape: tuple[int, ...], scale: float=1.0) -> np.ndarray:
        return scale * np.ones(shape)

    def normal_initializer(shape: tuple[int, ...], scale: float=1.0) -> np.ndarray:
        return scale * np.random.randn(*shape)

    def glorot_uniform_initializer(shape: tuple[int, ...], scale: float=1.0) -> np.ndarray:
        if len(shape) != 2:
            raise ValueError('Glorot uniform initializer only supports 2D shapes')
        fan_in = shape[0]
        fan_out = shape[1]
        limit = scale * np.sqrt(6 / (fan_in + fan_out))
        return np.random.uniform(-limit, limit, shape)
    initializers = {'zeros': zeros_initializer, 'ones': ones_initializer, 'normal': normal_initializer, 'glorot_uniform': glorot_uniform_initializer}
    if name not in initializers:
        raise ValueError(f'Unsupported initialisation strategy: {name}')
    return initializers[name]

import heapq
def huffman_encode(tokens: list[str]) -> dict[str, str]:
    """Generate a Huffman code for the supplied tokens.

    The function must return a dictionary mapping **every** unique token in
    *tokens* to its binary Huffman code.  All codes must be prefix-free and
    consist only of the characters ‘0’ and ‘1’.
    """
    frequency = {}
    for token in tokens:
        if token not in frequency:
            frequency[token] = 0
        frequency[token] += 1
    heap = []
    for (token, freq) in frequency.items():
        node = {'token': token, 'freq': freq, 'left': None, 'right': None}
        heapq.heappush(heap, (freq, node))
    while len(heap) > 1:
        (freq1, node1) = heapq.heappop(heap)
        (freq2, node2) = heapq.heappop(heap)
        parent = {'token': '', 'freq': freq1 + freq2, 'left': node1, 'right': node2}
        heapq.heappush(heap, (parent['freq'], parent))
    codes = {}

    def traverse(node, code):
        if node['token']:
            codes[node['token']] = code
        if node['left']:
            traverse(node['left'], code + '0')
        if node['right']:
            traverse(node['right'], code + '1')
    traverse(heap[0][1], '')
    return codes

