import re
def apply_activation(x, param=None):
    """Apply a chosen activation to the input vector.

    Args:
        x (list[float] | np.ndarray): Input data. Anything that can be
            converted to a NumPy array of floats is accepted.
        param (str | None, optional): Description of the activation. See the
            task specification for the accepted values. If *None* the
            identity function is applied.

    Returns:
        list[float]: Result of applying the activation element-wise, rounded
            to six decimals and converted to a standard Python *list*.
    """
    x = np.array(x, dtype=float)
    if param is None or param.lower() == 'identity':
        return x.tolist()
    elif param.lower() == 'relu':
        return np.maximum(x, 0).tolist()
    elif param.lower() == 'tanh':
        return np.tanh(x).tolist()
    elif param.lower() == 'sigmoid':
        return np.round(np.reciprocal(1 + np.exp(-x)), 6).tolist()
    elif param.lower().startswith('leaky relu'):
        alpha = 0.01 if param.lower().find('alpha=') == -1 else float(re.search('alpha=([0-9\\.]+)', param.lower()).group(1))
        return np.where(x > 0, x, alpha * x).tolist()
    elif param.lower().startswith('affine'):
        match = re.search('slope=([0-9\\.]+), intercept=([0-9\\.]+)', param.lower())
        if match:
            slope = float(match.group(1))
            intercept = float(match.group(2))
            return np.round(slope * x + intercept, 6).tolist()
    else:
        raise ValueError(f'Unknown activation: {param}')

def get_metric(name: str):
    """Return an evaluation metric function by *name*.

    Supported names:
    - 'accuracy'
    - 'precision'
    - 'recall'
    - 'f1'

    If the name is invalid, the function should return -1.
    The returned metric functions must accept two equal-length iterables
    (y_true, y_pred) containing only 0s and 1s and return the metric value
    rounded to 4 decimal places.
    """
    if name == 'accuracy':

        def accuracy(y_true, y_pred):
            """Return the accuracy of the predictions."""
            correct = sum((1 for (yt, yp) in zip(y_true, y_pred) if yt == yp))
            return round(correct / len(y_true), 4)
    elif name == 'precision':

        def precision(y_true, y_pred):
            """Return the precision of the predictions."""
            tp = sum((1 for (yt, yp) in zip(y_true, y_pred) if yt == yp == 1))
            fp = sum((1 for (yt, yp) in zip(y_true, y_pred) if yt == 0 and yp == 1))
            if tp + fp == 0:
                return 0
            return round(tp / (tp + fp), 4)
    elif name == 'recall':

        def recall(y_true, y_pred):
            """Return the recall of the predictions."""
            tp = sum((1 for (yt, yp) in zip(y_true, y_pred) if yt == yp == 1))
            fn = sum((1 for (yt, yp) in zip(y_true, y_pred) if yt == 1 and yp == 0))
            if tp + fn == 0:
                return 0
            return round(tp / (tp + fn), 4)
    elif name == 'f1':

        def f1(y_true, y_pred):
            """Return the F1 score of the predictions."""
            p = precision(y_true, y_pred)
            r = recall(y_true, y_pred)
            if p + r == 0:
                return 0
            return round(2 * p * r / (p + r), 4)
    else:
        return -1
    return locals()[name]

def classification_accuracy(y_true: list, y_pred: list) -> float:
    """Compute the accuracy between the true and predicted class labels.

    Args:
        y_true (list): Ground-truth class labels.
        y_pred (list): Predicted class labels produced by a model.

    Returns:
        float: The accuracy score rounded to 4 decimal places.
              Returns -1 when the inputs have different lengths or when
              either of the inputs is empty.
    """
    if len(y_true) != len(y_pred):
        return -1
    if not y_true or not y_pred:
        return -1
    correct_predictions = 0
    for (true_label, predicted_label) in zip(y_true, y_pred):
        if true_label == predicted_label:
            correct_predictions += 1
    accuracy = correct_predictions / len(y_true)
    return round(accuracy, 4)

from typing import List, Dict, Set, Tuple
def topological_sort(vertices: list, edges: list[tuple]) -> list | None:
    """Determine a topological ordering of a directed graph.

    The implementation **must** use depth-first search (DFS) and follow the
    deterministic exploration rule described in the task description.

    Args:
        vertices: List of vertex identifiers (hashable).
        edges:    List of directed edges given as 2-tuples (source, target).

    Returns:
        • List of vertices in topological order when the graph is acyclic.
        • ``None`` when the graph contains at least one directed cycle.
    """
    graph: Dict[int, List[int]] = {v: [] for v in vertices}
    for (u, v) in edges:
        graph[u].append(v)
    visited: Set[int] = set()
    current_path: Set[int] = set()
    order: List[int] = []

    def dfs(vertex: int) -> bool:
        visited.add(vertex)
        current_path.add(vertex)
        for neighbour in graph[vertex]:
            if neighbour not in visited:
                if not dfs(neighbour):
                    return False
            elif neighbour in current_path:
                return False
        current_path.remove(vertex)
        order.append(vertex)
        return True
    for vertex in vertices:
        if vertex not in visited:
            if not dfs(vertex):
                return None
    return order[::-1]

import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    if order not in [0, 1, 2]:
        return -1
    if isinstance(x, list):
        x = np.array(x)
    sigmoid_value = 1 / (1 + np.exp(-x))
    if order == 1:
        derivative = sigmoid_value * (1 - sigmoid_value)
    elif order == 2:
        derivative = sigmoid_value * (1 - sigmoid_value) * (1 - 2 * sigmoid_value)
    if isinstance(x, np.ndarray):
        result = derivative.tolist()
    else:
        result = round(derivative, 4)
    return result

