def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """
    Compute the Radial Basis Function (RBF) kernel matrix.

    Args:
        X (list[list[int | float]]): The first collection of N-dimensional vectors.
        Y (list[list[int | float]] | None, optional): The second collection of M-dimensional vectors. Defaults to None.
        sigma (float | list[float] | None, optional): The scale (band-width) parameter. Defaults to None.

    Returns:
        list[list[float]]: The kernel matrix rounded to four decimal places.
    """
    if sigma is None:
        sigma = np.sqrt(len(X[0]) / 2)
    elif isinstance(sigma, list):
        if len(sigma) != len(X[0]):
            return -1
        for s in sigma:
            if s <= 0:
                return -1
    elif not isinstance(sigma, (int, float)) or sigma <= 0:
        return -1
    if Y is not None and len(X[0]) != len(Y[0]):
        return -1
    if Y is None:
        Y = X
    kernel_matrix = [[np.exp(-0.5 * np.sum((np.array(x) - np.array(y)) ** 2 / s ** 2)) for y in Y] for (x, s) in zip(X, [sigma] * len(X))]
    kernel_matrix = [[round(x, 4) for x in row] for row in kernel_matrix]
    return kernel_matrix

