from typing import List, Tuple
def astar(grid: List[List[int]], start: Tuple[int, int], dest: Tuple[int, int]) -> int:
    """A* search on a 2-D grid.

    Args:
        grid: 2-D list containing only 0 (free) and 1 (obstacle).
        start: (row, column) pair indicating the start position.
        dest:  (row, column) pair indicating the goal position.

    Returns:
        The minimum number of moves needed to reach *dest* from *start* using
        4-directional movement, or ‑1 if the destination is unreachable.
    """
    movements = [(0, 1), (0, -1), (1, 0), (-1, 0)]

    def is_valid(cell: Tuple[int, int]) -> bool:
        return 0 <= cell[0] < len(grid) and 0 <= cell[1] < len(grid[0])

    def heuristic(cell: Tuple[int, int]) -> int:
        return abs(cell[0] - dest[0]) + abs(cell[1] - dest[1])
    queue = [(0, start)]
    visited = set()
    came_from = {}
    while queue:
        (_, current) = heapq.heappop(queue)
        if current == dest:
            path = []
            while current in came_from:
                path.append(current)
                current = came_from[current]
            return len(path) - 1
        visited.add(current)
        for movement in movements:
            neighbor = (current[0] + movement[0], current[1] + movement[1])
            if not is_valid(neighbor) or grid[neighbor[0]][neighbor[1]] == 1:
                continue
            g = len(path) if current in came_from else 0
            f = g + heuristic(neighbor)
            if neighbor not in visited or f < came_from.get(neighbor, float('inf')):
                came_from[neighbor] = current
                heapq.heappush(queue, (f, neighbor))
    return -1

