from typing import Any, Dict, List
def build_tree(data: List[List[Any]]) -> Dict[str, Any]:
    """Build a CART decision tree using the Gini impurity criterion.

    The *data* argument is a list where every inner list represents a training
    sample.  All but the last elements of each sample are feature values; the
    last element is the class label.

    The function should return the root of the generated tree expressed as a
    nested dictionary:
        Leaf   -> {"label": class_label}
        Branch -> {"feature": index,
                    "value":   split_value,
                    "left":    <subtree for samples with feature == value>,
                    "right":   <subtree for samples with feature != value>}

    If *data* is empty return the empty dictionary.
    """
    if not data:
        return {}
    gini = gini_impurity([sample[-1] for sample in data])
    if gini == 0:
        return {'label': Counter([sample[-1] for sample in data]).most_common(1)[0][0]}
    (best_feature, best_value, best_gini_gain) = (None, None, 0)
    for feature_index in range(len(data[0]) - 1):
        unique_values = set((sample[feature_index] for sample in data))
        for value in sorted(unique_values):
            (left_data, right_data) = split_data(data, feature_index, value)
            if left_data and right_data:
                gini_left = gini_impurity([sample[-1] for sample in left_data])
                gini_right = gini_impurity([sample[-1] for sample in right_data])
                gini_gain = gini - len(left_data) / len(data) * gini_left - len(right_data) / len(data) * gini_right
                if gini_gain > best_gini_gain:
                    (best_feature, best_value, best_gini_gain) = (feature_index, value, gini_gain)
    left_subtree = build_tree([sample for sample in data if sample[best_feature] == best_value])
    right_subtree = build_tree([sample for sample in data if sample[best_feature] != best_value])
    return {'feature': best_feature, 'value': best_value, 'left': left_subtree, 'right': right_subtree}
def gini_impurity(labels: List[Any]) -> float:
    """Compute the Gini impurity of a list of labels."""
    label_counts = Counter(labels)
    gini = 1
    for count in label_counts.values():
        gini -= (count / len(labels)) ** 2
    return gini
def split_data(data: List[List[Any]], feature_index: int, value: Any) -> (List[List[Any]], List[List[Any]]):
    """Split the data into two subsets based on the given feature and value."""
    left_data = [sample for sample in data if sample[feature_index] == value]
    right_data = [sample for sample in data if sample[feature_index] != value]
    return (left_data, right_data)

import numpy as np
from collections import deque
def dbscan(data: np.ndarray, eps: float, min_samples: int) -> list[int]:
    """Density-Based Spatial Clustering (DBSCAN).

    Parameters
    ----------
    data : np.ndarray
        A 2-D array with shape (n_samples, n_features).
    eps : float
        Radius that defines the neighbourhood of a point.
    min_samples : int
        Minimum number of points required to form a dense region (core point).

    Returns
    -------
    list[int]
        Cluster labels for every sample.  Noise points receive the label –1
        while clusters are numbered 1, 2, 3, … in the order they are found.
    """
    n_samples = data.shape[0]
    labels = np.full(n_samples, -1, dtype=int)
    for i in range(n_samples):
        if labels[i] != -1:
            continue
        neighbors = []
        for j in range(n_samples):
            if i != j and np.linalg.norm(data[i] - data[j]) <= eps:
                neighbors.append(j)
        if len(neighbors) >= min_samples:
            labels[i] = 1
            queue = deque([i])
            while queue:
                current_point = queue.popleft()
                for neighbor in neighbors:
                    if labels[neighbor] == -1:
                        labels[neighbor] = labels[current_point]
                        queue.append(neighbor)
                        for j in range(n_samples):
                            if neighbor != j and np.linalg.norm(data[neighbor] - data[j]) <= eps:
                                neighbors[j].append(neighbor)
    return labels.tolist()

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    if task_type == 'classification':
        return _predict_classification(root, features)
    elif task_type == 'regression':
        return _predict_regression(root, features)
    else:
        raise ValueError("Invalid task type. It should be either 'classification' or 'regression'.")
def _predict_classification(node: 'Node', features: list | tuple):
    if hasattr(node, 'y'):
        return node.y
    else:
        feature_index = node.label
        feature_value = features[feature_index]
        for child in node.child:
            if hasattr(child, 'x') and child.x == feature_value:
                return _predict_classification(child, features)
        return _predict_classification(node.child[1], features)
def _predict_regression(node: 'Node', features: list | tuple):
    if hasattr(node, 'y'):
        return node.y
    else:
        feature_index = node.label
        feature_value = features[feature_index]
        if feature_value <= node.s:
            return _predict_regression(node.child[0], features)
        else:
            return _predict_regression(node.child[1], features)

