from collections import defaultdict
import numpy as np
def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    """
    Maximum Entropy classifier with Generalized Iterative Scaling.

    Parameters:
    X_train (np.ndarray): Training samples.
    y_train (np.ndarray): Training labels.
    X_test (np.ndarray): Samples to classify.
    epsilon (float, optional): Convergence threshold. Defaults to 1e-3.
    n_iter (int, optional): Maximum number of iterations. Defaults to 100.

    Returns:
    list[int]: Predicted labels for every row in X_test.
    """
    (num_features, num_labels) = (X_train.shape[1], np.max(y_train) + 1)
    weights = np.zeros((num_features, num_labels, num_labels))
    empirical_distributions = defaultdict(lambda : defaultdict(int))
    for (i, row) in enumerate(X_train):
        for (j, feature) in enumerate(row):
            feature_value = feature
            label = y_train[i]
            empirical_distributions[j, feature_value][label] += 1
    for ((feature_index, feature_value), label_counts) in empirical_distributions.items():
        total = sum(label_counts.values())
        for (label, count) in label_counts.items():
            empirical_distributions[feature_index, feature_value][label] /= total
    max_update = float('inf')
    for _ in range(n_iter):
        new_weights = np.zeros(weights.shape)
        for (i, row) in enumerate(X_train):
            for (j, feature) in enumerate(row):
                feature_value = feature
                label = y_train[i]
                for k in range(num_labels):
                    for l in range(num_labels):
                        new_weights[j, feature_value, k] += weights[j, feature_value, l] * empirical_distributions[j, feature_value][l]
        max_update = 0
        for j in range(num_features):
            for feature_value in range(num_labels):
                for k in range(num_labels):
                    update = abs(new_weights[j, feature_value, k] - weights[j, feature_value, k])
                    max_update = max(max_update, update)
        weights = new_weights
        if max_update < epsilon:
            break
    predicted_labels = []
    for (i, row) in enumerate(X_test):
        max_prob = -math.inf
        predicted_label = -1
        for (j, feature) in enumerate(row):
            feature_value = feature
            for k in range(num_labels):
                prob = math.exp(np.sum(weights[j, feature_value, k] * X_test[i]))
                if prob > max_prob:
                    max_prob = prob
                    predicted_label = k
        predicted_labels.append(predicted_label)
    return predicted_labels

import numpy as np
def minesweeper_step(ground: list[list[float | int]], flags: list[list[bool]]) -> tuple[list[tuple[int, int]], list[tuple[int, int]]]:
    """Perform one logical deduction step on a Minesweeper board.

    The function receives the currently visible part of the board (```ground```)
    and the already placed flags (```flags```). It must return two *sorted* lists:

    1. **clicks** – every square that can be safely revealed.
    2. **new_flags** – every square that must be a bomb according to the rules
       described in the task description.

    Notes:
        * ``ground`` and ``flags`` have identical dimensions.
        * Hidden squares are represented by ``np.nan`` inside ``ground``.
        * Neighbourhood is the 8-connected Moore neighbourhood.
    """
    ground = np.array(ground)
    flags = np.array(flags)
    clicks = []
    new_flags = []
    for r in range(ground.shape[0]):
        for c in range(ground.shape[1]):
            if not np.isnan(ground[r, c]) and (not flags[r, c]):
                value = int(ground[r, c])
                bombs = 0
                for dr in [-1, 0, 1]:
                    for dc in [-1, 0, 1]:
                        if 0 <= r + dr < ground.shape[0] and 0 <= c + dc < ground.shape[1]:
                            if flags[r + dr, c + dc]:
                                bombs += 1
                unexplored = 0
                for dr in [-1, 0, 1]:
                    for dc in [-1, 0, 1]:
                        if 0 <= r + dr < ground.shape[0] and 0 <= c + dc < ground.shape[1]:
                            if np.isnan(ground[r + dr, c + dc]) and (not flags[r + dr, c + dc]):
                                unexplored += 1
                if value == 0:
                    for dr in [-1, 0, 1]:
                        for dc in [-1, 0, 1]:
                            if 0 <= r + dr < ground.shape[0] and 0 <= c + dc < ground.shape[1]:
                                if np.isnan(ground[r + dr, c + dc]) and (not flags[r + dr, c + dc]):
                                    clicks.append((r + dr, c + dc))
                elif value == 8:
                    for dr in [-1, 0, 1]:
                        for dc in [-1, 0, 1]:
                            if 0 <= r + dr < ground.shape[0] and 0 <= c + dc < ground.shape[1]:
                                if np.isnan(ground[r + dr, c + dc]) and (not flags[r + dr, c + dc]):
                                    new_flags.append((r + dr, c + dc))
                elif bombs == value:
                    for dr in [-1, 0, 1]:
                        for dc in [-1, 0, 1]:
                            if 0 <= r + dr < ground.shape[0] and 0 <= c + dc < ground.shape[1]:
                                if np.isnan(ground[r + dr, c + dc]) and (not flags[r + dr, c + dc]):
                                    clicks.append((r + dr, c + dc))
                elif unexplored == value:
                    for dr in [-1, 0, 1]:
                        for dc in [-1, 0, 1]:
                            if 0 <= r + dr < ground.shape[0] and 0 <= c + dc < ground.shape[1]:
                                if np.isnan(ground[r + dr, c + dc]) and (not flags[r + dr, c + dc]):
                                    new_flags.append((r + dr, c + dc))
    clicks = sorted(list(set(clicks)))
    new_flags = sorted(list(set(new_flags)))
    return (clicks, new_flags)

from copy import deepcopy
def deduce_mines(constraints: list[list]) -> tuple[list[str], list[str]]:
    """Simplify Minesweeper constraints.

    You receive *constraints* as a list of 2-element lists *[variables, value]*
    where *variables* is a list of identifiers and *value* is an int telling how
    many of those variables hide a mine.

    The function must apply the logical rules described in the task statement
    (solved constraints, propagation, subset rule, duplicate removal) until no
    new information can be obtained.

    The result is a tuple (safe, mines) of two **alphabetically sorted** lists.
    The first element contains all variables that are certainly safe, the second
    one all variables that are certainly mines.  If nothing can be deduced an
    empty list must be returned instead.
    """
    safe = set()
    mines = set()
    new_constraints = []
    for (variables, value) in constraints:
        if value == 0:
            safe.update(variables)
        elif value == len(variables):
            mines.update(variables)
        else:
            new_constraints.append((variables, value))
    variables_to_remove = set(safe) | set(mines)
    new_constraints = [(variables, value) for (variables, value) in new_constraints if not variables_to_remove.intersection(variables)]
    for (variables, value) in new_constraints:
        value -= len(variables_to_remove.intersection(variables))
    new_constraints = []
    for (variables1, value1) in constraints:
        for (variables2, value2) in new_constraints:
            if set(variables1).issubset(set(variables2)):
                new_constraints.append((tuple(sorted(set(variables2) - set(variables1))), value2 - value1))
                break
        else:
            new_constraints.append((variables1, value1))
    new_constraints = [tuple(sorted((variables, value))) for (variables, value) in new_constraints]
    new_constraints = [list(x) for x in set(new_constraints)]
    safe = set()
    mines = set()
    for (variables, value) in new_constraints:
        if value == 0:
            safe.update(variables)
        elif value == len(variables):
            mines.update(variables)
    return (sorted(list(safe)), sorted(list(mines)))

def maze_to_graph(maze: list[list[int]]) -> dict[tuple[int, int], list[tuple[int, int]]]:
    """Convert a binary maze into an adjacency-list graph.

    A 0 in the maze denotes an open cell, while 1 denotes a wall.  Two open
    cells are adjacent if they share an edge (up, down, left, right).

    Args:
        maze: Rectangular 2-D list containing only 0s and 1s.

    Returns:
        A dictionary mapping each open cell coordinate (row, col) to a sorted
        list of neighbouring open cell coordinates.  Walls are not included in
        the dictionary.  If there are no open cells the function returns an
        empty dictionary.
    """
    graph = {}
    (rows, cols) = (len(maze), len(maze[0]))
    for row in range(rows):
        for col in range(cols):
            if maze[row][col] == 0:
                graph[row, col] = []
                for (dr, dc) in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
                    (nr, nc) = (row + dr, col + dc)
                    if 0 <= nr < rows and 0 <= nc < cols:
                        if maze[nr][nc] == 0:
                            graph[row, col].append((nr, nc))
    for node in graph:
        graph[node].sort()
    return graph
