class Node:

    def __init__(self, label: int, x: float, s: float, y: int, child: list):
        self.label = label
        self.x = x
        self.s = s
        self.y = y
        self.child = child
def predict_sample(root: Node, features: list | tuple, task_type: str) -> int:
    """
    Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    if task_type == 'classification':
        if root.y is not None:
            return root.y
        else:
            for feature in features:
                for child in root.child:
                    if child.x == feature:
                        return predict_sample(child, features, task_type)
            return None
    elif task_type == 'regression':
        if root.y is not None:
            return root.y
        else:
            for feature in features:
                for child in root.child:
                    if feature <= child.x:
                        return predict_sample(child, features, task_type)
            return None
    else:
        raise ValueError("Invalid task_type. It should be either 'classification' or 'regression'.")
