        # If the current node is a leaf node, return its value
        if 'value' in tree:
            return tree['value']
        # If the feature value is less than the threshold, go to the true branch
        elif sample[tree['feature_i']] <= tree['threshold']:
            return evaluate_decision_tree(tree['true_branch'], sample)
        # Otherwise, go to the false branch
