        # Get the true and false branches from the current node
        true_branch, false_branch = tree['true_branch'], tree['false_branch']
        # Recursively traverse the true and false branches
        return evaluate_decision_tree(true_branch, sample) if sample[feature_i] <= threshold else evaluate_decision_tree(false_branch, sample)
