def build_tree(items, counts, parents):
    """
    Construct a rooted tree from parallel *items*, *counts* and *parents* lists.

    Parameters
    ----------
    items : list[str]
        The label (item) stored in each node.
    counts : list[int]
        The frequency (count) stored in each node.
    parents : list[int]
        The parent index of each node; –1 marks the single root.

    Returns
    -------
    dict
        The root node represented as a nested dictionary with keys
        'item', 'count' and 'children'.  If the inputs are empty, returns
        an empty dictionary.
    """
    if not items or not counts or (not parents):
        return {}
    tree = {}
    root = 0
    for (i, (item, count)) in enumerate(zip(items, counts)):
        if parents[i] != -1:
            tree[item] = {'item': item, 'count': count, 'children': []}
            tree[item]['children'].append({'item': item, 'count': count, 'children': []})
        else:
            tree[item] = {'item': item, 'count': count, 'children': []}
    tree[root]['children'].append({'item': root, 'count': counts[root], 'children': []})
    return tree

