import numpy as np
TIME_STEPS = 20
def string_to_int(text: str, time_steps: int, vocabulary: dict[str, int]) -> list[int]:
    """Converts a raw text string into a fixed-length list of integer token ids."""
    encoded_ids = []
    for char in text:
        if char in vocabulary:
            encoded_ids.append(vocabulary[char])
        else:
            encoded_ids.append(0)
    if len(encoded_ids) < time_steps:
        encoded_ids += [0] * (time_steps - len(encoded_ids))
    return encoded_ids
def int_to_string(indices, inverse_vocab: dict[int, str]) -> str:
    """Converts a list of integer ids back to text using an inverse vocabulary."""
    decoded_text = ''
    for index in indices:
        if index in inverse_vocab:
            decoded_text += inverse_vocab[index]
        else:
            decoded_text += '0'
    return decoded_text
def run_example(model, input_vocabulary: dict[str, int], inv_output_vocabulary: dict[int, str], text: str) -> str:
    """Runs the character-level sequence encoding and decoding process."""
    encoded_ids = string_to_int(text, TIME_STEPS, input_vocabulary)
    output = model.predict(encoded_ids)
    predicted_ids = np.argmax(output, axis=-1)
    decoded_text = int_to_string(predicted_ids, inv_output_vocabulary)
    return decoded_text

