from heapq import heappop, heappush
from typing import List, Tuple, Union
def find_shortest_path(grid: List[List[int]]) -> Union[List[Tuple[int, int]], int]:
    """
    Find a shortest path from the top-left to the bottom-right corner of a maze.

    Args:
        grid: Rectangular 2-D list of integers (0 or 1).

    Returns:
        A list of (row, column) tuples that represents a shortest path
        including the start (0,0) and goal (m-1,n-1) positions.
        -1 if no such path exists.
    """
    movements = [(0, 1), (0, -1), (1, 0), (-1, 0)]

    def heuristic(a, b):
        return abs(a[0] - b[0]) + abs(a[1] - b[1])

    def a_star_search(start, goal):
        open_list = []
        closed_list = set()
        cost = {start: 0}
        previous = {}
        heappush(open_list, (0, start))
        while open_list:
            (current_cost, current_node) = heappop(open_list)
            if current_node == goal:
                path = []
                while current_node in previous:
                    path.append(current_node)
                    current_node = previous[current_node]
                path.append(start)
                path.reverse()
                return path
            closed_list.add(current_node)
            for movement in movements:
                neighbor = (current_node[0] + movement[0], current_node[1] + movement[1])
                if 0 <= neighbor[0] < len(grid) and 0 <= neighbor[1] < len(grid[0]) and (grid[neighbor[0]][neighbor[1]] == 0) and (neighbor not in closed_list):
                    tentative_cost = cost[current_node] + 1
                    if neighbor not in cost or tentative_cost < cost[neighbor]:
                        cost[neighbor] = tentative_cost
                        previous[neighbor] = current_node
                        heappush(open_list, (tentative_cost + heuristic(neighbor, goal), neighbor))
        return -1
    return a_star_search((0, 0), (len(grid) - 1, len(grid[0]) - 1))

import numpy as np
def metric(name: str) -> callable:
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """

    def d(x: list, y: list) -> float:
        if not isinstance(x, (list, tuple, np.ndarray)):
            return -1
        if len(x) != len(y):
            return -1
        if len(x) == 0 or len(y) == 0:
            return -1
        if name not in ['euclidean', 'manhattan', 'chebyshev', 'cosine']:
            return -1
        if name == 'cosine':
            if np.allclose(np.linalg.norm(x), 0) or np.allclose(np.linalg.norm(y), 0):
                return -1
        if name == 'euclidean':
            return np.sqrt(np.sum((x - y) ** 2))
        elif name == 'manhattan':
            return np.sum(np.abs(x - y))
        elif name == 'chebyshev':
            return np.max(np.abs(x - y))
        else:
            return 1 - x * y / (np.linalg.norm(x) * np.linalg.norm(y))
    return d

