def usable_ace(hand: list[int]) -> bool:
    """
    Determine whether a Blackjack hand has a usable ace.

    A *usable ace* is an ace that can be counted as 11 without causing the
    hand's total value to exceed 21.

    Args:
        hand: List of integers where each integer represents the value of a card
              in a Blackjack hand (ace is 1, face cards are 10, etc.).

    Returns:
        True if the hand contains a usable ace, otherwise False.
    """
    has_ace_used = False
    for card in hand:
        if card == 1:
            has_ace_used = True
        elif card in [2, 3, 4, 5, 6, 7, 8, 9, 10]:
            if has_ace_used:
                return True
            else:
                total_value = sum(hand) - 10
                if total_value == 11:
                    return True
    return False

import numpy as np
def average_pool2d(X: np.ndarray, pool_shape: tuple[int, int], stride: tuple[int, int], accum_grad: np.ndarray) -> tuple[list, list]:
    """
    Performs forward and backward passes of a 2-D average-pooling layer.

    Args:
    X (np.ndarray): Input feature map with shape (N, C, H, W)
    pool_shape (tuple[int, int]): Pooling window size
    stride (tuple[int, int]): Stride of the pooling operation
    accum_grad (np.ndarray): Gradient of the loss with respect to the pooled output

    Returns:
    tuple[list, list]: Pooled output and gradient of the loss with respect to the pooled output
    """
    pooled_output = np.zeros_like(X, dtype=X.dtype)
    for i in range(X.shape[0]):
        for j in range(X.shape[1]):
            for k in range(X.shape[2]):
                for l in range(X.shape[3]):
                    window_sum = np.sum(X[i, j, k, l] * np.exp(-((i - k) / stride[0]) ** 2 - ((j - l) / stride[1]) ** 2))
                    pooled_output[i, j, k, l] += window_sum * np.exp(-((i - k) / stride[0]) ** 2 - ((j - l) / stride[1]) ** 2)
    grad_input = np.zeros_like(X, dtype=X.dtype)
    for i in range(X.shape[0]):
        for j in range(X.shape[1]):
            for k in range(X.shape[2]):
                for l in range(X.shape[3]):
                    grad_input[i, j, k, l] = accum_grad[i, j, k, l] - pooled_output[i, j, k, l]
    pooled_output = [list((map(round, x) for x in pooled_output.tolist())) for i in range(X.shape[0])]
    grad_input = [list((map(round, x) for x in grad_input.tolist()))]
    return (pooled_output, grad_input)

