def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """
    Compute the Radial Basis Function (RBF) kernel matrix.

    The function follows the specifications given in the task description.
    It returns -1 on invalid input, otherwise a nested list containing the kernel matrix rounded to four decimal places.

    Args:
        X (list[list[int | float]]): The input feature matrix.
        Y (list[list[int | float]] | None, optional): The target matrix. Defaults to None.
        sigma (float | list[float] | None, optional): The scale parameter. Defaults to None.

    Returns:
        list[list[float]]: The kernel matrix.
    """
    if sigma is None:
        if len(X) != len(Y):
            return -1
        if len(X[0]) != len(Y[0]):
            return -1
        if sigma is not None and (not isinstance(sigma, list)):
            sigma = [sigma]
        if not isinstance(sigma, (int, float)):
            return -1
        if sigma <= 0:
