    elif isinstance(sigma, list):
        sigma = np.array(sigma)
    else:
        sigma = np.array(sigma)
    kernel_matrix = np.exp(-0.5 * np.sum((X - np.array([0])) ** 2, axis=1) / sigma ** 2)
