def flatten_parameters(params: Dict[str, Any]) -> Dict[str, Any]:
    """
    Flatten a nested parameter dictionary.

    A dot (`.`) is used to join nested keys.  The input dictionary must not be
    mutated.

    Args:
        params: A potentially nested dictionary where some values can be other
                dictionaries.

    Returns:
        A new dictionary without nested structures.
    """

    def _flatten(obj: Dict[str, Any], prefix: str='') -> Dict[str, Any]:
        """Helper function to recursively flatten the input dictionary."""
        result: Dict[str, Any] = {}
        for (key, value) in obj.items():
            new_key = prefix + key if prefix else key
            if isinstance(value, dict):
                result.update(_flatten(value, new_key + '.'))
            elif isinstance(value, (list, tuple)):
                result.update({new_key + '.item': item for item in value})
            elif isinstance(value, str):
                result[new_key] = value
            elif isinstance(value, (int, float)):
                result[new_key] = value
            else:
                raise ValueError(f'Unsupported type: {type(value)}')
        return result
    return _flatten(params)

