                            if (x, y) != (i, j) and (not flags[x][y]):
                                unexplored += 1
                    if ground[i][j] == 0:
                        clicks.append((i, j))
                        new_flags.append((i, j))
                    elif ground[i][j] == 8:
                        clicks.append((i, j))
                        new_flags.append((i, j))
                    else:
                        bombs = unexplored
                        unexplored = 0
                        for x in range(max(0, i - 1), min(len(ground), i + 2)):
