def tanh_activation(x, derivative: bool=False) -> list[float]:
    """
    Compute tanh or its derivative element-wise.

    Args:
        x: A 1-D list or NumPy array of numbers.
        derivative: If True, return the gradient of tanh. Otherwise return tanh.

    Returns:
        Python list with each element rounded to 4 decimals.
    """
    x = np.array(x)
    if derivative:
        return np.round(1 - np.tanh(x) ** 2, 4).tolist()
    else:
        return np.round(np.tanh(x), 4).tolist()

from typing import List, Union
Number = Union[int, float]
def k_smallest(nums: List[Number], k: int) -> List[Number]:
    """
    Return the k smallest numbers contained in *nums*.

    The function should scan the input list once, maintaining a custom
    max-heap (size at most k) implemented with a Python list.  At the end,
    return the k smallest elements in ascending order.  If *k* is invalid
    (≤0 or larger than len(nums)) return -1.

    The use of the built-in ``heapq`` module is **not** allowed.
    """
    if k <= 0 or k > len(nums):
        return -1
    heap = nums[:k]

    def move_up(index: int) -> None:
        parent_index = (index - 1) // 2
        if parent_index < 0:
            return
        if heap[parent_index] > heap[index]:
            (heap[parent_index], heap[index]) = (heap[index], heap[parent_index])
            move_up(parent_index)

    def move_down(index: int) -> None:
        left_child_index = 2 * index + 1
        right_child_index = 2 * index + 2
        smallest = index
        if left_child_index < len(heap) and heap[left_child_index] < heap[smallest]:
            smallest = left_child_index
        if right_child_index < len(heap) and heap[right_child_index] < heap[smallest]:
            smallest = right_child_index
        if smallest != index:
            (heap[index], heap[smallest]) = (heap[smallest], heap[index])
            move_down(smallest)
    for i in range(k, len(nums)):
        if heap[0] > nums[i]:
            heap[0] = nums[i]
            move_up(0)
        else:
            for j in range(i, 0, -1):
                if heap[j] < nums[i]:
                    break
                heap[j] = nums[i]
                i = j - 1
            heap[0] = nums[i]
            move_down(0)
    return sorted(heap)

