def create_encoding_dicts(obs_ids: list, action_ids: list, obs_encoder: dict | None=None, act_encoder: dict | None=None) -> dict:
    """
    Generate forward and backward mappings for observations and actions.

    Args:
        obs_ids (list): All distinct observation identifiers.
        action_ids (list): All distinct action identifiers.
        obs_encoder (dict | None, optional): Existing obs→number mapping.
        act_encoder (dict | None, optional): Existing act→number mapping.

    Returns:
        dict: A dictionary that contains the keys 'obs2num', 'num2obs',
        'act2num', and 'num2act'.
    """
    obs2num = obs_encoder or {}
    act2num = act_encoder or {}
    num2obs = {v: k for (k, v) in obs2num.items()}
    num2act = {v: k for (k, v) in act2num.items()}
    if obs_ids:
        for (i, obs) in enumerate(obs_ids):
            obs2num[obs] = i
            num2obs[i] = obs
        for (i, act) in enumerate(action_ids):
            act2num[act] = i
            num2act[i] = act
    return {'obs2num': obs2num, 'num2obs': num2obs, 'act2num': act2num, 'num2act': num2act}

import numpy as np
def sigmoid(x):
    """Element-wise logistic sigmoid."""
    return np.clip(np.exp(x) / (1 + np.exp(x)), 0, 1)
def tanh(x):
    """Element-wise hyperbolic tangent."""
    return np.tanh(x)
def relu(x):
    """Element-wise Rectified Linear Unit."""
    return np.maximum(x, 0)
def leaky_relu(x, alpha: float=0.01):
    """Element-wise Leaky-ReLU with slope *alpha* for x<0."""
    return np.where(x < 0, alpha * x, x)
def softmax(x):
    """Numerically stable softmax over a 1-D input."""
    e_x = np.exp(x - np.max(x))
    return np.clip(e_x / np.sum(e_x), 0, 1)
def get_activation(name):
    """Return the activation function by *name*."""
    activation_functions = {'sigmoid': sigmoid, 'tanh': tanh, 'relu': relu, 'leaky_relu': lambda x: leaky_relu(x), 'softmax': softmax}
    if name not in activation_functions:
        raise ValueError('Invalid activation function.')
    return activation_functions[name]

