from typing import Any, Dict, Optional
class KernelBase:
    """Minimal base class for all kernels.  Do NOT modify this class."""

    def __init__(self, **kwargs: Any) -> None:
        self.params: Dict[str, Any] = kwargs

    def set_params(self, state: Dict[str, Any]):
        """(Re)sets parameters from a previously stored summary dict."""
        hp = state.get('hyperparameters', {})
        self.params.update({k: v for (k, v) in hp.items() if k != 'id'})
        return self

    def summary(self) -> Dict[str, Any]:
        """Dictionary description that can be fed back to *initialize_kernel*."""
        return {'hyperparameters': {'id': self.__class__.__name__, **self.params}}
class LinearKernel(KernelBase):

    def __init__(self):
        super().__init__()
class RBFKernel(KernelBase):

    def __init__(self, gamma: float=1.0):
        super().__init__(gamma=gamma)
def initialize_kernel(param: Optional[Any]=None) -> KernelBase:
    """Create and return a kernel object from various representations.

    Args:
        param: None (default LinearKernel), an existing KernelBase instance, a
            string description such as 'RBF(gamma=0.5)', or a dictionary with a
            'hyperparameters' entry as produced by KernelBase.summary().

    Returns:
        KernelBase: the newly created or forwarded kernel instance.
    """
    if param is None:
        return LinearKernel()
    if isinstance(param, KernelBase):
        return param
    match = re.match('(\\w+)\\((.*)\\)', str(param))
    if match:
        (kernel_id, kwargs_str) = match.groups()
        kwargs = {}
        for kwarg in kwargs_str.split(','):
            if kwarg:
                kwarg = kwarg.strip()
                if '=' in kwarg:
                    (key, value) = kwarg.split('=')
                    kwargs[key.strip()] = value.strip()
                else:
                    kwargs[kwarg] = True
        if kernel_id == 'RBF':
            kwargs['gamma'] = float(kwargs.get('gamma', 1.0))
        elif kernel_id == 'Polynomial':
            kwargs['degree'] = int(kwargs.get('degree', 3))
            kwargs['coef0'] = float(kwargs.get('coef0', 1.0))
        else:
            raise NotImplementedError(f'Unknown kernel: {kernel_id}')
        return RBFKernel(**kwargs)
    if isinstance(param, dict):
        if 'hyperparameters' not in param:
            raise ValueError("Dictionary must contain 'hyperparameters' key")
        if 'id' not in param['hyperparameters']:
            raise ValueError("Dictionary must contain 'id' key")
        kernel_id = param['hyperparameters']['id']
        if kernel_id not in ['LinearKernel', 'PolynomialKernel', 'RBFKernel']:
            raise NotImplementedError(f'Unknown kernel: {kernel_id}')
        kernel = globals()[kernel_id]()

import re
def parse_activation(act_str: str) -> dict:
    """
    Parse an activation-function description string.

    The string can be one of the following forms:
        • "ReLU"
        • "Sigmoid"
        • "Tanh"
        • "Affine(slope=<number>, intercept=<number>)"
        • "Leaky ReLU(alpha=<number>)"

    Args:
        act_str: Description of the activation function.

    Returns:
        A dictionary with key "type" and, when applicable, its parameters.
        If the input format is invalid, return -1.
    """
    activation_patterns = {'ReLU': 'ReLU', 'Sigmoid': 'Sigmoid', 'Tanh': 'Tanh', 'Affine': 'Affine\\((slope=[-]?\\d+(?:\\.\\d+)?, intercept=[-]?\\d+(?:\\.\\d+)?\\)', 'Leaky ReLU': 'Leaky ReLU\\((alpha=[-]?\\d+(?:\\.\\d+)?\\))'}
    for (act, pattern) in activation_patterns.items():
        if re.fullmatch(pattern, act_str):
            if act == 'Affine' or act == 'Leaky ReLU':
                match = re.search('(\\d+(?:\\.\\d+)?, (\\d+(?:\\.\\d+)?)', act_str)
                if match:
                    return {'type': act, 'slope': float(match.group(1)), 'intercept': float(match.group(2))}
                else:
                    return {'type': act, 'slope': None, 'intercept': None}
            else:
                return {'type': act}
            return {'type': act}
    return -1

import numpy as np
def env_stats(actions: list, observations: list) -> dict:
    """
    Compute statistics for collections of actions and observations.

    The function inspects *actions* and *observations* and returns a dictionary
    containing information about dimensionality, data type (discrete or
    continuous) and the unique values appearing in every dimension.

    Parameters
    ----------
    actions : list
        A list with the actions that were taken.  Each element is either a
        scalar or an iterable of scalars (for multi-dimensional spaces).
    observations : list
        A list with the corresponding observations.  Same structural
        requirements as *actions*.

    Returns
    -------
    dict
        A dictionary with the keys described in the task description.
    """
    stats = {'tuple_actions': False, 'tuple_observations': False, 'multidim_actions': False, 'multidim_observations': False, 'continuous_actions': False, 'continuous_observations': False, 'n_actions_per_dim': [], 'action_dim': 0, 'n_obs_per_dim': [], 'obs_dim': 0, 'action_ids': [], 'obs_ids': []}
    if len(actions) != len(observations):
        raise ValueError('Actions and observations must have the same length')
    if not actions or not observations:
        raise ValueError('Actions and observations must not be empty')
    if any((isinstance(action, (list, tuple, np.ndarray)) for action in actions)):
        stats['tuple_actions'] = True
    if any((isinstance(observation, (list, tuple, np.ndarray)) for observation in observations)):
        stats['tuple_observations'] = True
    for action in actions:
        if len(action) > 1:
            stats['multidim_actions'] = True
            stats['action_dim'] = len(action)
            stats['n_actions_per_dim'].append(len(set(action)))
    for observation in observations:
        if len(observation) > 1:
            stats['multidim_observations'] = True
            stats['obs_dim'] = len(observation)
            stats['n_obs_per_dim'].append(len(set(observation)))
    for action in actions:
        if any((not isinstance(x, int) and (not isinstance(x, float)) for x in action)):
            stats['continuous_actions'] = True
    for observation in observations:
        if any((not isinstance(x, int) and (not isinstance(x, float)) for x in observation)):
            stats['continuous_observations'] = True
    for action in actions:
        stats['action_ids'].append(sorted(set(action)))
    for observation in observations:
        stats['obs_ids'].append(sorted(set(observation)))
    return stats

