def sigmoid(x, order: int=0):
    """
    Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    if order not in [0, 1, 2]:
        return -1
    if isinstance(x, (int, float)):
        if order == 0:
            return round(1 / (1 + np.exp(-x)), 4)
        elif order == 1:
            return round(x * (1 - x), 4)
        elif order == 2:
            return round(x * (1 - x) * (1 - 2 * x), 4)
    elif order == 0:
        return np.vectorize(lambda x: 1 / (1 + np.exp(-x)))(x).tolist()
    elif order == 1:
        return np.vectorize(lambda x: x * (1 - x))(x).tolist()
    elif order == 2:
        return np.vectorize(lambda x: x * (1 - x) * (1 - 2 * x))(x).tolist()

import numpy as np
def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """
    Compute the Radial Basis Function (RBF) kernel matrix.

    The function should follow the specifications given in the task
    description. It must return -1 on invalid input, otherwise a nested list
    containing the kernel matrix rounded to four decimal places.
    """
    if not isinstance(X, list) or not all((isinstance(row, list) for row in X)):
        return -1
    if not all((len(row) == len(X[0]) for row in X)):
        return -1
    if Y is not None and (not isinstance(Y, list)) or not all((isinstance(row, list) for row in Y)):
        return -1
    if Y is not None and (not all((len(row) == len(X[0]) for row in Y))):
        return -1
    if sigma is None:
        sigma = np.sqrt(len(X[0]))
    elif not isinstance(sigma, (list, float)) or (isinstance(sigma, list) and len(sigma) != len(X[0])):
        return -1
    (N, M) = (len(X), len(Y) if Y is not None else len(X))
    K = np.zeros((N, M))
    for i in range(N):
        for j in range(M):
            if Y is None:
                K[i, j] = np.exp(-0.5 * np.sum((np.array(X[i]) - np.array(X[j])) ** 2) / sigma ** 2)
            else:
                K[i, j] = np.exp(-0.5 * np.sum((np.array(X[i]) - np.array(Y[j])) ** 2) / sigma[j] ** 2)
    K = [row for row in K.tolist()]
    for i in range(N):
        for j in range(M):
            K[i][j] = round(K[i][j], 4)
    return K

import math
def compute_ball_radius(centroid: list[float], X: list[list[float]]) -> float:
    """
    Compute the maximum Euclidean distance between *centroid* and the points in *X* (i.e., the Ball-tree node radius), rounded to 4 decimals.
    """
    if not X:
        return 0.0
    max_distance = 0.0
    for point in X:
        distance = math.sqrt(sum(((a - b) ** 2 for (a, b) in zip(centroid, point))))
        max_distance = max(max_distance, distance)
    return round(max_distance, 4)

from collections import Counter, defaultdict
import itertools
def fp_growth(transactions, min_sup):
    """
    Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : Iterable[Iterable[Hashable]]
        A collection of transactions.  Each transaction is an iterable containing hashable items.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list[tuple]
        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """
    item_freq = Counter((item for transaction in transactions for item in transaction))
    item_freq = {item: freq for (item, freq) in item_freq.items() if freq >= min_sup}
    item_freq = {item: freq for (item, freq) in item_freq.items() if freq > 0}
    item_freq = {item: freq for (item, freq) in item_freq.items()}
    fp_tree = defaultdict(list)
    for transaction in transactions:
        for item in transaction:
            fp_tree[item].append(transaction)
    header_table = defaultdict(list)
    for (item, transactions) in fp_tree.items():
        for transaction in transactions:
            header_table[item].append(transaction)
    result = set()
    for item in item_freq:
        result.add((item,))
    while True:
        cond_pattern_bases = []
        for (item, transactions) in fp_tree.items():
            if len(transactions) > 1:
                cond_pattern_bases.append((item,))
        cond_fp_tree = defaultdict(list)
        for cond_pattern_base in cond_pattern_bases:
            for transaction in fp_tree[cond_pattern_base[0]]:
                cond_fp_tree[cond_pattern_base[0]].append(transaction)
        for cond_pattern_base in cond_pattern_bases:
            for transaction in cond_fp_tree[cond_pattern_base[0]]:
                for item in transaction:
                    if cond_pattern_base + (item,) not in result:
                        result.add(cond_pattern_base + (item,))
        if not cond_pattern_bases:
            break
    result = sorted(result, key=lambda x: (len(x), x))
    return result

import numpy as np
def leaky_relu(x: np.ndarray | list | int | float, alpha: float=0.3, order: int=0) -> list:
    """
    Compute Leaky ReLU, its first derivative, or its second derivative.

    Args:
        x: Values to be transformed. Accepts NumPy array, list, or scalar.
        alpha: Slope used when an element is non-positive (default 0.3).
        order: 0 – return the function value; 1 – return the first derivative; 2 – return the second derivative.

    Returns:
        Python list with the same shape as *x* that contains the requested quantity.
    """
    if not isinstance(x, np.ndarray):
        x = np.array([x])
    if order not in [0, 1, 2]:
        return []
    if order == 0:
        return np.where(x > 0, x, alpha * x).tolist()
    elif order == 1:
        return np.where(x > 0, 1, alpha).tolist()
    elif order == 2:
        return [0] * len(x)

import numpy as np
def hard_sigmoid(z: np.ndarray, order: int=0) -> list[float]:
    """Compute Hard-Sigmoid activation or its derivatives.

    Parameters
    ----------
    z : np.ndarray
        Input array whose elements the activation (or its derivatives) will be
        applied to.
    order : int, optional (default = 0)
        • 0 – return Hard-Sigmoid values.
        • 1 – return first derivative.
        • 2 – return second derivative.

    Returns
    -------
    list[float]
        A list containing the computed values, rounded to 4 decimal places.
        If an unsupported *order* is provided the function should return an
        empty list.
    """

    def hard_sigmoid_func(z):
        return np.where(z < -2.5, 0, np.where(z <= 2.5, 0.2 * z + 0.5, 1))

    def hard_sigmoid_prime(z):
        return np.where(z <= 2.5, 0.2, 0)

    def hard_sigmoid_second(z):
        return np.zeros_like(z)
    if order == 0:
        return np.round(hard_sigmoid_func(z), 4).tolist()
    elif order == 1:
        return np.round(hard_sigmoid_prime(z), 4).tolist()
    elif order == 2:
        return np.round(hard_sigmoid_second(z), 4).tolist()
    else:
        return []

from typing import Any, Dict
def flatten_parameters(params: Dict[str, Any]) -> Dict[str, Any]:
    """Flatten a nested parameter dictionary.

    A dot (`.`) is used to join nested keys.  The input dictionary must not be
    mutated.

    Args:
        params: A potentially nested dictionary where some values can be other
                dictionaries.

    Returns:
        A new dictionary without nested structures.
    """
    flattened = {}
    for (key, value) in params.items():
        if isinstance(value, dict):
            sub_flattened = flatten_parameters(value)
            for (sub_key, sub_value) in sub_flattened.items():
                flattened[f'{key}.{sub_key}'] = sub_value
        else:
            flattened[key] = value
    return flattened

import numpy as np
def sigmoid_(Z):
    """
    Compute the logistic sigmoid activation element-wise.

    Args:
        Z: A scalar, 1-D list/array, or 2-D list/array of real numbers.

    Returns:
        Sigmoid-transformed value(s) rounded to 4 decimals.  Returns a float for
        scalar input; otherwise returns a Python list preserving the input
        shape.
    """
    if not isinstance(Z, (int, float)):
        Z = np.array(Z)
    sigmoid_Z = 1 / (1 + np.exp(-Z))
    sigmoid_Z = np.round(sigmoid_Z, 4)
    if len(Z.shape) == 0:
        return float(sigmoid_Z)
    else:
        return sigmoid_Z.tolist()

