def leaf_values(root: Optional[Node]) -> List[int]:
    """Collect all leaf values from left to right.

    Args:
        root: The root of the binary tree or ``None``.

    Returns:
        A list containing the values found in every leaf node, ordered from
        left-most leaf to right-most leaf.  If the tree is empty the list is
        empty as well.
    """

    def traverse(node):
        if node is None:
            return []
        if node.left is None and node.right is None:
            return [node.val]
        return traverse(node.left) + traverse(node.right)
    return traverse(root)

def predict_sample(root: 'Node', features: list | tuple, task_type: str) -> any:
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    if isinstance(root, dict):
        if 'y' in root:
            return root['y']
        label = root['label']
        child_index = features[label]
        return predict_sample(root['child'][child_index], features, task_type)
    elif isinstance(root, Node):
        if 'y' in root:
            return root.y
        label = root.label
        value = features[label]
        if task_type == 'regression':
            if value <= root.s:
                return predict_sample(root.child[0], features, task_type)
            else:
                return predict_sample(root.child[1], features, task_type)
        else:
            for child in root.child:
                if child.x == value:
                    return predict_sample(child, features, task_type)
            return predict_sample(root.child[1], features, task_type)

from collections import defaultdict
def naive_bayes_predict(train_data: list[tuple[str, list[str]]], keywords: set[str], test_samples: list[list[str]]) -> list[str]:
    """
    Build an unsmoothed Bernoulli Naive Bayes text classifier and predict labels.

    The classifier treats every word in *keywords* as a binary feature that is
    either present or absent in a document.  Words outside *keywords* are
    ignored.  Training follows the standard Bernoulli model *without* any form
    of smoothing, and *no* class prior is used when computing the likelihoods.

    Args:
        train_data: List of ``(label, tokens)`` pairs forming the training set.
        keywords:   Set of words that act as binary features.
        test_samples: List of token lists (one per unseen document).

    Returns:
        List of predicted labels for the documents in *test_samples* (same
        order as input).
    """
    keyword_counts = defaultdict(lambda : defaultdict(int))
    class_counts = defaultdict(int)
    for (label, tokens) in train_data:
        for token in tokens:
            if token in keywords:
                keyword_counts[label][token] += 1
        class_counts[label] += 1
    likelihoods = {}
    for (label, tokens) in train_data:
        likelihoods[label] = 1
        for token in tokens:
            if token in keywords:
                likelihoods[label] *= keyword_counts[label][token] / class_counts[label]
        for token in keywords:
            if token not in tokens:
                likelihoods[label] *= 1 - keyword_counts[label][token] / class_counts[label]
    predicted_labels = []
    for tokens in test_samples:
        max_likelihood = 0
        predicted_label = None
        for (label, likelihood) in likelihoods.items():
            likelihood *= 1
            for token in tokens:
                if token in keywords:
                    likelihood *= keyword_counts[label][token] / class_counts[label]
                else:
                    likelihood *= 1 - keyword_counts[label][token] / class_counts[label]
            if likelihood > max_likelihood:
                max_likelihood = likelihood
                predicted_label = label
        predicted_labels.append(predicted_label)
    return predicted_labels

