from typing import List, Tuple
def deduce_mines(constraints: List[List]) -> Tuple[List[str], List[str]]:
    """
    Simplify Minesweeper constraints.

    Args:
    constraints: A list of 2-element lists [variables, value] where variables is a list of identifiers and value is an int telling how many of those variables hide a mine.

    Returns:
    A tuple (safe, mines) of two alphabetically sorted lists. The first element contains all variables that are certainly safe, the second one all variables that are certainly mines. If nothing can be deduced an empty list must be returned instead.
    """
    constraint_dict = {}
    for (variables, value) in constraints:
        for variable in variables:
            if variable not in constraint_dict:
                constraint_dict[variable] = []
            constraint_dict[variable].append((value, variables))
    for (variables, value) in constraints:
        if value == 0:
            for variable in variables:
                constraint_dict[variable].append((0, variables))
        elif value == len(variables):
            for variable in variables:
                constraint_dict[variable].append((len(variables), variables))
    for (variable, values) in constraint_dict.items():
        values.sort()
        for (value, variables) in values:
            if value > 0:
                for (other_variable, other_values) in constraint_dict.items():
                    if variable != other_variable:
                        for (other_value, other_variables) in other_values:
                            if other_value > 0:
                                other_values.remove((other_value, other_variables))
                                other_values.append((other_value - 1, other_variables))
    for (variable, values) in constraint_dict.items():
        values.sort()
        for (value, variables) in values:
            for (other_variable, other_values) in constraint_dict.items():
                if variable != other_variable:
                    other_values.sort()
                    for (other_value, other_variables) in other_values:
                        if value > other_value and other_value == len(variables):
                            other_values.remove((other_value, other_variables))
                            other_values.append((value - other_value, variables))
    for (variable, values) in constraint_dict.items():
        values.sort()
        values = list(set(values))
    safe = []
    mines = []
    for (variable, values) in constraint_dict.items():
        for (value, variables) in values:
            if value == 0:
                safe.extend(variables)
            elif value == len(variables):
                mines.extend(variables)
    safe.sort()
    mines.sort()
    return (safe, mines)

import numpy as np
def convert_maze_to_graph(maze: np.ndarray) -> dict:
    """
    Convert a binary maze to an adjacency-list representation.

    Args:
        maze: A 2-D NumPy array of 0s and 1s.

    Returns:
        A dictionary representing the adjacency list of the maze-graph.
    """
    (rows, cols) = maze.shape
    graph = {}
    for row in range(rows):
        for col in range(cols):
            if maze[row, col] == 1:
                adjacent_cells = []
                for (dr, dc) in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
                    (nr, nc) = (row + dr, col + dc)
                    if 0 <= nr < rows and 0 <= nc < cols:
                        if maze[nr, nc] == 1:
                            adjacent_cells.append((nr, nc))
                adjacent_cells.sort()
                graph[row, col] = adjacent_cells
    return graph
