def adaboost_1d_predict(x_train: list[float], y_train: list[int], x_test: list[float], epsilon: float=0.0) -> list[int]:
    """Trains a 1-D AdaBoost ensemble and returns predictions.

    Parameters
    ----------
    x_train : list[float]
        Training sample values (one-dimensional).
    y_train : list[int]
        Labels corresponding to *x_train* (each value must be 1 or –1).
    x_test : list[float]
        Sample values to classify after training.
    epsilon : float, default 0.0
        Upper bound on the allowed training error.  Training stops once
        the ensemble’s training error ≤ *epsilon*.

    Returns
    -------
    list[int]
        Predicted labels (1 or –1) for every value in *x_test*.
    """
    train_data = list(zip(x_train, y_train))
    train_data.sort(key=lambda x: x[0])
    weights = [1.0 / len(train_data) for _ in range(len(train_data))]
    ensemble = []
    while True:
        min_error = float('inf')
        best_stump = None
        for i in range(len(train_data) - 1):
            theta = (train_data[i][0] + train_data[i + 1][0]) / 2
            error = 0.0
            for (j, (x, y)) in enumerate(train_data):
                if x <= theta:
                    prediction = 1
                else:
                    prediction = -1
                if prediction != y:
                    error += weights[j]
            if error < min_error:
                min_error = error
                best_stump = (0, theta)
            error = 0.0
            for (j, (x, y)) in enumerate(train_data):
                if x > theta:
                    prediction = 1
                else:
                    prediction = -1
                if prediction != y:
                    error += weights[j]
            if error < min_error:
                min_error = error
                best_stump = (1, theta)
        alpha = 0.5 * math.log((1 - min_error) / min_error)
        ensemble.append((alpha, best_stump))
        for (i, (x, y)) in enumerate(train_data):
            if best_stump[0] == 0:
                if x <= best_stump[1]:
                    prediction = 1
                else:
                    prediction = -1
            elif x > best_stump[1]:
                prediction = 1
            else:
                prediction = -1
            weights[i] *= math.exp(-alpha * y * prediction)
        weights = [weight / sum(weights) for weight in weights]
        train_error = 0.0
        for (x, y) in train_data:
            prediction = 0.0
            for (alpha, stump) in ensemble:
                if stump[0] == 0:
                    if x <= stump[1]:
                        prediction += alpha
                    else:
                        prediction -= alpha
                elif x > stump[1]:
                    prediction += alpha
                else:
                    prediction -= alpha
            if math.copysign(1, prediction) != y:
                train_error += 1
        if train_error / len(train_data) <= epsilon:
            break
    predictions = []
    for x in x_test:
        prediction = 0.0
        for (alpha, stump) in ensemble:
            if stump[0] == 0:
                if x <= stump[1]:
                    prediction += alpha
                else:
                    prediction -= alpha
            elif x > stump[1]:
                prediction += alpha
            else:
                prediction -= alpha
        predictions.append(int(math.copysign(1, prediction)))
    return predictions

import numpy as np
def softplus(x, deriv: bool=False):
    """
    SoftPlus activation function and its derivative.

    Parameters:
    x (scalar, list, or NumPy array): Input values.
    deriv (bool, optional): Whether to return the derivative. Defaults to False.

    Returns:
    list or float: SoftPlus(x) or its derivative, rounded to the nearest 4th decimal.
    """
    x = np.array(x)
    if deriv:
        result = 1 / (1 + np.exp(-x))
    else:
        result = np.where(x > 0, np.log(np.exp(x) + 1), np.log1p(np.exp(x)))
    result = np.round(result, 4)
    if result.size == 1:
        return result.item()
    else:
        return result.tolist()

