def knn_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, k: int, metric: str='euclidean') -> np.ndarray:
    """
    k-Nearest Neighbors Classifier.

    Parameters:
    X_train (np.ndarray): Labelled training set.
    y_train (np.ndarray): Labels for the training set.
    X_test (np.ndarray): Unlabeled test set.
    k (int): Number of nearest neighbors to consider.
    metric (str): Distance metric to use. Defaults to 'euclidean'.

    Returns:
    np.ndarray: 1-D array of predicted labels.
    """

    def euclidean_distance(x, y):
        return np.sqrt(np.sum((x - y) ** 2))

    def manhattan_distance(x, y):
        return np.sum(np.abs(x - y))

    def cosine_distance(x, y):
        return 1 - np.dot(x, y) / (np.linalg.norm(x) * np.linalg.norm(y))
    metric_functions = {'euclidean': euclidean_distance, 'manhattan': manhattan_distance, 'cosine': cosine_distance}
    if metric not in metric_functions:
        metric = 'euclidean'
    distance_function = metric_functions[metric]
    predicted_labels = np.zeros(X_test.shape[0], dtype=y_train.dtype)
    for (i, test_sample) in enumerate(X_test):
        distances = np.array([distance_function(test_sample, train_sample) for train_sample in X_train])
        k_nearest_indices = np.argsort(distances)[:k]
        k_nearest_labels = y_train[k_nearest_indices]
        predicted_label = np.bincount(k_nearest_labels).argmax()
        predicted_labels[i] = predicted_label
    return predicted_labels

import numpy as np
def metric(name: str):
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """

    def euclidean(x, y):
        return np.sqrt(np.sum((np.array(x) - np.array(y)) ** 2))

    def manhattan(x, y):
        return np.sum(np.abs(np.array(x) - np.array(y)))

    def chebyshev(x, y):
        return np.max(np.abs(np.array(x) - np.array(y)))

    def cosine(x, y):
        x_norm = np.linalg.norm(x)
        y_norm = np.linalg.norm(y)
        if x_norm == 0 or y_norm == 0:
            return -1
        return 1 - np.dot(x, y) / (x_norm * y_norm)
    metrics = {'euclidean': euclidean, 'manhattan': manhattan, 'chebyshev': chebyshev, 'cosine': cosine}

    def distance(x, y):
        try:
            x = np.array(x, dtype=float)
            y = np.array(y, dtype=float)
        except ValueError:
            return -1
        if len(x.shape) != 1 or len(y.shape) != 1 or x.size == 0 or (y.size == 0):
            return -1
        if x.size != y.size:
            return -1
        if name not in metrics:
            return -1
        result = metrics[name](x, y)
        return round(result, 4)
    return distance

def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack.

    The function **must** follow the rules presented in the task description.

    Args:
        player: List of integers (1–10) representing the player's final hand. 1 is Ace.
        dealer: List of integers (1–10) representing the dealer's final hand.

    Returns:
        The player's reward as a float. Possible values are -1, 0, 1 or 1.5.
    """

    def calculate_score(hand: list[int]) -> int:
        score = 0
        aces = 0
        for card in hand:
            if card == 1:
                aces += 1
                score += 11
            else:
                score += card
        while score > 21 and aces:
            score -= 10
            aces -= 1
        return score
    player_score = calculate_score(player)
    dealer_score = calculate_score(dealer)
    if player_score > 21:
        return -1.0
    elif dealer_score > 21:
        return 1.0
    if len(player) == 2 and (player[0] == 1 and player[1] == 10 or (player[0] == 10 and player[1] == 1)):
        if player_score > dealer_score:
            return 1.5
        elif player_score == dealer_score:
            return 0.0
        else:
            return -1.0
    if player_score > dealer_score:
        return 1.0
    elif player_score < dealer_score:
        return -1.0
    else:
        return 0.0

import numpy as np
def spectral_clustering(X: np.ndarray, k: int) -> list[int]:
    """Cluster data using Spectral Clustering.

    Args:
        X: 2-D NumPy array where each row is a data sample.
        k: Desired number of clusters.

    Returns:
        List of length n_samples containing an integer cluster label for each sample.
    """
    n_samples = X.shape[0]
    W = np.zeros((n_samples, n_samples))
    for i in range(n_samples):
        for j in range(n_samples):
            if i != j:
                W[i, j] = 1 / (1 + np.linalg.norm(X[i] - X[j]))
    D = np.diag(np.sum(W, axis=1))
    L = D - W
    (eigenvalues, eigenvectors) = np.linalg.eig(L)
    idx = eigenvalues.argsort()[:k]
    E = eigenvectors[:, idx]
    centroids = E[:k, :]
    labels = np.zeros(n_samples, dtype=int)
    for i in range(n_samples):
        labels[i] = np.argmin(np.linalg.norm(E[i, :] - centroids, axis=1))
    for _ in range(100):
        prev_labels = labels.copy()
        for i in range(k):
            points_in_cluster = E[labels == i, :]
            if points_in_cluster.size > 0:
                centroids[i] = np.mean(points_in_cluster, axis=0)
            else:
                centroids[i] = E[np.random.choice(n_samples), :]
        for i in range(n_samples):
            labels[i] = np.argmin(np.linalg.norm(E[i, :] - centroids, axis=1))
        if np.all(labels == prev_labels):
            break
    return labels.tolist()

from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool):
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def is_box(space: Space) -> bool:
        """Check if a space is a Box or a composite space with all Box sub-spaces."""
        if isinstance(space, Box):
            return True
        elif isinstance(space, (Tuple, Dict)):
            if isinstance(space, Tuple):
                return all((is_box(sub_space) for sub_space in space.spaces))
            else:
                return all((is_box(sub_space) for sub_space in space.spaces.values()))
        else:
            return False
    cont_action = is_box(env.action_space) if not tuple_action else is_box(env.action_space)
    cont_obs = is_box(env.observation_space) if not tuple_obs else is_box(env.observation_space)
    return (cont_action, cont_obs)

import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    if order not in [0, 1, 2]:
        return -1
    x = np.array(x)
    sigmoid_x = 1 / (1 + np.exp(-x))
    if order == 0:
        result = sigmoid_x
    elif order == 1:
        result = sigmoid_x * (1 - sigmoid_x)
    elif order == 2:
        result = sigmoid_x * (1 - sigmoid_x) * (1 - 2 * sigmoid_x)
    result = np.round(result, 4)
    if result.size == 1:
        return result.item()
    else:
        return result.tolist()

import numpy as np
def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """
    Compute the Radial Basis Function (RBF) kernel matrix.

    The function should follow the specifications given in the task description. 
    It must return -1 on invalid input, otherwise a nested list containing the 
    kernel matrix rounded to four decimal places.
    """
    X = np.array(X)
    if Y is None:
        Y = X
    else:
        Y = np.array(Y)
    C = X.shape[1]
    if Y.shape[1] != C:
        return -1
    if sigma is None:
        sigma = np.sqrt(C / 2)
    if isinstance(sigma, (int, float)):
        sigma = np.full(C, sigma)
    elif isinstance(sigma, (list, np.ndarray)):
        if len(sigma) != C:
            return -1
        sigma = np.array(sigma)
    else:
        return -1
    if np.any(sigma <= 0):
        return -1
    kernel_matrix = np.zeros((X.shape[0], Y.shape[0]))
    for i in range(X.shape[0]):
        for j in range(Y.shape[0]):
            distance = np.sum(((X[i] - Y[j]) / sigma) ** 2)
            kernel_matrix[i, j] = np.exp(-0.5 * distance)
    kernel_matrix = np.round(kernel_matrix, 4).tolist()
    return kernel_matrix
