import itertools
def fp_growth(transactions, min_sup):
    """Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : Iterable[Iterable[Hashable]]
        A collection of transactions.  Each transaction is an iterable containing hashable items.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list[tuple]
        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """
    item_counts = Counter((item for transaction in transactions for item in transaction))
    frequent_items = {item: count for (item, count) in item_counts.items() if count >= min_sup}
    sorted_items = sorted(frequent_items, key=frequent_items.get, reverse=True)
    fp_tree = {}
    for transaction in transactions:
        transaction = tuple(sorted([item for item in transaction if item in frequent_items], key=lambda item: sorted_items.index(item)))
        current_node = fp_tree
        for item in transaction:
            if item not in current_node:
                current_node[item] = {}
            current_node = current_node[item]
    header_table = {item: [] for item in frequent_items}

    def traverse(node, path):
        for (item, child_node) in node.items():
            header_table[item].append((path, child_node))
            traverse(child_node, path + (item,))
    traverse(fp_tree, ())

    def mine(node, prefix):
        frequent_item_sets = []
        for (item, node_list) in header_table.items():
            if any((node == child_node for (_, child_node) in node_list)):
                conditional_pattern_base = []
                for (path, child_node) in node_list:
                    if node == child_node:
                        conditional_pattern_base.append(path)
                conditional_fp_tree = {}
                for transaction in conditional_pattern_base:
                    current_node = conditional_fp_tree
                    for item in transaction:
                        if item not in current_node:
                            current_node[item] = {}
                        current_node = current_node[item]
                if conditional_fp_tree:
                    frequent_item_sets.extend(mine(conditional_fp_tree, prefix + (item,)))
                else:
                    combinations = []
                    for r in range(1, len(conditional_pattern_base[0]) + 1):
                        combinations.extend(itertools.combinations(conditional_pattern_base[0], r))
                    frequent_item_sets.extend([prefix + (item,) + combination for combination in combinations])
        return frequent_item_sets
    frequent_item_sets = mine(fp_tree, ())
    frequent_item_sets = sorted(set((tuple(sorted(item_set)) for item_set in frequent_item_sets)), key=lambda item_set: (len(item_set), item_set))
    return frequent_item_sets
