def softplus(x, deriv: bool=False):
    """
    SoftPlus activation function and its derivative.

    Parameters:
    x (scalar, list, or NumPy array): Input values.
    deriv (bool, optional): Whether to return the derivative. Defaults to False.

    Returns:
    list or float: SoftPlus(x) or its derivative, rounded to the nearest 4th decimal.
    """
    x = np.array(x)
    if deriv:
        result = 1 / (1 + np.exp(-x))
    else:
        result = np.where(x > 0, np.log(np.exp(x) + 1), np.log1p(np.exp(x)))
    result = np.round(result, 4)
    if result.size == 1:
        return result.item()
    else:
        return result.tolist()

import numpy as np
def compute_impurity(y, criterion):
    """Compute a node's impurity.

    Args:
        y (numpy.ndarray): 1-D array with the target values.
        criterion (str): One of {"entropy", "gini", "mse"}.

    Returns:
        float: Impurity value rounded to 4 decimal places.
    """
    if len(y) == 0:
        return 0.0
    if criterion not in ['entropy', 'gini', 'mse']:
        raise ValueError("Invalid criterion. Must be one of 'entropy', 'gini', or 'mse'.")
    if criterion == 'entropy':
        (unique, counts) = np.unique(y, return_counts=True)
        probabilities = counts / len(y)
        entropy = -np.sum(probabilities * np.log2(probabilities))
        return round(entropy, 4)
    elif criterion == 'gini':
        (unique, counts) = np.unique(y, return_counts=True)
        probabilities = counts / len(y)
        gini = 1 - np.sum(np.square(probabilities))
        return round(gini, 4)
    elif criterion == 'mse':
        mean = np.mean(y)
        mse = np.mean(np.square(y - mean))
        return round(mse, 4)
