from scipy.spatial import distance
def knn_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, k: int, metric: str='euclidean') -> np.ndarray:
    """
    k-Nearest Neighbors Classifier.

    Parameters:
    X_train (np.ndarray): Labelled training set.
    y_train (np.ndarray): Labels for the training set.
    X_test (np.ndarray): Unlabeled test set.
    k (int): Number of nearest neighbors to consider.
    metric (str): Distance metric to use. Defaults to 'euclidean'.

    Returns:
    np.ndarray: 1-D array of predicted labels.
    """

    def euclidean_distance(x, y):
        return np.sqrt(np.sum((x - y) ** 2))

    def manhattan_distance(x, y):
        return np.sum(np.abs(x - y))

    def cosine_distance(x, y):
        return 1 - np.dot(x, y) / (np.linalg.norm(x) * np.linalg.norm(y))
    metric_functions = {'euclidean': euclidean_distance, 'manhattan': manhattan_distance, 'cosine': cosine_distance}
    if metric not in metric_functions:
        metric = 'euclidean'
    distance_function = metric_functions[metric]
    predicted_labels = np.zeros(X_test.shape[0], dtype=y_train.dtype)
    for (i, test_sample) in enumerate(X_test):
        distances = np.array([distance_function(test_sample, train_sample) for train_sample in X_train])
        k_nearest_indices = np.argsort(distances)[:k]
        k_nearest_labels = y_train[k_nearest_indices]
        predicted_label = np.bincount(k_nearest_labels).argmax()
        predicted_labels[i] = predicted_label
    return predicted_labels

def accuracy_score(y_true, y_pred):
    """Return the classification accuracy between *y_true* and *y_pred*.

    The function must:
      • return ‑1 if the two containers have different lengths;
      • return 0 when both containers are empty;
      • otherwise return the proportion of matching elements rounded to
        4 decimal places.

    Args:
        y_true (list | tuple): Ground-truth class labels.
        y_pred (list | tuple): Predicted class labels.

    Returns:
        float | int: The accuracy or ‑1 according to the rules above.
    """
    if len(y_true) != len(y_pred):
        return -1
    if len(y_true) == 0:
        return 0.0
    matches = sum((1 for (true, pred) in zip(y_true, y_pred) if true == pred))
    accuracy = matches / len(y_true)
    return round(accuracy, 4)

import numpy as np
def metric(name: str):
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """

    def euclidean(x, y):
        return np.sqrt(np.sum((np.array(x) - np.array(y)) ** 2))

    def manhattan(x, y):
        return np.sum(np.abs(np.array(x) - np.array(y)))

    def chebyshev(x, y):
        return np.max(np.abs(np.array(x) - np.array(y)))

    def cosine(x, y):
        x_norm = np.linalg.norm(x)
        y_norm = np.linalg.norm(y)
        if x_norm == 0 or y_norm == 0:
            return -1
        return 1 - np.dot(x, y) / (x_norm * y_norm)
    metrics = {'euclidean': euclidean, 'manhattan': manhattan, 'chebyshev': chebyshev, 'cosine': cosine}

    def distance(x, y):
        try:
            x = np.array(x, dtype=float)
            y = np.array(y, dtype=float)
        except ValueError:
            return -1
        if len(x.shape) != 1 or len(y.shape) != 1 or x.size == 0 or (y.size == 0):
            return -1
        if x.size != y.size:
            return -1
        if name not in metrics:
            return -1
        result = metrics[name](x, y)
        return round(result, 4)
    return distance

