import numpy as np
def gaussian_bandit_oracle(payoff_dists: list[tuple[float, float]], payoff_probs: list[float]) -> tuple[float, int] | int:
    """
    Determine the arm with the highest expected reward in a Gaussian bandit.

    The function must compute the expected reward for each arm (p_k * μ_k) and
    return a tuple (best_ev, best_arm), where best_ev is rounded to 4 decimal
    places and best_arm is the index of the arm with that expected reward.  If
    the input is invalid (see task description) the function should return -1.
    """
    if len(payoff_dists) != len(payoff_probs) or len(payoff_dists) == 0:
        return -1
    for (i, (mu, sigma_squared)) in enumerate(payoff_dists):
        if sigma_squared <= 0:
            return -1
    for p in payoff_probs:
        if p < 0 or p > 1:
            return -1
    expected_rewards = np.array([p * mu for ((mu, _), p) in zip(payoff_dists, payoff_probs)])
    best_arm = np.argmax(expected_rewards)
    best_ev = round(expected_rewards[best_arm], 4)
    return (best_ev, best_arm)

import numpy as np
def sigmoid(x):
    """
    Element-wise logistic sigmoid.

    Args:
        x: Input value(s).

    Returns:
        The sigmoid of x, rounded to 4 decimal places.
    """
    if isinstance(x, (int, float)):
        return round(1 / (1 + np.exp(-x)), 4)
    elif isinstance(x, (list, tuple)):
        return np.array([round(1 / (1 + np.exp(-i)), 4) for i in x])
    elif isinstance(x, np.ndarray):
        return np.array([round(i, 4) for i in 1 / (1 + np.exp(-x))])
    else:
        raise TypeError('Unsupported input type.')
def tanh(x):
    """
    Element-wise hyperbolic tangent.

    Args:
        x: Input value(s).

    Returns:
        The hyperbolic tangent of x, rounded to 4 decimal places.
    """
    if isinstance(x, (int, float)):
        return round(np.tanh(x), 4)
    elif isinstance(x, (list, tuple)):
        return np.array([round(np.tanh(i), 4) for i in x])
    elif isinstance(x, np.ndarray):
        return np.array([round(i, 4) for i in np.tanh(x)])
