        raise TypeError('Unsupported input type.')
def relu(x):
    """
    Element-wise Rectified Linear Unit.

    Args:
        x: Input value(s).

    Returns:
        The ReLU of x, rounded to 4 decimal places.
    """
    if isinstance(x, (int, float)):
        return round(max(0, x), 4)
    elif isinstance(x, (list, tuple)):
        return np.array([round(max(0, i), 4) for i in x])
    elif isinstance(x, np.ndarray):
        return np.array([round(max(0, i), 4) for i in x])
    else:
        raise TypeError('Unsupported input type.')
def leaky_relu(x, alpha: float=0.01):
    """
    Element-wise Leaky-ReLU with slope *alpha* for x<0.

    Args:
        x: Input value(s).
        alpha: Slope for negative inputs (default: 0.01).

    Returns:
        The Leaky-ReLU of x, rounded to 4 decimal places.
    """
    if isinstance(x, (int, float)):
        return round(max(alpha * x, x), 4)
    elif isinstance(x, (list, tuple)):
        return np.array([round(max(alpha * i, i), 4) for i in x])
    elif isinstance(x, np.ndarray):
        return np.array([round(max(alpha * i, i), 4) for i in x])
    else:
        raise TypeError('Unsupported input type.')
def softmax(x):
    """
    Numerically stable softmax over a 1-D input.

    Args:
        x: Input value(s).

    Returns:
        The softmax of x, rounded to 4 decimal places.
    """
    if isinstance(x, (int, float)):
        raise ValueError('Softmax is not defined for scalar inputs.')
    elif isinstance(x, (list, tuple)):
        x = np.array(x)
    elif not isinstance(x, np.ndarray):
        raise TypeError('Unsupported input type.')
    e_x = np.exp(x - np.max(x))
    return np.array([round(i, 4) for i in e_x / e_x.sum()])
def get_activation(name):
    """
    Return the activation function by *name*.

    Args:
        name (str): One of {"sigmoid", "tanh", "relu", "leaky_relu", "softmax"}

    Returns:
        Callable: the requested activation function.

    Raises:
        ValueError: If *name* is not recognised.
