from collections import defaultdict
def huffman_encode(tokens: list[str]) -> dict[str, str]:
    """Generate a Huffman code for the supplied tokens.

    The function must return a dictionary mapping **every** unique token in
    *tokens* to its binary Huffman code.  All codes must be prefix-free and
    consist only of the characters ‘0’ and ‘1’.
    """
    if not tokens:
        return {}
    frequency = defaultdict(int)
    for token in tokens:
        frequency[token] += 1
    if len(frequency) == 1:
        return {tokens[0]: '0'}
    heap = [[weight, [token, '']] for (token, weight) in frequency.items()]
    heapq.heapify(heap)
    while len(heap) > 1:
        lo = heapq.heappop(heap)
        hi = heapq.heappop(heap)
        for pair in lo[1:]:
            pair[1] = '0' + pair[1]
        for pair in hi[1:]:
            pair[1] = '1' + pair[1]
        heapq.heappush(heap, [lo[0] + hi[0]] + lo[1:] + hi[1:])
    huffman_code = sorted(heap[0][1:], key=lambda p: (len(p[-1]), p))
    huffman_dict = {}
    for p in huffman_code:
        huffman_dict[p[0]] = p[1]
    return huffman_dict
