from typing import List
def ngram_log_prob(corpus: List[str], query: str, N: int) -> float:
    if N < 1:
        raise ValueError('N must be greater than or equal to 1')
    tokenized_corpus = []
    for sentence in corpus:
        tokens = sentence.split()
        if N > 1:
            padded_tokens = ['<bol>'] * (N - 1) + tokens + ['<eol>']
        else:
            padded_tokens = tokens
        tokenized_corpus.append(padded_tokens)
    ngram_counts = Counter()
    prefix_counts = Counter()
    for tokens in tokenized_corpus:
        for i in range(len(tokens) - N + 1):
            ngram = tuple(tokens[i:i + N])
            ngram_counts[ngram] += 1
            if N > 1:
                prefix = ngram[:-1]
                prefix_counts[prefix] += 1
            else:
                prefix_counts['total'] = prefix_counts.get('total', 0) + 1
    query_tokens = query.split()
    if N > 1:
        padded_query_tokens = ['<bol>'] * (N - 1) + query_tokens + ['<eol>']
    else:
        padded_query_tokens = query_tokens
    if len(padded_query_tokens) < N:
        return float('-inf')
    log_prob = 0.0
    for i in range(len(padded_query_tokens) - N + 1):
        ngram = tuple(padded_query_tokens[i:i + N])
        count_ngram = ngram_counts.get(ngram, 0)
        if count_ngram == 0:
            return float('-inf')
        if N > 1:
            prefix = ngram[:-1]
            count_prefix = prefix_counts.get(prefix, 0)
        else:
            count_prefix = prefix_counts.get('total', 0)
        if count_prefix == 0:
            return float('-inf')
        prob = count_ngram / count_prefix
        log_prob += math.log(prob)
    return round(log_prob, 4)

from collections import Counter
def build_fp_tree(transactions: list[list[str]], min_support: int) -> dict:
    item_support = Counter()
    for transaction in transactions:
        unique_items = set(transaction)
        for item in unique_items:
            item_support[item] += 1
    frequent_items = {item: support for (item, support) in item_support.items() if support >= min_support}
    ordered_items = sorted(frequent_items.items(), key=lambda x: (-x[1], x[0]))
    global_ordering = [item for (item, _) in ordered_items]
    root = {'support': 0, 'children': {}}
    for transaction in transactions:
        filtered_transaction = [item for item in set(transaction) if item in frequent_items]
        ordered_transaction = sorted(filtered_transaction, key=lambda x: global_ordering.index(x))
        current_node = root
        for item in ordered_transaction:
            if item not in current_node['children']:
                current_node['children'][item] = {'support': 0, 'children': {}}
            current_node = current_node['children'][item]
            current_node['support'] += 1
    return root

import numpy as np
def linear_kernel(X: list[list[int | float]] | 'np.ndarray', Y: list[list[int | float]] | 'np.ndarray' | None=None, c0: int | float=0) -> list[list[float]] | int:
    X = np.array(X, dtype=float)
    if Y is not None:
        Y = np.array(Y, dtype=float)
    else:
        Y = X
    if X.size == 0:
        return []
    if X.shape[1] != Y.shape[1]:
        return -1
    K = np.dot(X, Y.T) + c0
    K = np.round(K, 4)
    return K.tolist()

import numpy as np
def leaf_predict(leaf: 'Leaf', classifier: bool):
    """Return the prediction stored in a decision-tree leaf.

    Args:
        leaf: A `Leaf` object whose `value` attribute is either a sequence of
              class probabilities (classification) or a single number
              (regression).
        classifier: When *True* treat the leaf as belonging to a
                     classification tree; otherwise treat it as regression.

    Returns:
        int | float: Predicted class index for classification; otherwise the
                     raw scalar stored in the leaf.
    """
    if classifier:
        if isinstance(leaf.value, (list, tuple, np.ndarray)):
            return np.argmax(leaf.value)
        else:
            raise ValueError('For classification, leaf.value must be a sequence of probabilities.')
    elif isinstance(leaf.value, (int, float)):
        return leaf.value
    else:
        raise ValueError('For regression, leaf.value must be a scalar.')

from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Discrete(Space):

    def __init__(self, n: int):
        self.n = n
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool) -> PyTuple[bool, bool]:
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def _is_space_continuous(space: Space) -> bool:
        if isinstance(space, Box):
            return True
        elif isinstance(space, Discrete):
            return False
        elif isinstance(space, Tuple):
            return all((isinstance(s, Box) for s in space.spaces))
        elif isinstance(space, Dict):
            return all((isinstance(s, Box) for s in space.spaces.values()))
        else:
            raise ValueError('Unsupported space type')
    cont_action = _is_space_continuous(env.action_space) if not tuple_action else isinstance(env.action_space, (Tuple, Dict)) and (_is_space_continuous(env.action_space) if isinstance(env.action_space, (Tuple, Dict)) else False)
    cont_obs = _is_space_continuous(env.observation_space) if not tuple_obs else isinstance(env.observation_space, (Tuple, Dict)) and (_is_space_continuous(env.observation_space) if isinstance(env.observation_space, (Tuple, Dict)) else False)
    if tuple_action and isinstance(env.action_space, (Tuple, Dict)):
        cont_action = _is_space_continuous(env.action_space)
    else:
        cont_action = _is_space_continuous(env.action_space)
    if tuple_obs and isinstance(env.observation_space, (Tuple, Dict)):
        cont_obs = _is_space_continuous(env.observation_space)
    else:
        cont_obs = _is_space_continuous(env.observation_space)
    return (cont_action, cont_obs)

import numpy as np
def conv1D(X: np.ndarray, W: np.ndarray, stride: int, pad, dilation: int=0) -> list:
    (n_examples, signal_length, in_channels) = X.shape
    (kernel_width, _, out_channels) = W.shape
    effective_kernel_width = (kernel_width - 1) * (dilation + 1) + 1
    if isinstance(pad, str) and pad.lower() == 'same':
        out_len = int(np.ceil(signal_length / stride))
        total_pad = max(0, (out_len - 1) * stride + effective_kernel_width - signal_length)
        p_left = total_pad // 2
        p_right = total_pad - p_left
    elif isinstance(pad, int):
        p_left = pad
        p_right = pad
    elif isinstance(pad, tuple) and len(pad) == 2:
        (p_left, p_right) = pad
    else:
        raise ValueError('Invalid pad value')
    output_length = (signal_length + p_left + p_right - effective_kernel_width) // stride + 1
    X_padded = np.pad(X, ((0, 0), (p_left, p_right), (0, 0)), mode='constant')
    Y = np.zeros((n_examples, output_length, out_channels))
    for i in range(output_length):
        start_idx = i * stride
        end_idx = start_idx + effective_kernel_width
        step = dilation + 1
        X_slice = X_padded[:, start_idx:end_idx:step, :]
        for j in range(kernel_width):
            idx_in_X_slice = j * (dilation + 1)
            if idx_in_X_slice < X_slice.shape[1]:
                Y[:, i, :] += np.dot(X_slice[:, idx_in_X_slice, :], W[j, :, :])
    return Y.tolist()

import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    if order not in [0, 1, 2]:
        return -1
    original_is_scalar = isinstance(x, (int, float))
    x = np.array(x, dtype=float)
    sigmoid_x = 1 / (1 + np.exp(-x))
    if order == 0:
        result = sigmoid_x
    elif order == 1:
        result = sigmoid_x * (1 - sigmoid_x)
    elif order == 2:
        result = sigmoid_x * (1 - sigmoid_x) * (1 - 2 * sigmoid_x)
    result = np.round(result, 4)
    if original_is_scalar:
        return float(result.item())
    else:
        return result.tolist()

import numpy as np
def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    X = np.array(X)
    if Y is not None:
        Y = np.array(Y)
        if X.shape[1] != Y.shape[1]:
            return -1
    else:
        Y = X
    C = X.shape[1]
    if sigma is None:
        sigma = np.sqrt(C / 2)
    if isinstance(sigma, float) or isinstance(sigma, int):
        if sigma <= 0:
            return -1
        sigma_array = np.array([sigma] * C)
    elif isinstance(sigma, list):
        if len(sigma) != C:
            return -1
        sigma_array = np.array(sigma)
        if np.any(sigma_array <= 0):
            return -1
    else:
        return -1
    kernel_matrix = np.zeros((X.shape[0], Y.shape[0]))
    for i in range(X.shape[0]):
        for j in range(Y.shape[0]):
            diff = X[i] - Y[j]
            scaled_diff = diff / sigma_array
            squared_distance = np.sum(scaled_diff ** 2)
            kernel_matrix[i, j] = np.exp(-0.5 * squared_distance)
    kernel_matrix = np.round(kernel_matrix, 4).tolist()
    return kernel_matrix

