    subset1 = subset1
    subset2 = subset2
    return [subset1, subset2]

import numpy as np
def update_q_values(Q: 'np.ndarray', Q_next: 'np.ndarray', actions: list[int], rewards: list[float], dones: list[bool], gamma: float) -> list[list[float]]:
    if gamma < 0 or gamma > 1:
        return -1
    batch_size = Q.shape[0]
    if len(actions) != batch_size or len(rewards) != batch_size or len(dones) != batch_size or (Q_next.shape[0] != batch_size):
        return -1
    y = Q.copy()
    for i in range(batch_size):
        if dones[i]:
            target = rewards[i]
        else:
            target = rewards[i] + gamma * np.max(Q_next[i])
        y[i, actions[i]] = target
    y = np.round(y, 4)
    y_list = y.tolist()
    return y_list

import numpy as np
def regression_tree_predict(X_train: list[float | int], y_train: list[float | int], X_test: list[float | int], max_depth: int=3) -> list[float]:
    """Fit a univariate regression tree of depth *max_depth* and predict.

    Parameters
    ----------
    X_train : list[float | int]
        1-D feature values for the training set.
    y_train : list[float | int]
        Continuous target values for the training set.
    X_test : list[float | int]
        1-D feature values for which predictions are required.
    max_depth : int, default 3
        Maximum depth of the binary tree (root has depth 0).

    Returns
    -------
    list[float]
        Predictions for every element in *X_test*.
    """
    if not X_train:
        return []
    data = np.array(list(zip(X_train, y_train)))

    def build_tree(data, depth):
        if depth == max_depth or len(data) < 2:
            return np.mean(data[:, 1])
        data = data[data[:, 0].argsort()]
        best_split = None
        best_sse = float('inf')
        for i in range(1, len(data)):
            left_sse = np.sum((data[:i, 1] - np.mean(data[:i, 1])) ** 2)
            right_sse = np.sum((data[i:, 1] - np.mean(data[i:, 1])) ** 2)
            sse = left_sse + right_sse
            if sse < best_sse:
                best_sse = sse
                best_split = i
        if best_split is not None:
            left_child = build_tree(data[:best_split], depth + 1)
            right_child = build_tree(data[best_split:], depth + 1)
            return {'feature': data[best_split, 0], 'left': left_child, 'right': right_child}
        else:
            return np.mean(data[:, 1])
    tree = build_tree(data, 0)

    def predict(tree, X_test):
        predictions = []
        for x in X_test:
            node = tree
            while isinstance(node, dict):
                if x <= node['feature']:
                    node = node['left']
                else:
                    node = node['right']
            predictions.append(node)
        return predictions
    return predict(tree, X_test)

import numpy as np
from itertools import combinations_with_replacement
def polynomial_features(X: list[list[int | float]], degree: int) -> list[list[float]]:
    if degree < 0:
        return -1
    X = np.array(X)
    (n_samples, n_features) = X.shape
    poly_features = []
    for sample in X:
        sample_features = []
        for d in range(degree + 1):
            for indices in combinations_with_replacement(range(n_features), d):
                monomial = 1
                index_counts = {}
                for index in indices:
                    if index not in index_counts:
                        index_counts[index] = 0
                    index_counts[index] += 1
                for (index, count) in index_counts.items():
                    monomial *= sample[index] ** count
                sample_features.append(monomial)
        poly_features.append(sample_features)
    return poly_features

import numpy as np
def sigmoid_grad(z):
    if isinstance(z, np.ndarray):
        result = z * (1 - z)
        return np.round(result, 4)
    elif isinstance(z, list):
        if len(z) == 0:
            return []
        else:
            return [sigmoid_grad(i) for i in z]
    elif isinstance(z, (int, float)):
        result = z * (1 - z)
        return float(np.round(result, 4))
    else:
        raise TypeError('Input must be a float, list or NumPy ndarray')

import numpy as np
import math
from collections import defaultdict
def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    """
    Maximum Entropy Classifier with Generalised Iterative Scaling.

    Parameters:
    - X_train (np.ndarray): Training samples.
    - y_train (np.ndarray): Training labels.
    - X_test (np.ndarray): Samples to classify.
    - epsilon (float, optional): Convergence threshold. Defaults to 1e-3.
    - n_iter (int, optional): Maximum number of iterations. Defaults to 100.

    Returns:
    - list[int]: Predicted labels for every row in X_test.
    """
    (n_samples, n_features) = X_train.shape
    labels = np.unique(y_train)
    feature_value_label_pairs = defaultdict(int)
    empirical_dist = defaultdict(float)
    for i in range(n_samples):
        for j in range(n_features):
            feature_value = (j, X_train[i, j])
            for label in labels:
                feature_value_label = (feature_value, label)
                feature_value_label_pairs[feature_value_label] = len(feature_value_label_pairs)
                if label == y_train[i]:
                    empirical_dist[feature_value_label] += 1.0
    empirical_dist = {k: v / n_samples for (k, v) in empirical_dist.items()}
    weights = np.zeros(len(feature_value_label_pairs))
    max_f_x_y = 0
    for i in range(n_samples):
        f_x_y = sum((1 for j in range(n_features) if (j, X_train[i, j]) in [(fv[0], fv[1]) for (fv, _) in feature_value_label_pairs.keys()]))
        max_f_x_y = max(max_f_x_y, f_x_y)
    for _ in range(n_iter):
        model_dist = defaultdict(float)
        for i in range(n_samples):
            sum_exp = 0
            exp_values = {}
            for label in labels:
                exp_value = math.exp(sum((weights[feature_value_label_pairs[feature_value, label]] for feature_value in [(j, X_train[i, j]) for j in range(n_features)] if (feature_value, label) in feature_value_label_pairs)))
                exp_values[label] = exp_value
                sum_exp += exp_value
            for label in labels:
                for j in range(n_features):
                    feature_value = (j, X_train[i, j])
                    if (feature_value, label) in feature_value_label_pairs:
                        model_dist[feature_value, label] += exp_values[label] / sum_exp
        model_dist = {k: v / n_samples for (k, v) in model_dist.items()}
        weight_updates = np.zeros_like(weights)
        for (feature_value_label, idx) in feature_value_label_pairs.items():
            weight_updates[idx] = 1.0 / max_f_x_y * math.log(empirical_dist.get(feature_value_label, 0) / model_dist.get(feature_value_label, 1e-09))
        max_update = np.max(np.abs(weight_updates))
        weights += weight_updates
        if max_update < epsilon:
            break
    predictions = []
    for i in range(X_test.shape[0]):
        label_probs = {}
        for label in labels:
            sum_exp = sum((weights[feature_value_label_pairs[feature_value, label]] for feature_value in [(j, X_test[i, j]) for j in range(n_features)] if (feature_value, label) in feature_value_label_pairs))
            label_probs[label] = math.exp(sum_exp)
        sum_prob = sum(label_probs.values())
        label_probs = {label: prob / sum_prob for (label, prob) in label_probs.items()}
        predicted_label = max(label_probs, key=label_probs.get)
        predictions.append(int(predicted_label))
    return predictions

import numpy as np
def elastic_net_regression(X: np.ndarray, y: np.ndarray, learning_rate: float, iterations: int, l1_penalty: float, l2_penalty: float) -> tuple[list[float], float]:
    """Fits a linear model with Elastic-Net regularisation."""
    (m, n) = X.shape
    weights = np.zeros(n)
    bias = 0.0
    if learning_rate == 0 or iterations == 0:
        return (list(np.round(weights, 4)), round(bias, 4))
    for _ in range(iterations):
        y_hat = np.dot(X, weights) + bias
        r = y - y_hat
        weights_gradients = (-2 * np.dot(X.T, r) + l1_penalty * np.sign(weights) + 2 * l2_penalty * weights) / m
        bias_gradient = -2 * np.sum(r) / m
        weights -= learning_rate * weights_gradients
        bias -= learning_rate * bias_gradient
    return (list(np.round(weights, 4)), round(bias, 4))
