def build_tree(items: list[str], counts: list[int], parents: list[int]) -> dict:
    if not items:
        return {}
    nodes = []
    for (item, count) in zip(items, counts):
        node = {'item': item, 'count': count, 'children': []}
        nodes.append(node)
    root_index = None
    for (i, parent_index) in enumerate(parents):
        if parent_index == -1:
            root_index = i
        else:
            parent_node = nodes[parent_index]
            parent_node['children'].append(nodes[i])
    return nodes[root_index]

def check_data(a, b):
    """Validate and convert the two inputs so that both are numeric.

    The function must accept integers, floats, or numeric strings.  Any string
    should be converted with `float()`; existing numeric values stay as they
    are.  The pair `(a, b)` must then be returned.
    """
    if isinstance(a, str):
        a = float(a)
    if isinstance(b, str):
        b = float(b)
    return (a, b)
def validate_input(function):

    def wrapper(a, b):
        (a, b) = check_data(a, b)
        return function(a, b)
    return wrapper
@validate_input
def sum_numbers(a, b):
    """Return the arithmetic sum of *a* and *b* after validation."""
    sum_value = a + b
    if sum_value.is_integer():
        return int(sum_value)
    else:
        return sum_value

from typing import List, Optional
class Node:
    """Binary decision-tree node (provided).

    Non-leaf node:   rule = (feature_index, threshold)
    Leaf node:       rule = (None, prediction_value)
    """

    def __init__(self, left: Optional['Node'], right: Optional['Node'], rule: tuple):
        self.left = left
        self.right = right
        self.feature = rule[0]
        self.threshold = rule[1]
def predict_tree(root: Optional[Node], X: List[List[float]]) -> List[float]:
    """Predict the target values for a list of samples using a binary decision tree.

    Args:
    root: The root node of the binary decision tree.
    X: A list of samples, where each sample is a list of float feature values.

    Returns:
    A list of predicted target values in the same order as the input samples.
    """
    if root is None:
        return []

    def traverse_tree(node: Node, sample: List[float]) -> float:
        if node.left is None and node.right is None:
            return node.threshold
        if sample[node.feature] <= node.threshold:
            return traverse_tree(node.left, sample)
        else:
            return traverse_tree(node.right, sample)
    predictions = [traverse_tree(root, sample) for sample in X]
    return predictions

