def tokenize_whitespace(line: str, lowercase: bool=True, filter_stopwords: bool=True, filter_punctuation: bool=True, **kwargs) -> list[str]:
    """
    Tokenize *line* by whitespace with optional lower-casing, punctuation stripping and
    stop-word removal.

    Args:
        line: Input text string.
        lowercase: If True, convert text to lower-case before tokenisation.
        filter_stopwords: If True, remove common English stop-words.
        filter_punctuation: If True, strip punctuation from each token.
        **kwargs: Additional keyword arguments for future compatibility.

    Returns:
        List of processed tokens in their original order.
    """
    stop_words = {'i', 'me', 'my', 'myself', 'we', 'our', 'ours', 'ourselves', 'you', "you're", "you've", "you'll", "you'd", 'your', 'yours', 'yourself', 'yourselves', 'he', 'him', 'his', 'himself', 'she', "she's", 'her', 'hers', 'herself', 'it', "it's", 'its', 'itself', 'they', 'them', 'their', 'theirs', 'themselves', 'what', 'which', 'who', 'whom', 'this', 'that', "that'll", 'these', 'those', 'am', 'is', 'are', 'was', 'were', 'be', 'been', 'being', 'have', 'has', 'had', 'having', 'do', 'does', 'did', 'doing', 'a', 'an', 'the', 'and', 'but', 'if', 'or', 'because', 'as', 'until', 'while', 'of', 'at', 'by', 'for', 'with', 'about', 'against', 'between', 'into', 'through', 'during', 'before', 'after', 'above', 'below', 'to', 'from', 'up', 'down', 'in', 'out', 'on', 'off', 'over', 'under', 'again', 'further', 'then', 'once', 'here', 'there', 'when', 'where', 'why', 'how', 'all', 'any', 'both', 'each', 'few', 'more', 'most', 'other', 'some', 'such', 'no', 'nor', 'not', 'only', 'own', 'same', 'so', 'than', 'too', 'very', 's', 't', 'can', 'will', 'just', 'don', "don't", 'should', "should've", 'now', 'd', 'll', 'm', 'o', 're', 've', 'y', 'ain', 'aren', "aren't", 'couldn', "couldn't", 'didn', "didn't", 'doesn', "doesn't", 'hadn', "hadn't", 'hasn', "hasn't", 'haven', "haven't", 'isn', "isn't", 'ma', 'mightn', "mightn't", 'mustn', "mustn't", 'needn', "needn't", 'shan', "shan't", 'shouldn', "shouldn't", 'wasn', "wasn't", 'weren', "weren't", 'won', "won't", 'wouldn', "wouldn't"}
    if lowercase:
        line = line.lower()
    tokens = line.split()
    processed_tokens = []
    for token in tokens:
        if filter_punctuation:
            original_token = token
            token = token.strip(string.punctuation)
            if token:
                if filter_stopwords:
                    if token not in stop_words:
                        processed_tokens.append(token)
                else:
                    processed_tokens.append(token)
        elif filter_stopwords:
            if token not in stop_words:
                processed_tokens.append(token)
        else:
            processed_tokens.append(token)
    return processed_tokens

import numpy as np
def softplus(x, deriv: bool=False):
    """Compute SoftPlus activation function and its derivative."""
    x = np.asarray(x)
    if deriv:
        result = 1 / (1 + np.exp(-x))
    else:
        result = np.where(x > 0, x + np.log1p(np.exp(-x)), np.log1p(np.exp(x)))
    result = np.round(result, 4)
    if result.size == 1:
        return result.item()
    else:
        return result.tolist()

import numpy as np
from collections import Counter
def unsmoothed_ngram_log_prob(corpus: list[str], sequence: list[str], N: int) -> float:
    """Compute the unsmoothed MLE N-gram log-probability of *sequence*."""
    ngram_counts = Counter()
    prefix_counts = Counter()
    if N > 1:
        for i in range(len(corpus) - N + 1):
            ngram = tuple(corpus[i:i + N])
            ngram_counts[ngram] += 1
            prefix = ngram[:-1]
            prefix_counts[prefix] += 1
    else:
        ngram_counts = Counter(corpus)
        total_words = len(corpus)
    if N > 1:
        total_log_prob = 0.0
        for i in range(len(sequence) - N + 1):
            ngram = tuple(sequence[i:i + N])
            prefix = ngram[:-1]
            count_ngram = ngram_counts.get(ngram, 0)
            count_prefix = prefix_counts.get(prefix, 0)
            if count_ngram == 0 or count_prefix == 0:
                return float('-inf')
            prob = count_ngram / count_prefix
            total_log_prob += np.log(prob)
        return round(total_log_prob, 4)
    else:
        total_log_prob = 0.0
        for word in sequence:
            count_word = ngram_counts.get(word, 0)
            if count_word == 0:
                return float('-inf')
            prob = count_word / total_words
            total_log_prob += np.log(prob)
        return round(total_log_prob, 4)

import numpy as np
def compute_impurity(y, criterion):
    """Compute a node's impurity.

    Args:
        y (numpy.ndarray): 1-D array with the target values.
        criterion (str): One of {"entropy", "gini", "mse"}.

    Returns:
        float: Impurity value rounded to 4 decimal places.
    """
    if len(y) == 0:
        return 0.0
    if criterion not in ['entropy', 'gini', 'mse']:
        raise ValueError("Invalid criterion. Must be one of {'entropy', 'gini', 'mse'}")
    if criterion == 'entropy':
        (unique_classes, counts) = np.unique(y, return_counts=True)
        probabilities = counts / len(y)
        entropy = -np.sum(probabilities * np.log2(probabilities))
        return round(entropy, 4)
    elif criterion == 'gini':
        (unique_classes, counts) = np.unique(y, return_counts=True)
        probabilities = counts / len(y)
        gini = 1 - np.sum(probabilities ** 2)
        return round(gini, 4)
    elif criterion == 'mse':
        mean_y = np.mean(y)
        mse = np.mean((y - mean_y) ** 2)
        return round(mse, 4)

import numpy as np
def drelu(Z):
    if isinstance(Z, (int, float)):
        return 1.0 if Z > 0 else 0.0
    elif isinstance(Z, list):
        return [drelu(z) for z in Z]
    elif isinstance(Z, np.ndarray):
        result = np.where(Z > 0, 1.0, 0.0)
        return result.tolist()
    else:
        raise TypeError('Input must be a scalar, list, or numpy ndarray')

import numpy as np
def relu_(Z):
    """Apply the element-wise Rectified Linear Unit (ReLU) activation.

    The function keeps the container type of the input:
    scalar → scalar, list → list, NumPy array → NumPy array.

    Args:
        Z (int | float | list | numpy.ndarray): Input data – scalar, 1-D or 2-D.

    Returns:
        Same type as *Z* with all negative values clipped to 0.
    """
    if isinstance(Z, np.ndarray):
        return np.maximum(Z, 0)
    elif isinstance(Z, list):
        return np.maximum(np.array(Z), 0).tolist()
    else:
        return max(Z, 0)

import re
from collections import Counter
class Token:

    def __init__(self, word):
        self.count = 0
        self.word = word

    def __repr__(self):
        return "Token(word='{}', count={})".format(self.word, self.count)
def tokenize_and_count(text: str) -> list[Token]:
    words = re.findall('[a-zA-Z]+', text)
    lowercase_words = [word.lower() for word in words]
    word_counts = Counter(lowercase_words)
    tokens = [Token(word) for (word, count) in word_counts.items()]
    for token in tokens:
        token.count = word_counts[token.word]
    tokens.sort(key=lambda token: (-token.count, token.word))
    return tokens

import numpy as np
def polynomial_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, d: int=3, gamma: float | None=None, c0: float=1) -> list[list[float]]:
    X_np = np.array(X)
    (N, C) = X_np.shape
    if Y is None:
        Y_np = X_np
    else:
        Y_np = np.array(Y)
    if gamma is None:
        gamma = 1 / C
    K = np.dot(X_np, Y_np.T)
    K = (gamma * K + c0) ** d
    K = np.round(K, 4)
    return K.tolist()

import numpy as np
def best_arm(payoff_probs: list[float]) -> tuple[float, int]:
    """Find the arm with the highest expected reward in a Bernoulli bandit.

    Parameters
    ----------
    payoff_probs : list[float]
        A list where each element is the success probability of the
        corresponding arm. Probabilities must lie in the interval [0, 1].

    Returns
    -------
    tuple
        (max_expected_reward, best_arm_index).  If the input list is empty or
        contains an invalid probability (<0 or >1), the function must return
        (-1.0, -1).
    """
    if not payoff_probs:
        return (-1.0, -1)
    for prob in payoff_probs:
        if prob < 0 or prob > 1:
            return (-1.0, -1)
    max_prob = payoff_probs[0]
    best_arm_index = 0
    for i in range(1, len(payoff_probs)):
        if payoff_probs[i] > max_prob:
            max_prob = payoff_probs[i]
            best_arm_index = i
    return (float(max_prob), best_arm_index)

from collections import deque
def escape_fire_maze(grid: list[str]) -> int:
    n = len(grid)
    directions = [(0, 1), (0, -1), (1, 0), (-1, 0)]
    fire_time = [[float('inf')] * n for _ in range(n)]
    fire_queue = deque()
    for i in range(n):
        for j in range(n):
            if grid[i][j] == 'F':
                fire_queue.append((i, j))
                fire_time[i][j] = 0
    while fire_queue:
        (x, y) = fire_queue.popleft()
        for (dx, dy) in directions:
            (nx, ny) = (x + dx, y + dy)
            if 0 <= nx < n and 0 <= ny < n and (grid[nx][ny] != '#') and (fire_time[nx][ny] > fire_time[x][y] + 1):
                fire_time[nx][ny] = fire_time[x][y] + 1
                fire_queue.append((nx, ny))
    if grid[0][0] == 'F' or grid[n - 1][n - 1] == 'F':
        return -1
    visited = [[False] * n for _ in range(n)]
    agent_queue = deque([(0, 0, 0)])
    visited[0][0] = True
    while agent_queue:
        (x, y, time) = agent_queue.popleft()
        if x == n - 1 and y == n - 1:
            return time
        for (dx, dy) in directions:
            (nx, ny) = (x + dx, y + dy)
            if 0 <= nx < n and 0 <= ny < n and (grid[nx][ny] != '#') and (not visited[nx][ny]) and (time + 1 < fire_time[nx][ny]):
                agent_queue.append((nx, ny, time + 1))
                visited[nx][ny] = True
    return -1

import numpy as np
def sigmoid(X: np.ndarray) -> np.ndarray:
    """Compute the sigmoid activation."""
    return np.round(1 / (1 + np.exp(-X)), 4)
def sigmoid_grad(A: np.ndarray) -> np.ndarray:
    """Compute the gradient of the sigmoid activation given its output."""
    return np.round(A * (1 - A), 4)
def tanh(X: np.ndarray) -> np.ndarray:
    """Compute the tanh activation."""
    return np.round(np.tanh(X), 4)
def tanh_grad(A: np.ndarray) -> np.ndarray:
    """Compute the gradient of the tanh activation given its output."""
    return np.round(1 - A ** 2, 4)
def relu(X: np.ndarray) -> np.ndarray:
    """Compute the ReLU activation."""
    return np.round(np.maximum(X, 0), 4)
def relu_grad(A: np.ndarray) -> np.ndarray:
    """Compute the gradient of the ReLU activation given its output."""
    return np.round((A > 0).astype(int), 4)
def softmax(X: np.ndarray) -> np.ndarray:
    """Compute the softmax activation along the last axis."""
    exp_X = np.exp(X - np.max(X, axis=-1, keepdims=True))
    return np.round(exp_X / np.sum(exp_X, axis=-1, keepdims=True), 4)
def softmax_grad(A: np.ndarray) -> np.ndarray:
    """Compute the gradient of the softmax activation given its output."""
    return np.ones_like(A)
def activation_forward_backward(X: np.ndarray, activation: str, upstream_grad: np.ndarray) -> tuple[np.ndarray, np.ndarray]:
    """Apply an activation function and compute its local gradient.

    Parameters
    ----------
    X : np.ndarray
        Raw pre-activation values.
    activation : str
        Name of the activation function. Supported values are
        "sigmoid", "tanh", "relu", and "softmax".
    upstream_grad : np.ndarray
        Gradient that arrives from the layer above (must have the same shape as
        `X`).

    Returns
    -------
    tuple[np.ndarray, np.ndarray]
        A two-element tuple `(A, grad)` where:
          • `A`    – the activation applied to `X`.
          • `grad` – the element-wise product of `upstream_grad` and the local
                      derivative of the activation (rounded to 4 decimals).
    """
    activation_functions = {'sigmoid': (sigmoid, sigmoid_grad), 'tanh': (tanh, tanh_grad), 'relu': (relu, relu_grad), 'softmax': (softmax, softmax_grad)}
    if activation not in activation_functions:
        raise ValueError(f'Unsupported activation: {activation}')
    (forward_func, grad_func) = activation_functions[activation]
    A = forward_func(X)
    local_grad = grad_func(A)
    grad = np.round(upstream_grad * local_grad, 4)
    return (A, grad)

def accuracy_score(y_true, y_pred):
    """Return the classification accuracy between *y_true* and *y_pred*.

    The function must:
      • return ‑1 if the two containers have different lengths;
      • return 0 when both containers are empty;
      • otherwise return the proportion of matching elements rounded to
        4 decimal places.

    Args:
        y_true (list | tuple): Ground-truth class labels.
        y_pred (list | tuple): Predicted class labels.

    Returns:
        float | int: The accuracy or ‑1 according to the rules above.
    """
    if len(y_true) != len(y_pred):
        return -1
    if len(y_true) == 0:
        return 0
    matches = 0
    for (true, pred) in zip(y_true, y_pred):
        if true == pred:
            matches += 1
    accuracy = matches / len(y_true)
    return round(accuracy, 4)

import numpy as np
def ridge_regression(X: list[list[int | float]], y: list[int | float], alpha: float=1.0, fit_intercept: bool=True) -> list[float]:
    X = np.array(X)
    y = np.array(y)
    if fit_intercept:
        X = np.hstack((np.ones((X.shape[0], 1)), X))
    XTX = np.dot(X.T, X)
    XTy = np.dot(X.T, y)
    if fit_intercept:
        A = np.eye(X.shape[1]) * alpha
        A[0, 0] = 0
    else:
        A = np.eye(X.shape[1]) * alpha
    beta = np.dot(np.linalg.inv(XTX + A), XTy)
    beta = np.round(beta, 4)
    return beta.tolist()

import numpy as np
class Node:
    """Represents a node in the decision tree."""

    def __init__(self, feature=None, threshold=None, label=None, left=None, right=None):
        self.feature = feature
        self.threshold = threshold
        self.label = label
        self.left = left
        self.right = right
def gini_impurity(y):
    """Calculates the Gini impurity of a node."""
    (_, counts) = np.unique(y, return_counts=True)
    gini = 1 - np.sum((counts / len(y)) ** 2)
    return gini
def gini_gain(X, y, feature, threshold):
    """Calculates the Gini gain for a given split."""
    left_idx = X[:, feature] <= threshold
    right_idx = ~left_idx
    n_left = np.sum(left_idx)
    n_right = len(y) - n_left
    if n_left == 0 or n_right == 0:
        return 0
    gini_left = gini_impurity(y[left_idx])
    gini_right = gini_impurity(y[right_idx])
    gini_children = n_left / len(y) * gini_left + n_right / len(y) * gini_right
    gini_parent = gini_impurity(y)
    gain = gini_parent - gini_children
    return gain
def best_split(X, y):
    """Finds the best split for a given node."""
    best_gain = 0
    best_feature = None
    best_threshold = None
    for feature in range(X.shape[1]):
        unique_values = np.unique(X[:, feature])
        if len(unique_values) > 1:
            thresholds = (unique_values[:-1] + unique_values[1:]) / 2
            for threshold in thresholds:
                gain = gini_gain(X, y, feature, threshold)
                if gain > best_gain:
                    best_gain = gain
                    best_feature = feature
                    best_threshold = threshold
    return (best_feature, best_threshold, best_gain)
def majority_class(y):
    """Returns the majority class label."""
    return np.argmax(np.bincount(y))
def build_tree(X, y, depth=0, max_depth=None):
    """Recursively builds the decision tree."""
    if len(np.unique(y)) == 1:
        return Node(label=y[0])
    if max_depth is not None and depth >= max_depth:
        return Node(label=majority_class(y))
    (feature, threshold, gain) = best_split(X, y)
    if gain == 0:
        return Node(label=majority_class(y))
    left_idx = X[:, feature] <= threshold
    right_idx = ~left_idx
    if np.sum(left_idx) == 0 or np.sum(right_idx) == 0:
        return Node(label=majority_class(y))
    left = build_tree(X[left_idx], y[left_idx], depth + 1, max_depth)
    right = build_tree(X[right_idx], y[right_idx], depth + 1, max_depth)
    return Node(feature=feature, threshold=threshold, left=left, right=right)
def predict_sample(node, x):
    """Predicts the class label for a single sample."""
    if node.label is not None:
        return node.label
    if x[node.feature] <= node.threshold:
        return predict_sample(node.left, x)
    else:
        return predict_sample(node.right, x)
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    """Builds a CART decision tree on (X_train, y_train) and predicts labels for X_test."""
    root = build_tree(X_train, y_train, max_depth=max_depth)
    predictions = [predict_sample(root, x) for x in X_test]
    return predictions

import numpy as np
from skimage.transform import resize
def batch_resample(X, new_dim, mode='bilinear'):
    (out_rows, out_cols) = new_dim
    (n_ex, in_rows, in_cols, in_channels) = X.shape
    Y = np.zeros((n_ex, out_rows, out_cols, in_channels))
    if mode not in ['bilinear', 'neighbor']:
        raise NotImplementedError(f'Unrecognized resampling mode: {mode}')
    order = 1 if mode == 'bilinear' else 0
    for i in range(n_ex):
        for c in range(in_channels):
            Y[i, :, :, c] = resize(X[i, :, :, c], (out_rows, out_cols), order=order, mode='reflect')
    return np.round(Y, 4)

import numpy as np
def metric(name: str):
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """
    supported_metrics = ['euclidean', 'manhattan', 'chebyshev', 'cosine']
    if name not in supported_metrics:
        return lambda x, y: -1

    def euclidean(x, y):
        return round(np.sqrt(np.sum((np.array(x) - np.array(y)) ** 2)), 4)

    def manhattan(x, y):
        return round(np.sum(np.abs(np.array(x) - np.array(y))), 4)

    def chebyshev(x, y):
        return round(np.max(np.abs(np.array(x) - np.array(y))), 4)

    def cosine(x, y):
        x = np.array(x)
        y = np.array(y)
        dot_product = np.dot(x, y)
        norm_x = np.linalg.norm(x)
        norm_y = np.linalg.norm(y)
        if norm_x == 0 or norm_y == 0:
            return -1
        return round(1 - dot_product / (norm_x * norm_y), 4)
    distance_functions = {'euclidean': euclidean, 'manhattan': manhattan, 'chebyshev': chebyshev, 'cosine': cosine}
    distance_function = distance_functions[name]

    def validate(x, y):
        if not isinstance(x, (list, tuple, np.ndarray)) or not isinstance(y, (list, tuple, np.ndarray)):
            return False
        try:
            x = np.array(x, dtype=float)
            y = np.array(y, dtype=float)
        except ValueError:
            return False
        if len(x.shape) != 1 or len(y.shape) != 1 or x.size == 0 or (y.size == 0):
            return False
        if x.size != y.size:
            return False
        return True

    def validated_distance(x, y):
        if not validate(x, y):
            return -1
        if name == 'cosine' and (np.linalg.norm(x) == 0 or np.linalg.norm(y) == 0):
            return -1
        return distance_function(x, y)
    return validated_distance

def get_gym_environs(registry: list) -> list:
    """Extract unique environment ids from a registry.

    Args:
        registry (list): A list whose elements can be any Python object. When an
            element is a dictionary **and** contains the key "id", the value of
            that key is considered an environment id.

    Returns:
        list: A list of unique ids in the order they first appear in *registry*.
    """
    unique_ids = {}
    for item in registry:
        if isinstance(item, dict) and 'id' in item:
            id_value = item['id']
            if id_value not in unique_ids:
                unique_ids[id_value] = None
