import numpy as np
def one_hot_encode(labels: list[str], categories: list[str] | None=None) -> list[list[int]]:
    """Convert categorical labels to a one-hot encoded matrix.

    Args:
        labels: A list of categorical labels of length *N*.
        categories: Optional complete list of unique categories of length *C*.
            If *None*, the set of categories is inferred from *labels* in the
            order of their first appearance.

    Returns:
        A list of lists that represents the one-hot encoded matrix of shape
        (N, C) where *C* is the number of categories. If an unknown label is
        found when *categories* is supplied, the function returns -1.
    """
    if categories is not None:
        for label in labels:
            if label not in categories:
                return -1
        cats = categories
    else:
        cats = []
        for label in labels:
            if label not in cats:
                cats.append(label)
    encoded = np.zeros((len(labels), len(cats)), dtype=int)
    for (i, label) in enumerate(labels):
        if label in cats:
            encoded[i, cats.index(label)] = 1
    return encoded.tolist()

import numpy as np
def analyze_bandit(payoffs: list[list[int | float]], payoff_probs: list[list[float]]) -> tuple[list[float], int, float]:
    if len(payoffs) != len(payoff_probs):
        return -1
    expected_values = []
    for k in range(len(payoffs)):
        if len(payoffs[k]) != len(payoff_probs[k]):
            return -1
        prob_sum = sum(payoff_probs[k])
        if abs(prob_sum - 1) > 1e-08:
            return -1
        expected_payoff = sum((x * p for (x, p) in zip(payoffs[k], payoff_probs[k])))
        expected_values.append(round(expected_payoff, 4))
    max_ev = max(expected_values)
    best_arm = min([i for (i, ev) in enumerate(expected_values) if ev == max_ev])
    best_ev = round(max_ev, 4)
    return (expected_values, best_arm, best_ev)

import functools
from typing import Callable, Any
def doublewrap(decorator_func: Callable[..., Callable[..., Any]]) -> Callable[..., Any]:
    """Return a decorator that can be used *with* or *without* parentheses.

    Args:
        decorator_func: A callable that expects the function to decorate as
            its first positional argument followed only by *optional*
            positional or keyword arguments.

    Returns:
        A new decorator that forwards the call in the correct form so that
        ``decorator_func`` can be applied either directly or after receiving
        its own optional parameters.
    """

    @functools.wraps(decorator_func)
    def new_decorator(*args, **kwargs):
        if len(args) == 1 and callable(args[0]):
            return decorator_func(args[0])
        else:

            @functools.wraps(decorator_func)
            def real_decorator(func):
                return decorator_func(func, *args, **kwargs)
            return real_decorator
    return new_decorator

from collections import deque
def extract_realtime_model(graph: dict[str, list[str]]) -> list[str]:
    if 'input' not in graph:
        return []
    queue = deque([('input', ['input'])])
    visited = set()
    shortest_paths = []
    min_distance = float('inf')
    while queue:
        (node, path) = queue.popleft()
        if node == 'output_realtime':
            if len(path) < min_distance:
                min_distance = len(path)
                shortest_paths = [path]
            elif len(path) == min_distance:
                shortest_paths.append(path)
        if node not in visited and len(path) <= min_distance:
            visited.add(node)
            if node in graph:
                for neighbor in graph[node]:
                    if neighbor not in path:
                        new_path = path + [neighbor]
                        queue.append((neighbor, new_path))
    if shortest_paths:
        return min(shortest_paths)
    else:
        return []

import numpy as np
def he_normal(shape: tuple[int, ...]) -> list:
    """Return weights initialised with He Normal strategy.

    Args:
        shape: A tuple that represents the desired tensor shape.

    Returns:
        Nested Python list containing the initialised weights.
    """
    if len(shape) == 0:
        fan_in = 0
    elif len(shape) == 1:
        fan_in = shape[0]
    elif len(shape) == 2:
        fan_in = shape[0]
    else:
        receptive_field_size = np.prod(shape[2:])
        fan_in = shape[1] * receptive_field_size
    if fan_in == 0:
        return []
    std = np.sqrt(2 / fan_in)
    rng = np.random.default_rng(42)
    weights = rng.normal(0, std, shape)
    return weights.tolist()

def hand_value(hand: list[int]) -> int:
    """Calculate the highest legal value of a hand not larger than 21."""
    sum_hand = sum(hand)
    aces = hand.count(1)
    while aces > 0 and sum_hand + 10 <= 21:
        sum_hand += 10
        aces -= 1
    return sum_hand if sum_hand <= 21 else 22
def is_natural_blackjack(hand: list[int]) -> bool:
    """Check if a given hand is a natural blackjack."""
    return len(hand) == 2 and 1 in hand and (10 in hand)
def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack."""
    player_score = hand_value(player)
    dealer_score = hand_value(dealer)
    player_bust = player_score > 21
    dealer_bust = dealer_score > 21
    if player_bust:
        return -1.0
    if dealer_bust:
        if is_natural_blackjack(player):
            return 1.5
        else:
            return 1.0
    elif player_score > dealer_score:
        if is_natural_blackjack(player):
            return 1.5
        else:
            return 1.0
    elif player_score == dealer_score:
        return 0.0
    else:
        return -1.0

from typing import List
def score(hand: List[str]) -> int:
    """Return the Blackjack score of *hand* or 0 if the hand is bust.

    Args:
        hand: A list of card labels ('2'-'10', 'J', 'Q', 'K', 'A').

    Returns:
        An integer between 0 and 21 (inclusive).  If every possible value of
        the hand exceeds 21 the function returns 0.
    """
    total = 0
    aces = 0
    for card in hand:
        if card in ['J', 'Q', 'K']:
            total += 10
        elif card == 'A':
            total += 11
            aces += 1
        else:
            total += int(card)
    while total > 21 and aces:
        total -= 10
        aces -= 1
    if total > 21:
        return 0
    else:
        return total

