import numpy as np
def sigmoid(x):
    """Element-wise logistic sigmoid."""
    x = _to_numpy_array(x)
    result = 1 / (1 + np.exp(-x))
    return _round_result(result, x)
def tanh(x):
    """Element-wise hyperbolic tangent."""
    x = _to_numpy_array(x)
    result = np.tanh(x)
    return _round_result(result, x)
def relu(x):
    """Element-wise Rectified Linear Unit."""
    x = _to_numpy_array(x)
    result = np.maximum(x, 0)
    return _round_result(result, x)
def leaky_relu(x, alpha: float=0.01):
    """Element-wise Leaky-ReLU with slope *alpha* for x<0."""
    x = _to_numpy_array(x)
    result = np.where(x >= 0, x, alpha * x)
    return _round_result(result, x)
def softmax(x):
    """Numerically stable softmax over a 1-D input."""
    x = _to_numpy_array(x)
    x = x - np.max(x)
    exp_x = np.exp(x)
    result = exp_x / np.sum(exp_x)
    return _round_result(result, x)
def get_activation(name):
    """Return the activation function by *name*.

    Args:
        name (str): One of {"sigmoid", "tanh", "relu", "leaky_relu", "softmax"}

    Returns:
        Callable: the requested activation function.

    Raises:
        ValueError: If *name* is not recognised.
    """
    activations = {'sigmoid': sigmoid, 'tanh': tanh, 'relu': relu, 'leaky_relu': leaky_relu, 'softmax': softmax}
    if name not in activations:
        raise ValueError('Invalid activation function.')
    return activations[name]
def _to_numpy_array(x):
    """Convert input to a 1-D NumPy array if it's not already."""
    if isinstance(x, (int, float)):
        return np.array([x])
    elif isinstance(x, (list, tuple)):
        return np.array(x)
    elif isinstance(x, np.ndarray):
        if x.ndim > 1:
            raise ValueError('Input must be a scalar or a 1-D array.')
        return x
    else:
        raise TypeError('Input must be a scalar, list, tuple or 1-D NumPy array.')
def _round_result(result, original_input):
    """Round the result to 4 decimal places and maintain the original input structure."""
    if isinstance(original_input, (int, float)):
        return float(np.round(result, 4).item())
    elif isinstance(original_input, (list, tuple)):
        return np.round(result, 4).tolist()
    elif isinstance(original_input, np.ndarray):
        return np.round(result, 4)

