import numpy as np
def train_decision_stump(X: np.ndarray, y: np.ndarray, sample_weights: np.ndarray) -> dict:
    (n_samples, n_features) = X.shape
    min_weighted_error = float('inf')
    best_feature = None
    best_threshold = None
    best_polarity = None
    for feature_index in range(n_features):
        feature_values = X[:, feature_index]
        unique_values = np.unique(feature_values)
        for threshold in unique_values:
            for polarity in [1, -1]:
                predictions = np.where(feature_values < threshold, polarity, -polarity)
                weighted_error = np.sum(sample_weights[y != predictions])
                if weighted_error < min_weighted_error:
                    min_weighted_error = weighted_error
                    best_feature = feature_index
                    best_threshold = threshold
                    best_polarity = polarity
    return {'feature_index': best_feature, 'threshold': round(best_threshold, 4), 'polarity': best_polarity, 'weighted_error': round(min_weighted_error, 4)}

import numpy as np
from itertools import combinations, permutations
def random_unweighted_graph(n_vertices: int, edge_prob: float=0.5, directed: bool=False) -> list[list[int]]:
    """Generate an un-weighted Erdős–Rényi random graph.

    The function must return the adjacency matrix (a nested list) of the graph.

    Args:
        n_vertices (int): Number of vertices in the graph (labelled 0..n-1).
        edge_prob  (float): Probability *p* that an admissible edge exists.
        directed   (bool):  Whether the graph is directed (default ``False``).

    Returns:
        list[list[int]]: The adjacency matrix *A* where *A[i][j] == 1* iff an
        edge from vertex *i* to vertex *j* exists.  The matrix must consist of
        integers (0/1) only and have shape *(n_vertices, n_vertices)*.
    """
    if n_vertices == 1:
        return [[0]]
    adj_matrix = np.zeros((n_vertices, n_vertices), dtype=int)
    if directed:
        for (i, j) in permutations(range(n_vertices), 2):
            if np.random.rand() <= edge_prob:
                adj_matrix[i, j] = 1
    else:
        for (i, j) in combinations(range(n_vertices), 2):
            if np.random.rand() <= edge_prob:
                adj_matrix[i, j] = 1
                adj_matrix[j, i] = 1
    return adj_matrix.tolist()

import numpy as np
def small_norm_clip(values):
    """Clip each element of *values* to the closed interval [-5, 5].

    Args:
        values (list[int | float] | np.ndarray): One-dimensional numeric data.

    Returns:
        list[float]: A Python list where every element has been limited to the
            range [-5, 5] while preserving the original order.
    """
    if isinstance(values, np.ndarray):
        if len(values.shape) != 1:
            raise ValueError('Input NumPy array must be one-dimensional')
        clipped = np.clip(values, -5, 5)
        return clipped.tolist()
    else:
        clipped_values = []
        for value in values:
            if value < -5:
                clipped_values.append(-5.0)
            elif value > 5:
                clipped_values.append(5.0)
            else:
                clipped_values.append(float(value))
        return clipped_values

def evaluate_decision_tree(tree: dict, sample: list[float]):
    """Traverse a binary decision-tree represented by nested dictionaries.

    Args:
        tree (dict): Root node of the decision tree. Internal nodes contain
            'feature_i', 'threshold', 'true_branch', 'false_branch'. Leaf
            nodes contain only 'value'.
        sample (list[float]): Feature vector that will be classified/regressed.

    Returns:
        The value stored in the reached leaf (int, float, or str).
    """
    if 'value' in tree:
        return tree['value']
    else:
        feature_i = tree['feature_i']
        threshold = tree['threshold']
        if sample[feature_i] <= threshold:
            return evaluate_decision_tree(tree['true_branch'], sample)
        else:
            return evaluate_decision_tree(tree['false_branch'], sample)

import math
def running_stats(data: list[int | float]) -> tuple[float, float, float]:
    """Return the running mean, variance and standard deviation of a stream.

    The computation must be *online*: iterate over the data once while
    keeping only a constant amount of memory (do not call numpy.mean,
    numpy.var, statistics.mean, etc.).  Implement Welford's method as
    explained in the task description.  All three returned numbers must
    be rounded to 4 decimal places.  If the input list is empty return
    (0.0, 0.0, 0.0).
    """
    n = 0
    m = 0.0
    s = 0.0
    for x in data:
        n += 1
        delta = x - m
        m += delta / n
        s += delta * (x - m)
    if n == 0:
        return (0.0, 0.0, 0.0)
    elif n == 1:
        return (round(m, 4), 0.0, 0.0)
    else:
        variance = s / (n - 1)
        std = math.sqrt(variance)
        return (round(m, 4), round(variance, 4), round(std, 4))

import numpy as np
def average_pool2d(X: np.ndarray, pool_shape: tuple[int, int], stride: tuple[int, int], accum_grad: np.ndarray) -> tuple[list, list]:
    (N, C, H, W) = X.shape
    (p_h, p_w) = pool_shape
    (s_h, s_w) = stride
    out_H = (H - p_h) // s_h + 1
    out_W = (W - p_w) // s_w + 1
    pooled_output = np.zeros((N, C, out_H, out_W))
    for n in range(N):
        for c in range(C):
            for h in range(out_H):
                for w in range(out_W):
                    start_h = h * s_h
                    start_w = w * s_w
                    end_h = start_h + p_h
                    end_w = start_w + p_w
                    window = X[n, c, start_h:end_h, start_w:end_w]
                    pooled_output[n, c, h, w] = np.mean(window)
    grad_input = np.zeros_like(X)
    for n in range(N):
        for c in range(C):
            for h in range(out_H):
                for w in range(out_W):
                    start_h = h * s_h
                    start_w = w * s_w
                    end_h = start_h + p_h
                    end_w = start_w + p_w
                    grad = accum_grad[n, c, h, w] / (p_h * p_w)
                    grad_input[n, c, start_h:end_h, start_w:end_w] += grad
    pooled_output = np.round(pooled_output, 4).tolist()
    grad_input = np.round(grad_input, 4).tolist()
    return (pooled_output, grad_input)

import numpy as np
def squared_error(actual, predicted):
    """Calculate the squared error or mean squared error.

    If *actual* and *predicted* are scalars, return the squared difference.
    If they are one-dimensional iterables (same length), return the mean squared
    error.  Return -1 for length mismatches or empty iterables.

    All results must be rounded to 4 decimal places.
    """
    if isinstance(actual, (int, float)) and isinstance(predicted, (int, float)):
        result = (actual - predicted) ** 2
        return round(result, 4)
    try:
        iter(actual)
        iter(predicted)
    except TypeError:
        return -1
    if not isinstance(actual, np.ndarray):
        actual = list(actual)
    if not isinstance(predicted, np.ndarray):
        predicted = list(predicted)
    if len(actual) != len(predicted) or len(actual) == 0:
        return -1
    actual = np.array(actual)
    predicted = np.array(predicted)
    mse = np.mean((actual - predicted) ** 2)
    return round(mse, 4)

import math
from collections import Counter, defaultdict
from typing import List
def ngram_log_prob(corpus: List[str], query: str, N: int) -> float:
    if N < 1:
        raise ValueError('N must be greater than or equal to 1')
    tokenized_corpus = []
    for sentence in corpus:
        tokens = sentence.split()
        if N > 1:
            padded_tokens = ['<bol>'] * (N - 1) + tokens + ['<eol>']
        else:
            padded_tokens = tokens
        tokenized_corpus.append(padded_tokens)
    ngram_counts = Counter()
    prefix_counts = Counter()
    for tokens in tokenized_corpus:
        for i in range(len(tokens) - N + 1):
            ngram = tuple(tokens[i:i + N])
            ngram_counts[ngram] += 1
            if N > 1:
                prefix = ngram[:-1]
                prefix_counts[prefix] += 1
            else:
                prefix_counts['total'] = prefix_counts.get('total', 0) + 1
    query_tokens = query.split()
    if N > 1:
        padded_query_tokens = ['<bol>'] * (N - 1) + query_tokens + ['<eol>']
    else:
        padded_query_tokens = query_tokens
    if len(padded_query_tokens) < N:
        return float('-inf')
    log_prob = 0.0
    for i in range(len(padded_query_tokens) - N + 1):
        ngram = tuple(padded_query_tokens[i:i + N])
        count_ngram = ngram_counts.get(ngram, 0)
        if count_ngram == 0:
            return float('-inf')
        if N > 1:
            prefix = ngram[:-1]
            count_prefix = prefix_counts.get(prefix, 0)
        else:
            count_prefix = prefix_counts.get('total', 0)
        if count_prefix == 0:
            return float('-inf')
        prob = count_ngram / count_prefix
        log_prob += math.log(prob)
    return round(log_prob, 4)

import heapq
from typing import List, Union
Number = Union[int, float]
def top_k_elements(sequence: List[Number], k: int, order: str='largest') -> List[Number]:
    if k <= 0 or not sequence:
        return []
    if k >= len(sequence):
        if order == 'largest':
            return sorted(sequence, reverse=True)
        else:
            return sorted(sequence)
    if order == 'largest':
        heap = []
        for num in sequence:
            if len(heap) < k:
                heapq.heappush(heap, num)
            elif num > heap[0]:
                heapq.heappop(heap)
                heapq.heappush(heap, num)
        return sorted(heap, reverse=True)
    elif order == 'smallest':
        heap = []
        for num in sequence:
            if len(heap) < k:
                heapq.heappush(heap, -num)
            elif -num > heap[0]:
                heapq.heappop(heap)
                heapq.heappush(heap, -num)
        return sorted([-x for x in heap])
    else:
        raise ValueError("Invalid order parameter. It should be 'largest' or 'smallest'.")

class Node:

    def __init__(self, key, val):
        self.key = key
        self.val = val
        self.left = None
        self.right = None

    def __lt__(self, other):
        return self.val < other.val

    def __le__(self, other):
        return self.val <= other.val

    def __gt__(self, other):
        return self.val > other.val

    def __ge__(self, other):
        return self.val >= other.val
def inorder_keys(pairs):
    """Build a BST from (key, value) *pairs* and return keys from an in-order traversal.

    Args:
        pairs (list[tuple[Any, int | float]]): List of (key, numeric value) pairs.

    Returns:
        list: Keys in in-order (ascending by numeric value). Return an empty
        list if *pairs* is empty.
    """
    if not pairs:
        return []
    root = Node(pairs[0][0], pairs[0][1])
    for (key, val) in pairs[1:]:
        node = Node(key, val)
        current = root
        while True:
            if node < current:
                if current.left is None:
                    current.left = node
                    break
                current = current.left
            else:
                if current.right is None:
                    current.right = node
                    break
                current = current.right

    def in_order_traversal(node):
        keys = []
        if node:
            keys += in_order_traversal(node.left)
            keys.append(node.key)
            keys += in_order_traversal(node.right)
        return keys
    return in_order_traversal(root)
