def update_kernel_settings(parameters: dict, hyperparameters: dict, summary_dict: dict) -> tuple[dict, dict]:
    """Update kernel parameters and hyperparameters.

    The function must not modify the input dictionaries.  It should return two
    *new* dictionaries in which the supplied `summary_dict` updates have been
    applied following the rules explained in the task description.

    Args:
        parameters (dict):      Existing kernel parameters.
        hyperparameters (dict): Existing kernel hyperparameters.
        summary_dict (dict):    Dictionary that may contain the keys
            "parameters" and/or "hyperparameters" whose values are dictionaries
            of updates.  Any top-level keys are also treated as updates.

    Returns:
        tuple[dict, dict]: `(new_parameters, new_hyperparameters)` – the
        updated dictionaries.
    """
    new_parameters = parameters.copy()
    new_hyperparameters = hyperparameters.copy()
    flattened_updates = {}
    if 'parameters' in summary_dict:
        flattened_updates.update(summary_dict['parameters'])
    if 'hyperparameters' in summary_dict:
        flattened_updates.update(summary_dict['hyperparameters'])
    for (key, value) in summary_dict.items():
        if key not in ['parameters', 'hyperparameters']:
            flattened_updates[key] = value
    for (key, value) in flattened_updates.items():
        if key in new_parameters:
            new_parameters[key] = value
        elif key in new_hyperparameters:
            new_hyperparameters[key] = value
    return (new_parameters, new_hyperparameters)

from collections import Counter
def build_fp_tree(transactions: list[list[str]], min_support: int) -> dict:
    item_support = Counter()
    for transaction in transactions:
        unique_items = set(transaction)
        for item in unique_items:
            item_support[item] += 1
    frequent_items = {item: support for (item, support) in item_support.items() if support >= min_support}
    ordered_items = sorted(frequent_items.items(), key=lambda x: (-x[1], x[0]))
    global_ordering = [item for (item, _) in ordered_items]
    root = {'support': 0, 'children': {}}
    for transaction in transactions:
        filtered_transaction = [item for item in set(transaction) if item in frequent_items]
        ordered_transaction = sorted(filtered_transaction, key=lambda x: global_ordering.index(x))
        current_node = root
        for item in ordered_transaction:
            if item not in current_node['children']:
                current_node['children'][item] = {'support': 0, 'children': {}}
            current_node = current_node['children'][item]
            current_node['support'] += 1
    return root

