def get_initializer(name: str):
    """Return a weight-initialisation callable associated with *name*.

    The callable must have the signature
        initializer(shape: tuple[int, ...], scale: float = 1.0) -> np.ndarray
    and create a numpy array filled according to the requested strategy.

    Supported strategies (case-insensitive):
        - "zeros"
        - "ones"
        - "normal"
        - "glorot_uniform"

    If *name* is not recognised the function must raise `ValueError`.
    """
    name = name.lower()
    if name == 'zeros':

        def zeros_initializer(shape: tuple[int, ...], scale: float=1.0) -> np.ndarray:
            return np.zeros(shape)
        return zeros_initializer
    elif name == 'ones':

        def ones_initializer(shape: tuple[int, ...], scale: float=1.0) -> np.ndarray:
            return np.ones(shape) * scale
        return ones_initializer
    elif name == 'normal':

        def normal_initializer(shape: tuple[int, ...], scale: float=1.0) -> np.ndarray:
            return np.random.normal(loc=0, scale=scale, size=shape)
        return normal_initializer
    elif name == 'glorot_uniform':

        def glorot_uniform_initializer(shape: tuple[int, ...], scale: float=1.0) -> np.ndarray:
            if len(shape) < 2:
                raise ValueError('Glorot uniform initialisation requires at least 2D shape')
            (fan_in, fan_out) = (shape[0], shape[1])
            limit = scale * np.sqrt(6 / (fan_in + fan_out))
            return np.random.uniform(low=-limit, high=limit, size=shape)
        return glorot_uniform_initializer
    else:
        raise ValueError(f'Unsupported initialisation strategy: {name}')

import numpy as np
def linear_kernel(X: list[list[int | float]] | 'np.ndarray', Y: list[list[int | float]] | 'np.ndarray' | None=None, c0: int | float=0) -> list[list[float]] | int:
    X = np.array(X, dtype=float)
    if Y is not None:
        Y = np.array(Y, dtype=float)
    else:
        Y = X
    if X.size == 0:
        return []
    if X.shape[1] != Y.shape[1]:
        return -1
    K = np.dot(X, Y.T) + c0
    K = np.round(K, 4)
    return K.tolist()

import heapq
def huffman_encode(tokens: list[str]) -> dict[str, str]:
    if not tokens:
        return {}
    frequency = {}
    for token in tokens:
        if token in frequency:
            frequency[token] += 1
        else:
            frequency[token] = 1
    if len(frequency) == 1:
        return {list(frequency.keys())[0]: '0'}
    heap = []
    for (token, freq) in frequency.items():
        node = (freq, token)
        heapq.heappush(heap, node)
    while len(heap) > 1:
        (freq1, token1) = heapq.heappop(heap)
        (freq2, token2) = heapq.heappop(heap)
        new_freq = freq1 + freq2
        new_token = ''
        new_node = (new_freq, new_token, (token1, token2))
        heapq.heappush(heap, new_node)
    heap = []
    for (token, freq) in frequency.items():
        node = Node(token, freq)
        heapq.heappush(heap, node)
    while len(heap) > 1:
        node1 = heapq.heappop(heap)
        node2 = heapq.heappop(heap)
        new_node = Node('', node1.freq + node2.freq, node1, node2)
        heapq.heappush(heap, new_node)
    root = heap[0]
    huffman_codes = {}

    def traverse(node, code):
        if node.token != '':
            huffman_codes[node.token] = code
        if node.left:
            traverse(node.left, code + '0')
        if node.right:
            traverse(node.right, code + '1')
    traverse(root, '')
    return huffman_codes
class Node:

    def __init__(self, token, freq, left=None, right=None):
        self.token = token
        self.freq = freq
        self.left = left
        self.right = right

    def __lt__(self, other):
        if self.freq == other.freq:
            return self.token < other.token
        return self.freq < other.freq

    def __eq__(self, other):
        return self.freq == other.freq and self.token == other.token

    def __repr__(self):
        return f'Node(token={self.token}, freq={self.freq})'

import numpy as np
def wgan_gp_loss(Y_fake: np.ndarray, module: str, Y_real: np.ndarray | None=None, gradInterp: np.ndarray | None=None, lambda_: float=10.0) -> float:
    """Compute the WGAN-GP loss for the critic or the generator."""
    if module == 'G':
        loss = -np.mean(Y_fake)
    elif module == 'C':
        if Y_real is None or gradInterp is None:
            raise ValueError('For critic loss, Y_real and gradInterp must be provided.')
        loss = np.mean(Y_fake) - np.mean(Y_real)
        gradInterp_norm = np.linalg.norm(gradInterp, axis=1)
        gradient_penalty = np.mean((gradInterp_norm - 1) ** 2)
        loss += lambda_ * gradient_penalty
    else:
        raise ValueError("Invalid module. Must be 'C' or 'G'.")
    return round(float(loss), 4)

import numpy as np
def leaf_predict(leaf: 'Leaf', classifier: bool):
    """Return the prediction stored in a decision-tree leaf.

    Args:
        leaf: A `Leaf` object whose `value` attribute is either a sequence of
              class probabilities (classification) or a single number
              (regression).
        classifier: When *True* treat the leaf as belonging to a
                     classification tree; otherwise treat it as regression.

    Returns:
        int | float: Predicted class index for classification; otherwise the
                     raw scalar stored in the leaf.
    """
    if classifier:
        if isinstance(leaf.value, (list, tuple, np.ndarray)):
            return np.argmax(leaf.value)
        else:
            raise ValueError('For classification, leaf.value must be a sequence of probabilities.')
    elif isinstance(leaf.value, (int, float)):
        return leaf.value
    else:
        raise ValueError('For regression, leaf.value must be a scalar.')

from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Discrete(Space):

    def __init__(self, n: int):
        self.n = n
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool) -> PyTuple[bool, bool]:
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def _is_space_continuous(space: Space) -> bool:
        if isinstance(space, Box):
            return True
        elif isinstance(space, Discrete):
            return False
        elif isinstance(space, Tuple):
            return all((isinstance(s, Box) for s in space.spaces))
        elif isinstance(space, Dict):
            return all((isinstance(s, Box) for s in space.spaces.values()))
        else:
            raise ValueError('Unsupported space type')
    cont_action = _is_space_continuous(env.action_space) if not tuple_action else isinstance(env.action_space, (Tuple, Dict)) and (_is_space_continuous(env.action_space) if isinstance(env.action_space, (Tuple, Dict)) else False)
    cont_obs = _is_space_continuous(env.observation_space) if not tuple_obs else isinstance(env.observation_space, (Tuple, Dict)) and (_is_space_continuous(env.observation_space) if isinstance(env.observation_space, (Tuple, Dict)) else False)
    if tuple_action and isinstance(env.action_space, (Tuple, Dict)):
        cont_action = _is_space_continuous(env.action_space)
    else:
        cont_action = _is_space_continuous(env.action_space)
    if tuple_obs and isinstance(env.observation_space, (Tuple, Dict)):
        cont_obs = _is_space_continuous(env.observation_space)
    else:
        cont_obs = _is_space_continuous(env.observation_space)
    return (cont_action, cont_obs)

import re
from typing import Any, Dict, Optional
class KernelBase:
    """Minimal base class for all kernels.  Do NOT modify this class."""

    def __init__(self, **kwargs: Any) -> None:
        self.params: Dict[str, Any] = kwargs

    def set_params(self, state: Dict[str, Any]):
        """(Re)sets parameters from a previously stored summary dict."""
        hp = state.get('hyperparameters', {})
        self.params.update({k: v for (k, v) in hp.items() if k != 'id'})
        return self

    def summary(self) -> Dict[str, Any]:
        """Dictionary description that can be fed back to *initialize_kernel*."""
        return {'hyperparameters': {'id': self.__class__.__name__, **self.params}}
class LinearKernel(KernelBase):

    def __init__(self):
        super().__init__()
class PolynomialKernel(KernelBase):

    def __init__(self, degree: int=3, coef0: float=1.0):
        super().__init__(degree=degree, coef0=coef0)
class RBFKernel(KernelBase):

    def __init__(self, gamma: float=1.0):
        super().__init__(gamma=gamma)
def initialize_kernel(param: Optional[Any]=None) -> KernelBase:
    """Create and return a kernel object from various representations.

    Args:
        param: None (default LinearKernel), an existing KernelBase instance, a
            string description such as 'RBF(gamma=0.5)', or a dictionary with a
            'hyperparameters' entry as produced by KernelBase.summary().

    Returns:
        KernelBase: the newly created or forwarded kernel instance.
    """
    if param is None:
        return LinearKernel()
    if isinstance(param, KernelBase):
        return param
    if isinstance(param, str):
        match = re.match('(\\w+)(?:\\((.*)\\))?', param.strip(), re.IGNORECASE)
        if match:
            kernel_name = match.group(1).capitalize() + 'Kernel'
            kernel_params = {}
            if match.group(2):
                for kv in match.group(2).split(','):
                    (key, value) = kv.split('=')
                    key = key.strip()
                    value = value.strip()
                    try:
                        value = int(value)
                    except ValueError:
                        try:
                            value = float(value)
                        except ValueError:
                            pass
                    kernel_params[key] = value
            if kernel_name == 'LinearKernel':
                return LinearKernel()
            elif kernel_name == 'PolynomialKernel':
                return PolynomialKernel(**kernel_params)
            elif kernel_name == 'RBFKernel':
                return RBFKernel(**kernel_params)
            else:
                raise NotImplementedError(f'Unknown kernel: {kernel_name}')
        else:
            raise ValueError(f'Invalid kernel string: {param}')
    if isinstance(param, dict):
        if 'hyperparameters' not in param:
            raise ValueError("Dictionary must contain 'hyperparameters' key")
        hp = param['hyperparameters']
        kernel_id = hp.get('id')
        if kernel_id is None:
            raise ValueError("'hyperparameters' dictionary must contain 'id' key")
        kernel_params = {k: v for (k, v) in hp.items() if k != 'id'}
        if kernel_id == 'LinearKernel':
            return LinearKernel().set_params(param)
        elif kernel_id == 'PolynomialKernel':
            return PolynomialKernel(**kernel_params)
        elif kernel_id == 'RBFKernel':
            return RBFKernel(**kernel_params)
        else:
            raise NotImplementedError(f'Unknown kernel: {kernel_id}')
    raise TypeError('param must be None, a KernelBase instance, a string, or a dictionary')

import numpy as np
def minkowski(x: np.ndarray | list, y: np.ndarray | list, p: float) -> float:
    """Compute the Minkowski–p distance between two vectors.

    Args:
        x (np.ndarray | list): First one-dimensional vector.
        y (np.ndarray | list): Second one-dimensional vector of the same length.
        p (float): Order of the norm (must be > 0).

    Returns:
        float: The Minkowski distance rounded to 4 decimal places, or ‑1 when
               input validation fails.
    """
    if isinstance(x, list):
        x = np.array(x)
    if isinstance(y, list):
        y = np.array(y)
    if x.shape != y.shape or p <= 0:
        return -1
    if len(x.shape) != 1 or len(y.shape) != 1:
        return -1
    try:
        distance = np.power(np.sum(np.power(np.abs(x - y), p)), 1 / p)
        return round(float(distance), 4)
    except Exception as e:
        return -1

import numpy as np
def conv1D(X: np.ndarray, W: np.ndarray, stride: int, pad, dilation: int=0) -> list:
    (n_examples, signal_length, in_channels) = X.shape
    (kernel_width, _, out_channels) = W.shape
    effective_kernel_width = (kernel_width - 1) * (dilation + 1) + 1
    if isinstance(pad, str) and pad.lower() == 'same':
        out_len = int(np.ceil(signal_length / stride))
        total_pad = max(0, (out_len - 1) * stride + effective_kernel_width - signal_length)
        p_left = total_pad // 2
        p_right = total_pad - p_left
    elif isinstance(pad, int):
        p_left = pad
        p_right = pad
    elif isinstance(pad, tuple) and len(pad) == 2:
        (p_left, p_right) = pad
    else:
        raise ValueError('Invalid pad value')
    output_length = (signal_length + p_left + p_right - effective_kernel_width) // stride + 1
    X_padded = np.pad(X, ((0, 0), (p_left, p_right), (0, 0)), mode='constant')
    Y = np.zeros((n_examples, output_length, out_channels))
    for i in range(output_length):
        start_idx = i * stride
        end_idx = start_idx + effective_kernel_width
        step = dilation + 1
        X_slice = X_padded[:, start_idx:end_idx:step, :]
        for j in range(kernel_width):
            idx_in_X_slice = j * (dilation + 1)
            if idx_in_X_slice < X_slice.shape[1]:
                Y[:, i, :] += np.dot(X_slice[:, idx_in_X_slice, :], W[j, :, :])
    return Y.tolist()

import math
def gaussian_cdf(x: float, mean: float, variance: float) -> float:
    """Compute Φ(x; μ, σ²) – the Gaussian CDF evaluated at *x*.

    Parameters
    ----------
    x : float
        The point at which to evaluate the CDF.
    mean : float
        The mean (μ) of the Gaussian distribution.
    variance : float
        The variance (σ²) of the Gaussian distribution. Must be non-negative.

    Returns
    -------
    float
        The probability that a Gaussian random variable with given parameters
        does not exceed *x*, rounded to six decimal places.
    """
    if variance == 0:
        if x < mean:
            return round(0, 6)
        elif x == mean:
            return round(0.5, 6)
        else:
            return round(1, 6)
    else:
        std_dev = math.sqrt(variance)
        z_score = (x - mean) / (math.sqrt(2) * std_dev)
        probability = 0.5 * (1 + math.erf(z_score))
        return round(probability, 6)

import re
def parse_activation(act_str: str):
    """Parse an activation-function description string.

    The string can be one of the following forms:
        • "ReLU"
        • "Sigmoid"
        • "Tanh"
        • "Affine(slope=<number>, intercept=<number>)"
        • "Leaky ReLU(alpha=<number>)"

    Args:
        act_str: Description of the activation function.

    Returns:
        A dictionary with key "type" and, when applicable, its parameters.
        If the input format is invalid, return -1.
    """
    if relu_pattern.match(act_str):
