def classification_accuracy(y_true: list, y_pred: list) -> float:
    """Compute the accuracy between the true and predicted class labels.

    Args:
        y_true (list): Ground-truth class labels.
        y_pred (list): Predicted class labels produced by a model.

    Returns:
        float: The accuracy score rounded to 4 decimal places.
              Returns -1 when the inputs have different lengths or when
              either of the inputs is empty.
    """
    if len(y_true) != len(y_pred):
        return -1
    if len(y_true) == 0 or len(y_pred) == 0:
        return -1
    correct_predictions = 0
    for (true_label, pred_label) in zip(y_true, y_pred):
        if true_label == pred_label:
            correct_predictions += 1
    accuracy = correct_predictions / len(y_true)
    return round(accuracy, 4)

from typing import List, Dict, Set, Tuple
def topological_sort(vertices: list, edges: list[tuple]) -> list | None:
    """Determine a topological ordering of a directed graph.

    The implementation **must** use depth-first search (DFS) and follow the
    deterministic exploration rule described in the task description.

    Args:
        vertices: List of vertex identifiers (hashable).
        edges:    List of directed edges given as 2-tuples (source, target).

    Returns:
        • List of vertices in topological order when the graph is acyclic.
        • ``None`` when the graph contains at least one directed cycle.
    """
    graph: Dict = {v: [] for v in vertices}
    for (u, v) in edges:
        graph[u].append(v)
    state: Dict = {v: 'unvisited' for v in vertices}
    order: List = []

    def dfs(vertex):
        """Perform DFS from the given vertex."""
        state[vertex] = 'visiting'
        for neighbor in [n for n in vertices if n in graph[vertex]]:
            if state[neighbor] == 'unvisited':
                if dfs(neighbor) is None:
                    return None
            elif state[neighbor] == 'visiting':
                return None
        state[vertex] = 'visited'
        order.append(vertex)
        return True
    for vertex in vertices:
        if state[vertex] == 'unvisited':
            if dfs(vertex) is None:
                return None
    return order[::-1]

import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    if order not in [0, 1, 2]:
        return -1
    original_is_scalar = isinstance(x, (int, float))
    x = np.array(x, dtype=float)
    sigmoid_x = 1 / (1 + np.exp(-x))
    if order == 0:
        result = sigmoid_x
    elif order == 1:
        result = sigmoid_x * (1 - sigmoid_x)
    elif order == 2:
        result = sigmoid_x * (1 - sigmoid_x) * (1 - 2 * sigmoid_x)
    result = np.round(result, 4)
    if original_is_scalar:
        return float(result.item())
    else:
        return result.tolist()

import numpy as np
def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    X = np.array(X)
    if Y is not None:
        Y = np.array(Y)
        if X.shape[1] != Y.shape[1]:
            return -1
    else:
        Y = X
    C = X.shape[1]
    if sigma is None:
        sigma = np.sqrt(C / 2)
    if isinstance(sigma, float) or isinstance(sigma, int):
        if sigma <= 0:
            return -1
        sigma_array = np.array([sigma] * C)
    elif isinstance(sigma, list):
        if len(sigma) != C:
            return -1
        sigma_array = np.array(sigma)
        if np.any(sigma_array <= 0):
            return -1
    else:
        return -1
    kernel_matrix = np.zeros((X.shape[0], Y.shape[0]))
    for i in range(X.shape[0]):
        for j in range(Y.shape[0]):
            diff = X[i] - Y[j]
            scaled_diff = diff / sigma_array
            squared_distance = np.sum(scaled_diff ** 2)
            kernel_matrix[i, j] = np.exp(-0.5 * squared_distance)
    kernel_matrix = np.round(kernel_matrix, 4).tolist()
    return kernel_matrix

import numpy as np
from typing import List, Union
def divide_on_feature(X: Union[np.ndarray, List[list]], feature_i: int, threshold) -> List[list]:
    if not isinstance(X, np.ndarray):
        X = np.array(X)
    subset1 = []
    subset2 = []
    for sample in X:
        if isinstance(threshold, (int, float)):
            if sample[feature_i] >= threshold:
                subset1.append(sample.tolist() if isinstance(sample, np.ndarray) else sample)
            else:
                subset2.append(sample.tolist() if isinstance(sample, np.ndarray) else sample)
        elif sample[feature_i] == threshold:
            subset1.append(sample.tolist() if isinstance(sample, np.ndarray) else sample)
        else:
            subset2.append(sample.tolist() if isinstance(sample, np.ndarray) else sample)
    subset1 = subset1
    subset2 = subset2
    return [subset1, subset2]

import numpy as np
def update_q_values(Q: 'np.ndarray', Q_next: 'np.ndarray', actions: list[int], rewards: list[float], dones: list[bool], gamma: float) -> list[list[float]]:
    if gamma < 0 or gamma > 1:
        return -1
    batch_size = Q.shape[0]
    if len(actions) != batch_size or len(rewards) != batch_size or len(dones) != batch_size or (Q_next.shape[0] != batch_size):
        return -1
    y = Q.copy()
    for i in range(batch_size):
        if dones[i]:
            target = rewards[i]
        else:
            target = rewards[i] + gamma * np.max(Q_next[i])
        y[i, actions[i]] = target
    y = np.round(y, 4)
    y_list = y.tolist()
    return y_list

import math
def compute_ball_radius(centroid: list[float], X: list[list[float]]) -> float:
    """Compute the maximum Euclidean distance between *centroid* and the points in *X* 
    (i.e., the Ball-tree node radius), rounded to 4 decimals."""
    if not X:
        return 0.0
    max_distance = 0.0
    for point in X:
        distance = math.sqrt(sum(((a - b) ** 2 for (a, b) in zip(centroid, point))))
        if distance > max_distance:
            max_distance = distance
    return round(max_distance, 4)

import os
def get_scriptdir(file_path: str) -> str:
    """Return the absolute directory that contains *file_path*.

    The function normalises *file_path* by resolving any symbolic links
    and removing ``..`` / ``.`` path segments, then returns only the
    directory component of this canonical path.

    Args:
        file_path: Path string pointing to a file.  May be absolute or
                    relative and may contain symlinks or redundant path
                    elements.

    Returns:
        The absolute directory path that contains *file_path*.  The
        returned path never ends with a path separator (unless it is the
        root directory).
    """
    abs_path = os.path.abspath(file_path)
    components = []
    path = abs_path
    while True:
        (head, tail) = os.path.split(path)
        if tail == '':
            components.insert(0, head)
            break
        components.insert(0, tail)
        path = head
    resolved_path = []
    for component in components:
        if component == '..':
            if resolved_path:
                resolved_path.pop()
        elif component == '.':
            continue
        else:
            resolved_path.append(component)
    canonical_path = ''
    for component in resolved_path:
        if canonical_path == '' and component == '':
            canonical_path = '/'
        elif canonical_path == '':
            canonical_path = component
        else:
            canonical_path = os.path.join(canonical_path, component)
    if canonical_path == '':
        canonical_path = '/'
    real_path = ''
    path_components = canonical_path.split(os.path.sep)
    real_path = ''
    for (i, component) in enumerate(path_components):
        if component == '':
            if real_path == '':
                real_path = '/'
            continue
        path_so_far = os.path.join(real_path, component) if real_path != '/' else '/' + component
        if os.path.islink(path_so_far):
            link_target = os.readlink(path_so_far)
            if os.path.isabs(link_target):
                real_path = link_target
                remaining_components = path_components[i + 1:]
                for c in remaining_components:
                    real_path = os.path.join(real_path, c)
                break
            else:
                real_path = os.path.dirname(path_so_far)
                new_path = os.path.join(real_path, link_target)
                remaining_components = path_components[i + 1:]
                for c in remaining_components:
                    new_path = os.path.join(new_path, c)
                real_path = os.path.normpath(new_path)
                break
        elif real_path == '' or real_path == '/':
            real_path = os.path.join(real_path, component)
        else:
            real_path = os.path.join(real_path, component)
    dir_path = os.path.dirname(real_path)
    if dir_path == '':
        dir_path = '/'
    return dir_path

import numpy as np
def regression_tree_predict(X_train: list[float | int], y_train: list[float | int], X_test: list[float | int], max_depth: int=3) -> list[float]:
    """Fit a univariate regression tree of depth *max_depth* and predict.

    Parameters
    ----------
    X_train : list[float | int]
        1-D feature values for the training set.
    y_train : list[float | int]
        Continuous target values for the training set.
    X_test : list[float | int]
        1-D feature values for which predictions are required.
    max_depth : int, default 3
        Maximum depth of the binary tree (root has depth 0).

    Returns
    -------
    list[float]
        Predictions for every element in *X_test*.
    """
    if not X_train:
        return []
    data = np.array(list(zip(X_train, y_train)))

    def build_tree(data, depth):
        if depth == max_depth or len(data) < 2:
            return np.mean(data[:, 1])
        data = data[data[:, 0].argsort()]
        best_split = None
        best_sse = float('inf')
        for i in range(1, len(data)):
            left_sse = np.sum((data[:i, 1] - np.mean(data[:i, 1])) ** 2)
            right_sse = np.sum((data[i:, 1] - np.mean(data[i:, 1])) ** 2)
            sse = left_sse + right_sse
            if sse < best_sse:
                best_sse = sse
                best_split = i
        if best_split is not None:
            left_child = build_tree(data[:best_split], depth + 1)
            right_child = build_tree(data[best_split:], depth + 1)
            return {'feature': data[best_split, 0], 'left': left_child, 'right': right_child}
        else:
            return np.mean(data[:, 1])
    tree = build_tree(data, 0)

    def predict(tree, X_test):
        predictions = []
        for x in X_test:
            node = tree
            while isinstance(node, dict):
                if x <= node['feature']:
                    node = node['left']
                else:
                    node = node['right']
            predictions.append(node)
        return predictions
    return predict(tree, X_test)

import numpy as np
from itertools import combinations_with_replacement
def polynomial_features(X: list[list[int | float]], degree: int) -> list[list[float]]:
    if degree < 0:
        return -1
    X = np.array(X)
    (n_samples, n_features) = X.shape
    poly_features = []
    for sample in X:
        sample_features = []
        for d in range(degree + 1):
            for indices in combinations_with_replacement(range(n_features), d):
                monomial = 1
                index_counts = {}
                for index in indices:
                    if index not in index_counts:
                        index_counts[index] = 0
                    index_counts[index] += 1
                for (index, count) in index_counts.items():
                    monomial *= sample[index] ** count
                sample_features.append(monomial)
        poly_features.append(sample_features)
    return poly_features

import numpy as np
def leaky_relu(x: np.ndarray | list | int | float, alpha: float=0.3, order: int=0) -> list:
    """Compute Leaky ReLU, its first derivative, or its second derivative.

    Args:
        x: Values to be transformed. Accepts NumPy array, list, or scalar.
        alpha: Slope used when an element is non-positive (default 0.3).
        order: 0 – return the function value; 1 – return the first derivative; 2 – return the second derivative.

    Returns:
        Python list with the same shape as *x* that contains the requested quantity.
    """
    x_arr = np.array(x)
    if order not in [0, 1, 2]:
        return []
    if order == 0:
        result = np.where(x_arr > 0, x_arr, alpha * x_arr)
    elif order == 1:
        result = np.where(x_arr > 0, 1, alpha)
    else:
        result = np.zeros_like(x_arr)
    return result.tolist()

from typing import Any, Dict
def flatten_parameters(params: Dict[str, Any]) -> Dict[str, Any]:
    """Flatten a nested parameter dictionary.

    A dot (`.`) is used to join nested keys.  The input dictionary must not be
    mutated.

    Args:
        params: A potentially nested dictionary where some values can be other
                dictionaries.

    Returns:
        A new dictionary without nested structures.
    """
    flat_params = {}
    for (key, value) in params.items():
        if isinstance(value, dict):
            sub_params = flatten_parameters(value)
            for (sub_key, sub_value) in sub_params.items():
                flat_params[f'{key}.{sub_key}'] = sub_value
        else:
            flat_params[key] = value
    return flat_params

import numpy as np
def sigmoid_grad(z):
    if isinstance(z, np.ndarray):
        result = z * (1 - z)
        return np.round(result, 4)
    elif isinstance(z, list):
        if len(z) == 0:
            return []
        else:
            return [sigmoid_grad(i) for i in z]
    elif isinstance(z, (int, float)):
        result = z * (1 - z)
        return float(np.round(result, 4))
    else:
        raise TypeError('Input must be a float, list or NumPy ndarray')

import numpy as np
def relu_grad(z):
    """Compute the element-wise derivative of the ReLU activation.

    The function should return 0 for every entry that is less than or equal to
    zero and 1 for every entry that is strictly greater than zero.  Scalars
    must yield a single integer; arrays/lists must yield a list of identical
    shape.
    """
    if isinstance(z, np.ndarray):
        if z.ndim == 0:
            return 1 if z.item() > 0 else 0
        else:
            grad = np.where(z > 0, 1, 0)
            return grad.tolist()
    elif isinstance(z, list):
        return [relu_grad(item) for item in z]
    else:
        return 1 if z > 0 else 0

import os
from typing import List, Union
def extract_archive(file_path: str, path: str='.', archive_format: Union[str, List[str], None]='auto') -> bool:
    """Checks whether *file_path* belongs to an allowed archive format.

    The function MUST NOT raise any exception and MUST NOT perform real file
    extraction.  Read the task description for the full specification.

    Args:
        file_path: Full path (or name) of the archive file.
        path: Destination folder – ignored in this simplified version.
        archive_format: See task description.

    Returns:
        True if the file name matches one of the allowed formats, False otherwise.
    """
    supported_formats = {'tar': ['.tar', '.tar.gz', '.tgz', '.tar.bz', '.tar.bz2', '.tbz'], 'zip': ['.zip']}
    if archive_format is None or (isinstance(archive_format, list) and len(archive_format) == 0):
        return False
    if archive_format == 'auto':
        allowed_formats = list(supported_formats.keys())
    elif isinstance(archive_format, str):
        allowed_formats = [archive_format]
    else:
        allowed_formats = archive_format
    file_ext = os.path.splitext(file_path)[1].lower()
    if file_ext == '.tar':
        for ext in ['.gz', '.bz', '.bz2']:
            if file_path.lower().endswith('.tar' + ext):
                file_ext = '.tar' + ext
                break
    for fmt in allowed_formats:
        if fmt in supported_formats and file_ext in [ext.lower() for ext in supported_formats[fmt]]:
            return True
    return False

def tree_stats(root: 'node | None') -> tuple[int, int]:
    """Return the number of leaf nodes and the maximum depth of the tree.

    A **leaf** is defined as a node whose ``results`` attribute is not ``None``.

    Parameters
    ----------
    root : node | None
        Root of the decision tree.  If *None*, the tree is considered empty.

    Returns
    -------
    tuple[int, int]
        ``(leaf_count, max_depth)`` where
        * *leaf_count* is the total number of leaves in the tree, and
        * *max_depth* is the length of the longest path from *root* to a leaf.
    """
    if root is None:
        return (0, 0)

    def traverse(node):
        if node.results is not None:
            return (1, 1)
        (left_leaf_count, left_max_depth) = traverse(node.left) if node.left else (0, 0)
        (right_leaf_count, right_max_depth) = traverse(node.right) if node.right else (0, 0)
        total_leaf_count = left_leaf_count + right_leaf_count
        max_depth = max(left_max_depth, right_max_depth) + 1
        return (total_leaf_count, max_depth)
    return traverse(root)

from collections import Counter
from typing import Any, Dict, List
def gini_impurity(data: List[List[Any]]) -> float:
    """Compute the Gini impurity of a dataset."""
    labels = [sample[-1] for sample in data]
    label_counts = Counter(labels)
    total_samples = len(labels)
    gini = 1.0
    for count in label_counts.values():
        gini -= (count / total_samples) ** 2
    return gini
def find_best_split(data: List[List[Any]]) -> (Dict[str, Any], float):
    """Find the best split for a dataset."""
    best_split = {}
    best_gain = 0.0
    num_samples = len(data)
    gini_before = gini_impurity(data)
    for feature_idx in range(len(data[0]) - 1):
        unique_values = set((sample[feature_idx] for sample in data))
        for value in sorted(unique_values):
            left_data = [sample for sample in data if sample[feature_idx] == value]
            right_data = [sample for sample in data if sample[feature_idx] != value]
            if not left_data or not right_data:
                continue
            left_gini = gini_impurity(left_data)
            right_gini = gini_impurity(right_data)
            weighted_gini = len(left_data) / num_samples * left_gini + len(right_data) / num_samples * right_gini
            gain = gini_before - weighted_gini
            if gain > best_gain:
                best_gain = gain
                best_split = {'feature': feature_idx, 'value': value, 'left': left_data, 'right': right_data}
    return (best_split, best_gain)
def build_tree(data: List[List[Any]]) -> Dict[str, Any]:
    """Build a CART decision tree using the Gini impurity criterion."""
    if not data:
        return {}
    (best_split, best_gain) = find_best_split(data)
    if best_gain > 0:
        return {'feature': best_split['feature'], 'value': best_split['value'], 'left': build_tree(best_split['left']), 'right': build_tree(best_split['right'])}
    else:
        labels = [sample[-1] for sample in data]
        label_counts = Counter(labels)
        majority_label = label_counts.most_common(1)[0][0]
        return {'label': majority_label}
