import numpy as np
def forward_step(A: list[list[float]], B: list[list[float]], S: list[float], obs: list[int], t: int) -> list[float]:
    try:
        A = np.array(A)
        B = np.array(B)
        S = np.array(S)
        if len(S.shape) != 1 or len(A.shape) != 2 or len(B.shape) != 2:
            return -1
        n = S.shape[0]
        if A.shape[0] != n or A.shape[1] != n or B.shape[0] != n:
            return -1
        if t < 0 or t >= len(obs):
            return -1
        obs_0 = obs[0]
        if obs_0 < 0 or obs_0 >= B.shape[1]:
            return -1
        alpha_0 = S * B[:, obs_0]
        if t == 0:
            return [round(float(x), 4) for x in alpha_0]
        alpha_t = alpha_0
        for i in range(1, t + 1):
            if obs[i] < 0 or obs[i] >= B.shape[1]:
                return -1
            alpha_t = np.dot(alpha_t, A) * B[:, obs[i]]
        return [round(float(x), 4) for x in alpha_t]
    except Exception as e:
        return -1

import numpy as np
import math
from collections import defaultdict
def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    """
    Maximum Entropy Classifier with Generalised Iterative Scaling.

    Parameters:
    - X_train (np.ndarray): Training samples.
    - y_train (np.ndarray): Training labels.
    - X_test (np.ndarray): Samples to classify.
    - epsilon (float, optional): Convergence threshold. Defaults to 1e-3.
    - n_iter (int, optional): Maximum number of iterations. Defaults to 100.

    Returns:
    - list[int]: Predicted labels for every row in X_test.
    """
    (n_samples, n_features) = X_train.shape
    labels = np.unique(y_train)
    feature_value_label_pairs = defaultdict(int)
    empirical_dist = defaultdict(float)
    for i in range(n_samples):
        for j in range(n_features):
            feature_value = (j, X_train[i, j])
            for label in labels:
                feature_value_label = (feature_value, label)
                feature_value_label_pairs[feature_value_label] = len(feature_value_label_pairs)
                if label == y_train[i]:
                    empirical_dist[feature_value_label] += 1.0
    empirical_dist = {k: v / n_samples for (k, v) in empirical_dist.items()}
    weights = np.zeros(len(feature_value_label_pairs))
    max_f_x_y = 0
    for i in range(n_samples):
        f_x_y = sum((1 for j in range(n_features) if (j, X_train[i, j]) in [(fv[0], fv[1]) for (fv, _) in feature_value_label_pairs.keys()]))
        max_f_x_y = max(max_f_x_y, f_x_y)
    for _ in range(n_iter):
        model_dist = defaultdict(float)
        for i in range(n_samples):
            sum_exp = 0
            exp_values = {}
            for label in labels:
                exp_value = math.exp(sum((weights[feature_value_label_pairs[feature_value, label]] for feature_value in [(j, X_train[i, j]) for j in range(n_features)] if (feature_value, label) in feature_value_label_pairs)))
                exp_values[label] = exp_value
                sum_exp += exp_value
            for label in labels:
                for j in range(n_features):
                    feature_value = (j, X_train[i, j])
                    if (feature_value, label) in feature_value_label_pairs:
                        model_dist[feature_value, label] += exp_values[label] / sum_exp
        model_dist = {k: v / n_samples for (k, v) in model_dist.items()}
        weight_updates = np.zeros_like(weights)
        for (feature_value_label, idx) in feature_value_label_pairs.items():
            weight_updates[idx] = 1.0 / max_f_x_y * math.log(empirical_dist.get(feature_value_label, 0) / model_dist.get(feature_value_label, 1e-09))
        max_update = np.max(np.abs(weight_updates))
        weights += weight_updates
        if max_update < epsilon:
            break
    predictions = []
    for i in range(X_test.shape[0]):
        label_probs = {}
        for label in labels:
            sum_exp = sum((weights[feature_value_label_pairs[feature_value, label]] for feature_value in [(j, X_test[i, j]) for j in range(n_features)] if (feature_value, label) in feature_value_label_pairs))
            label_probs[label] = math.exp(sum_exp)
        sum_prob = sum(label_probs.values())
        label_probs = {label: prob / sum_prob for (label, prob) in label_probs.items()}
        predicted_label = max(label_probs, key=label_probs.get)
        predictions.append(int(predicted_label))
    return predictions

from collections import defaultdict
def naive_bayes_predict(train_data: list[tuple[str, list[str]]], keywords: set[str], test_samples: list[list[str]]) -> list[str]:
    keyword_counts = defaultdict(lambda : defaultdict(int))
    class_counts = defaultdict(int)
    class_keyword_probabilities = {}
    for (label, tokens) in train_data:
        class_counts[label] += 1
        token_set = set(tokens)
        for keyword in keywords:
            if keyword in token_set:
                keyword_counts[label][keyword] += 1
    for (label, count) in class_counts.items():
        class_keyword_probabilities[label] = {keyword: keyword_counts[label][keyword] / count for keyword in keywords}
    predicted_labels = []
    for sample in test_samples:
        sample_set = set(sample)
        likelihoods = {}
        for label in class_counts.keys():
            likelihood = 1
            for keyword in keywords:
                probability = class_keyword_probabilities[label][keyword]
                if keyword in sample_set:
                    likelihood *= probability
                else:
                    likelihood *= 1 - probability
            likelihoods[label] = likelihood
        predicted_label = max(likelihoods, key=likelihoods.get)
        predicted_labels.append(predicted_label)
    return predicted_labels

import numpy as np
def elastic_net_regression(X: np.ndarray, y: np.ndarray, learning_rate: float, iterations: int, l1_penalty: float, l2_penalty: float) -> tuple[list[float], float]:
    """Fits a linear model with Elastic-Net regularisation."""
    (m, n) = X.shape
    weights = np.zeros(n)
    bias = 0.0
    if learning_rate == 0 or iterations == 0:
        return (list(np.round(weights, 4)), round(bias, 4))
    for _ in range(iterations):
        y_hat = np.dot(X, weights) + bias
        r = y - y_hat
        weights_gradients = (-2 * np.dot(X.T, r) + l1_penalty * np.sign(weights) + 2 * l2_penalty * weights) / m
        bias_gradient = -2 * np.sum(r) / m
        weights -= learning_rate * weights_gradients
        bias -= learning_rate * bias_gradient
    return (list(np.round(weights, 4)), round(bias, 4))

import numpy as np
def nearest_neighbor_graph(X):
    X = np.array(X)
    n = X.shape[0]
    if n == 1:
        return [[0]]
    k = min(int(np.sqrt(n)), 10)
    distances = np.linalg.norm(X[:, np.newaxis] - X, axis=2)
    np.fill_diagonal(distances, np.inf)
    nearest_neighbors = np.argsort(distances, axis=1)[:, :k]
    W = np.zeros((n, n))
    for i in range(n):
        for j in nearest_neighbors[i]:
            W[i, j] = 1
    W = np.logical_or(W, W.T).astype(int)
    return W.tolist()

import numpy as np
from typing import List, Tuple, Union
def minesweeper_step(ground: List[List[Union[float, int]]], flags: List[List[bool]]) -> Tuple[List[Tuple[int, int]], List[Tuple[int, int]]]:
    """Perform one logical deduction step on a Minesweeper board.

    The function receives the currently visible part of the board (```ground```)
    and the already placed flags (```flags```). It must return two *sorted* lists:

    1. **clicks** – every square that can be safely revealed.
    2. **new_flags** – every square that must be a bomb according to the rules
       described in the task description.

    Notes:
        * ``ground`` and ``flags`` have identical dimensions.
        * Hidden squares are represented by ``np.nan`` inside ``ground``.
        * Neighbourhood is the 8-connected Moore neighbourhood.
    """
    ground_array = np.array(ground)
    flags_array = np.array(flags)
    (rows, cols) = ground_array.shape
    clicks = set()
    new_flags = set()
    for r in range(rows):
        for c in range(cols):
            if not np.isnan(ground_array[r, c]) and (not flags_array[r, c]):
                value = ground_array[r, c]
                neighbours = get_neighbours(r, c, rows, cols)
                bombs = sum((flags_array[n[0], n[1]] for n in neighbours))
                unexplored = sum((np.isnan(ground_array[n[0], n[1]]) and (not flags_array[n[0], n[1]]) for n in neighbours))
                if value == 0:
                    for n in neighbours:
                        if np.isnan(ground_array[n[0], n[1]]) and (not flags_array[n[0], n[1]]):
                            clicks.add((n[0], n[1]))
                elif value == 8:
                    for n in neighbours:
                        if np.isnan(ground_array[n[0], n[1]]) and (not flags_array[n[0], n[1]]):
                            new_flags.add((n[0], n[1]))
                elif bombs == value:
                    for n in neighbours:
                        if np.isnan(ground_array[n[0], n[1]]) and (not flags_array[n[0], n[1]]):
                            clicks.add((n[0], n[1]))
                elif unexplored == value - bombs:
                    for n in neighbours:
                        if np.isnan(ground_array[n[0], n[1]]) and (not flags_array[n[0], n[1]]):
                            new_flags.add((n[0], n[1]))
    clicks = sorted(list(clicks))
    new_flags = sorted(list(new_flags))
    return (clicks, new_flags)
def get_neighbours(r: int, c: int, rows: int, cols: int) -> List[Tuple[int, int]]:
    """Get the 8-connected neighbours of a cell."""
    neighbours = []
    for dr in [-1, 0, 1]:
        for dc in [-1, 0, 1]:
            if dr == 0 and dc == 0:
                continue
            (nr, nc) = (r + dr, c + dc)
            if 0 <= nr < rows and 0 <= nc < cols:
                neighbours.append((nr, nc))
    return neighbours

from copy import deepcopy
def deduce_mines(constraints: list[list]) -> tuple[list[str], list[str]]:
    """Simplify Minesweeper constraints.

    You receive *constraints* as a list of 2-element lists *[variables, value]*
    where *variables* is a list of identifiers and *value* is an int telling how
    many of those variables hide a mine.

    The function must apply the logical rules described in the task statement
    (solved constraints, propagation, subset rule, duplicate removal) until no
    new information can be obtained.

    The result is a tuple (safe, mines) of two **alphabetically sorted** lists.
    The first element contains all variables that are certainly safe, the second
    one all variables that are certainly mines.  If nothing can be deduced an
    empty list must be returned instead.
    """
    constraints = deepcopy(constraints)
    safe = set()
    mines = set()
    changed = True
    while changed:
        changed = False
        for (variables, value) in constraints[:]:
            if value == 0:
                for var in variables:
                    if var not in mines:
                        safe.add(var)
                        changed = True
                constraints.remove([variables, value])
            elif value == len(variables):
                for var in variables:
                    if var not in safe:
                        mines.add(var)
                        changed = True
                constraints.remove([variables, value])
        for var in list(safe) + list(mines):
            for (i, (variables, value)) in enumerate(constraints):
                if var in variables:
                    new_variables = [v for v in variables if v != var]
                    if var in mines:
                        new_value = value - 1
                    else:
                        new_value = value
                    constraints[i] = [new_variables, new_value]
                    changed = True
        for (i, (variables1, value1)) in enumerate(constraints):
            for (j, (variables2, value2)) in enumerate(constraints):
                if i != j and set(variables1).issubset(set(variables2)):
                    new_variables = list(set(variables2) - set(variables1))
                    new_value = value2 - value1
                    if new_value >= 0:
                        constraints[j] = [new_variables, new_value]
                        changed = True
        seen = set()
        new_constraints = []
        for (variables, value) in constraints:
            key = (tuple(sorted(variables)), value)
            if key not in seen:
                seen.add(key)
                new_constraints.append([variables, value])
            else:
                changed = True
        constraints = new_constraints
    return (sorted(list(safe)), sorted(list(mines)))
