import numpy as np
def glm_links():
    """Construct and return standard GLM link functions.

    Returns
    -------
    dict
        A three-entry dictionary (identity, log, logit) where each entry is a
        dictionary containing callables for the link, its inverse and its
        derivative with respect to μ. All functions must work with numpy
        scalars as well as 1-D/2-D numpy arrays via element-wise operations.
    """

    def identity_link(mu):
        return mu

    def identity_inv_link(eta):
        return eta

    def identity_derivative(mu):
        return np.ones_like(mu)

    def log_link(mu):
        return np.log(mu)

    def log_inv_link(eta):
        return np.exp(eta)

    def log_derivative(mu):
        return 1 / mu

    def logit_link(mu):
        return np.log(mu / (1 - mu))

    def logit_inv_link(eta):
        return 1 / (1 + np.exp(-eta))

    def logit_derivative(mu):
        return 1 / (mu * (1 - mu))
    links = {'identity': {'link': identity_link, 'inv_link': identity_inv_link, 'derivative': identity_derivative}, 'log': {'link': log_link, 'inv_link': log_inv_link, 'derivative': log_derivative}, 'logit': {'link': logit_link, 'inv_link': logit_inv_link, 'derivative': logit_derivative}}
    return links

def build_tree(items: list[str], counts: list[int], parents: list[int]) -> dict:
    if not items or not counts or (not parents):
        return {}
    nodes = {}
    for i in range(len(items)):
        node = {'item': items[i], 'count': counts[i], 'children': []}
        nodes[i] = node
    for (i, parent_index) in enumerate(parents):
        if parent_index != -1:
            nodes[parent_index]['children'].append(nodes[i])
    root_index = parents.index(-1)
    return nodes[root_index]

def check_data(a, b):
    """Validate and convert the two inputs so that both are numeric.

    The function must accept integers, floats, or numeric strings.  Any string
    should be converted with `float()`; existing numeric values stay as they
    are.  The pair `(a, b)` must then be returned.
    """
    try:
        a = float(a)
        b = float(b)
        return (a, b)
    except ValueError:
        raise
def validate_input(function):

    def wrapper(a, b):
        (a, b) = check_data(a, b)
        return function(a, b)
    return wrapper
@validate_input
def sum_numbers(a, b):
    """Return the arithmetic sum of *a* and *b* after validation."""
    result = a + b
    return int(result) if result.is_integer() else result

from typing import List, Optional
class Node:
    """Binary decision-tree node (provided).

    Non-leaf node:   rule = (feature_index, threshold)
    Leaf node:       rule = (None, prediction_value)
    """

    def __init__(self, left: Optional['Node'], right: Optional['Node'], rule: tuple):
        self.left = left
        self.right = right
        self.feature = rule[0]
        self.threshold = rule[1]
def predict_tree(root: Optional[Node], X: List[List[float]]) -> List[float]:
    """Run the decision tree on a batch of samples and return the corresponding list of predictions."""
    if root is None:
        return []
    predictions = []
    for sample in X:
        node = root
        while node.left is not None and node.right is not None:
            if sample[node.feature] <= node.threshold:
                node = node.left
            else:
                node = node.right
        predictions.append(node.threshold)
    return predictions

import numpy as np
def compute_impurity(y, criterion):
    """Compute a node's impurity.

    Args:
        y (numpy.ndarray): 1-D array with the target values.
        criterion (str): One of {"entropy", "gini", "mse"}.

    Returns:
        float: Impurity value rounded to 4 decimal places.

    Raises:
        ValueError: If the criterion is not one of {"entropy", "gini", "mse"}.
    """
    if criterion not in ['entropy', 'gini', 'mse']:
        raise ValueError('Invalid criterion. Supported criteria are: entropy, gini, mse')
    if len(y) == 0:
        return 0.0
    if criterion == 'entropy':
        hist = np.bincount(y)
        ps = hist / len(y)
        return -np.sum(ps * np.log2(ps)).round(4)
    elif criterion == 'gini':
        hist = np.bincount(y)
        ps = hist / len(y)
        return (1 - np.sum(ps ** 2)).round(4)
    elif criterion == 'mse':
        return (np.var(y, ddof=0) if len(y) > 1 else 0.0).round(4)

from collections import Counter, defaultdict
def fp_growth(transactions: list[list[str]], min_support: int) -> list[list[str]]:
    """Discover every frequent item-set in *transactions* with FP-Growth.

    A *transaction* is represented by a list of items (strings).  `min_support`
    is the minimum number of transactions an item-set has to appear in.  The
    function returns **all** frequent item-sets where
        support(itemset) >= min_support.

    The result must be deterministic:
      • Inside each item-set the items have to be sorted alphabetically.
      • The outer list has to be sorted by `(len(itemset), itemset)`.
    If *transactions* is empty or no item-set meets the threshold return an
    empty list.
    """
    if not transactions:
        return []
    item_counts = Counter((item for transaction in transactions for item in transaction))
    frequent_items = {item: count for (item, count) in item_counts.items() if count >= min_support}
    if not frequent_items:
        return []
    frequent_items = dict(sorted(frequent_items.items()))
    fp_tree = {}
    header_table = {}
    for transaction in transactions:
        frequent_transaction = [item for item in transaction if item in frequent_items]
        frequent_transaction.sort()
        if frequent_transaction:
            node = fp_tree
            for item in frequent_transaction:
                if item not in node:
                    node[item] = {}
                node = node[item]
                header_table[item] = header_table.get(item, 0) + 1
    fp_tree = rebuild_fp_tree(transactions, frequent_items, min_support)
    frequent_itemsets = []

    def mine_fp_tree(tree, prefix, min_support, header_table):
        for item in sorted(header_table.keys()):
            support = header_table[item]
            if support >= min_support:
                itemset = prefix + [item]
                frequent_itemsets.append(itemset)
                conditional_tree = get_conditional_tree(tree, item, header_table)
                conditional_header_table = get_header_table(conditional_tree)
                mine_fp_tree(conditional_tree, itemset, min_support, conditional_header_table)
    mine_fp_tree(fp_tree, [], min_support, header_table)
    frequent_itemsets.sort(key=lambda x: (len(x), x))
    return frequent_itemsets
def rebuild_fp_tree(transactions, frequent_items, min_support):
    fp_tree = {}
    header_table = defaultdict(int)
    for transaction in transactions:
        frequent_transaction = [item for item in transaction if item in frequent_items]
        frequent_transaction.sort()
        if frequent_transaction:
            node = fp_tree
            for item in frequent_transaction:
                if item not in node:
                    node[item] = {}
                node = node[item]
                header_table[item] += 1
    return fp_tree
def get_conditional_tree(tree, item, header_table):
    conditional_tree = {}

    def traverse(node, prefix):
        for (child, children) in node.items():
            if child != item:
                if child not in prefix:
                    prefix[child] = 0
                prefix[child] += header_table[child]
                if children:
                    if child not in conditional_tree:
                        conditional_tree[child] = {}
                    traverse(children, conditional_tree[child])
    traverse(tree, {})
    return conditional_tree
def get_header_table(tree):
    header_table = {}

    def traverse(node):
        for (child, children) in node.items():
            if child not in header_table:
                header_table[child] = 1
            else:
                header_table[child] += 1
            if children:
                traverse(children)
    traverse(tree)
    return header_table
