import numpy as np
def relu_(Z):
    """Apply the element-wise Rectified Linear Unit (ReLU) activation.

    The function keeps the container type of the input:
    scalar → scalar, list → list, NumPy array → NumPy array.

    Args:
        Z (int | float | list | numpy.ndarray): Input data – scalar, 1-D or 2-D.

    Returns:
        Same type as *Z* with all negative values clipped to 0.
    """
    if isinstance(Z, np.ndarray):
        return np.maximum(Z, 0)
    elif isinstance(Z, list):
        if all((not isinstance(x, list) for x in Z)):
            return [max(0, x) for x in Z]
        else:
            return [[max(0, x) for x in row] for row in Z]
    elif isinstance(Z, (int, float)):
        return max(0, Z)
    else:
        raise TypeError('Unsupported input type. Only scalars, lists, and NumPy arrays are supported.')

import re
from collections import Counter
class Token:

    def __init__(self, word):
        self.count = 0
        self.word = word

    def __repr__(self):
        return "Token(word='{}', count={})".format(self.word, self.count)
def tokenize_and_count(text: str) -> list[Token]:
    lower_text = text.lower()
    words = re.findall('[a-z]+', lower_text)
    if not words:
        return []
    counter = Counter(words)
    token_list = [Token(word) for word in counter.keys()]
    for token in token_list:
        token.count = counter[token.word]
    sorted_tokens = sorted(token_list, key=lambda x: (-x.count, x.word))
    return sorted_tokens

from collections import deque
def escape_fire_maze(grid):
    n = len(grid)
    directions = [(0, 1), (0, -1), (1, 0), (-1, 0)]
    fire_time = [[float('inf')] * n for _ in range(n)]
    fire_queue = deque()
    for i in range(n):
        for j in range(n):
            if grid[i][j] == 'F':
                fire_time[i][j] = 0
                fire_queue.append((i, j))
    while fire_queue:
        (x, y) = fire_queue.popleft()
        for (dx, dy) in directions:
            (nx, ny) = (x + dx, y + dy)
            if 0 <= nx < n and 0 <= ny < n and (grid[nx][ny] != '#') and (fire_time[nx][ny] > fire_time[x][y] + 1):
                fire_time[nx][ny] = fire_time[x][y] + 1
                fire_queue.append((nx, ny))
    if grid[0][0] == 'F' or fire_time[0][0] != float('inf'):
        return -1
    if n == 1 and grid[0][0] == '.':
        return 0
    agent_queue = deque([(0, 0, 0)])
    visited = [[False] * n for _ in range(n)]
    visited[0][0] = True
    while agent_queue:
        (x, y, time) = agent_queue.popleft()
        if x == n - 1 and y == n - 1:
            return time
        for (dx, dy) in directions:
            (nx, ny) = (x + dx, y + dy)
            if 0 <= nx < n and 0 <= ny < n and (grid[nx][ny] != '#') and (not visited[nx][ny]) and (fire_time[nx][ny] > time + 1):
                agent_queue.append((nx, ny, time + 1))
                visited[nx][ny] = True
    return -1

from heapq import heappush, heappop
from typing import List, Tuple, Union
def find_shortest_path(grid: List[List[int]]) -> Union[int, List[Tuple[int, int]]]:
    """Find a shortest path from the top-left to the bottom-right corner of a maze.

    The maze is described by a 2-D list where 0 denotes a free cell and 1 denotes
    a blocked cell. The function uses the A* algorithm with the Manhattan
    heuristic to return *one* optimal path.

    Args:
        grid: Rectangular 2-D list of integers (0 or 1).

    Returns:
        • A list of (row, column) tuples that represents a shortest path
          including the start (0,0) and goal (m-1,n-1) positions.
        • -1 if no such path exists.
    """
    (rows, cols) = (len(grid), len(grid[0]))
    if grid[0][0] == 1 or grid[rows - 1][cols - 1] == 1:
        return -1
    movements = [(0, 1), (0, -1), (1, 0), (-1, 0)]
    queue = [(0, 0, 0)]

    def heuristic(row, col):
        return abs(row - (rows - 1)) + abs(col - (cols - 1))
    came_from = {}
    cost_so_far = {(0, 0): 0}
    while queue:
        (_, row, col) = heappop(queue)
        if row == rows - 1 and col == cols - 1:
            break
        for (dr, dc) in movements:
            (r2, c2) = (row + dr, col + dc)
            if 0 <= r2 < rows and 0 <= c2 < cols and (grid[r2][c2] == 0):
                new_cost = cost_so_far[row, col] + 1
                if (r2, c2) not in cost_so_far or new_cost < cost_so_far[r2, c2]:
                    cost_so_far[r2, c2] = new_cost
                    priority = new_cost + heuristic(r2, c2)
                    heappush(queue, (priority, r2, c2))
                    came_from[r2, c2] = (row, col)
    if (rows - 1, cols - 1) not in cost_so_far:
        return -1
    path = []
    (row, col) = (rows - 1, cols - 1)
    while (row, col) != (0, 0):
        path.append((row, col))

import numpy as np
def sigmoid(X: np.ndarray) -> np.ndarray:
    """The sigmoid activation function."""
    return 1 / (1 + np.exp(-X))
def sigmoid_derivative(A: np.ndarray) -> np.ndarray:
    """Derivative of the sigmoid activation function."""
    return A * (1 - A)
def tanh(X: np.ndarray) -> np.ndarray:
    """The hyperbolic tangent activation function."""
    return np.tanh(X)
def tanh_derivative(A: np.ndarray) -> np.ndarray:
    """Derivative of the hyperbolic tangent activation function."""
    return 1 - A ** 2
def relu(X: np.ndarray) -> np.ndarray:
    """The rectified linear unit activation function."""
    return np.maximum(X, 0)
def relu_derivative(A: np.ndarray) -> np.ndarray:
    """Derivative of the rectified linear unit activation function."""
    return (A > 0).astype(int)
def softmax(X: np.ndarray) -> np.ndarray:
    """The softmax activation function evaluated along the last axis."""
    max_val = np.max(X, axis=-1, keepdims=True)
    exp_X = np.exp(X - max_val)
    return exp_X / np.sum(exp_X, axis=-1, keepdims=True)
def softmax_derivative(A: np.ndarray) -> np.ndarray:
    """Derivative of the softmax activation function."""
    return np.ones_like(A)
def activation_forward_backward(X: np.ndarray, activation: str, upstream_grad: np.ndarray) -> tuple[np.ndarray, np.ndarray]:
    """Apply an activation function and compute its local gradient.

    Parameters
    ----------
    X : np.ndarray
        Raw pre-activation values.
    activation : str
        Name of the activation function. Supported values are
        "sigmoid", "tanh", "relu", and "softmax".
    upstream_grad : np.ndarray
        Gradient that arrives from the layer above (must have the same shape as
        `X`).

    Returns
    -------
    tuple[np.ndarray, np.ndarray]
        A two-element tuple `(A, grad)` where:
          • `A`    – the activation applied to `X`.
          • `grad` – the element-wise product of `upstream_grad` and the local
                      derivative of the activation (rounded to 4 decimals).
    """
    activations = {'sigmoid': (sigmoid, sigmoid_derivative), 'tanh': (tanh, tanh_derivative), 'relu': (relu, relu_derivative), 'softmax': (softmax, softmax_derivative)}
    if activation not in activations:
        raise ValueError(f'Unsupported activation: {activation}')
    A = activations[activation][0](X)
    if activation == 'softmax':
        local_grad = upstream_grad * activations[activation][1](A)
    else:
        local_grad = upstream_grad * activations[activation][1](A)
    A = np.round(A, 4)
    local_grad = np.round(local_grad, 4)
    return (A, local_grad)

def accuracy_score(y_true, y_pred):
    """Return the classification accuracy between *y_true* and *y_pred*.

    The function must:
      • return ‑1 if the two containers have different lengths;
      • return 0 when both containers are empty;
      • otherwise return the proportion of matching elements rounded to
        4 decimal places.

    Args:
        y_true (list | tuple): Ground-truth class labels.
        y_pred (list | tuple): Predicted class labels.

    Returns:
        float | int: The accuracy or ‑1 according to the rules above.
    """
    if len(y_true) != len(y_pred):
        return -1
    if len(y_true) == 0:
        return 0
    correct_predictions = 0
    for (true_label, pred_label) in zip(y_true, y_pred):
        if true_label == pred_label:
            correct_predictions += 1
    accuracy = correct_predictions / len(y_true)
    return round(accuracy, 4)

import numpy as np
def fit_lasso(X: np.ndarray, y: np.ndarray, lambda_param: float=1.0, max_iters: int=100, fit_intercept: bool=True) -> tuple[np.ndarray, float]:
    (m, n) = X.shape
    if fit_intercept:
        X_intercept = np.hstack((np.ones((m, 1)), X))
    else:
        X_intercept = np.hstack((np.zeros((m, 1)), X))
    w = np.zeros(n + 1 if fit_intercept else n)
    for _ in range(max_iters):
        if fit_intercept:
            b = np.mean(y - X_intercept[:, 1:].dot(w[1:]))
            w[0] = b
        for j in range(n):
            if fit_intercept and j == 0:
                continue
            index = 0 if fit_intercept else j
            w_temp = w.copy()
            w_temp[index] = 0
            r_j = y - X_intercept.dot(w_temp)
            x_j = X_intercept[:, index]
            numerator = np.dot(x_j, r_j)
            denominator = np.dot(x_j, x_j)
            if denominator == 0:
                continue
            w[index] = np.sign(numerator) * np.maximum(np.abs(numerator) - lambda_param * m, 0) / denominator
    if fit_intercept:
        b = w[0]
        w = w[1:]
    else:
        b = 0
    return (np.round(w, 4), np.round(b, 4))

import numpy as np
def adaboost_predict(X_train: list[list[int | float]], y_train: list[int], X_test: list[list[int | float]], n_estimators: int=10) -> list[int]:
    n_samples = len(y_train)
    weights = [1.0 / n_samples] * n_samples
    estimators = []
    y_train_binary = [1 if y == 1 else -1 for y in y_train]
    for _ in range(n_estimators):
        best_error = float('inf')
        best_estimator = None
        for feature_index in range(len(X_train[0])):
            thresholds = sorted([X_train[i][feature_index] for i in range(n_samples)])
            for threshold in thresholds:
                for polarity in [1, -1]:
                    (error, stump) = calculate_error(X_train, y_train_binary, weights, feature_index, threshold, polarity)
                    if error < best_error:
                        best_error = error
                        best_estimator = stump
                    elif error == best_error:
                        if (feature_index, threshold, polarity) < (best_estimator['feature_index'], best_estimator['threshold'], best_estimator['polarity']):
                            best_estimator = stump
        if best_error == 0:
            break
        alpha = 0.5 * np.log((1.0 - best_error) / (best_error + 1e-10))
        estimators.append({'feature_index': best_estimator['feature_index'], 'threshold': best_estimator['threshold'], 'polarity': best_estimator['polarity'], 'alpha': alpha})
        for i in range(n_samples):
            prediction = best_estimator['polarity'] if X_train[i][best_estimator['feature_index']] < best_estimator['threshold'] else -best_estimator['polarity']
            weights[i] *= np.exp(-alpha * y_train_binary[i] * prediction)
        weights = [w / sum(weights) for w in weights]
    predictions = []
    for x in X_test:
        H_x = 0
        for estimator in estimators:
            feature_index = estimator['feature_index']
            threshold = estimator['threshold']
            polarity = estimator['polarity']
            alpha = estimator['alpha']
            if x[feature_index] < threshold:
                H_x += alpha * polarity
            else:
                H_x += alpha * -polarity
        prediction = 1 if H_x >= 0 else 0
        predictions.append(prediction)
    return predictions
def calculate_error(X_train, y_train_binary, weights, feature_index, threshold, polarity):
    error = 0
    stump = {'feature_index': feature_index, 'threshold': threshold, 'polarity': polarity}
    for i in range(len(y_train_binary)):
        prediction = polarity if X_train[i][feature_index] < threshold else -polarity
        error += weights[i] * (y_train_binary[i] != prediction)
    return (error, stump)

import numpy as np
def softsign(z):
    """Compute the Softsign activation function.

    Softsign(z) = z / (1 + |z|)

    The function accepts either a scalar or a list/NumPy array. For a scalar it
    returns a float; for an array-like input it returns a list of floats, with
    every element rounded to 4 decimal places.
    """
    if isinstance(z, (int, float)):
        return round(z / (1 + abs(z)), 4)
    elif isinstance(z, (list, np.ndarray)):
        return [round(float(x) / (1 + abs(x)), 4) for x in z]
    else:
        raise ValueError('Input must be a scalar or a list/NumPy array of real numbers.')

import numpy as np
def compute_l1_penalty(weights, C):
    """Compute the L1 regularization penalty.

    Args:
        weights: Sequence or np.ndarray containing numeric weights (any shape).
        C: Regularization strength (non-negative float).

    Returns:
        L1 penalty (float) rounded to 4 decimal places.
    """

    def flatten(weights):
        flat_list = []
        if isinstance(weights, np.ndarray):
            return weights.flatten()
        else:
            for i in weights:
                if isinstance(i, list):
                    flat_list.extend(flatten(i))
                else:
                    flat_list.append(i)
            return flat_list
    if not weights:
        return 0.0
    flat_weights = flatten(weights)
    if isinstance(flat_weights, list):
        flat_weights = np.array(flat_weights)
    l1_penalty = C * np.sum(np.abs(flat_weights))
    return round(l1_penalty, 4)

import functools
from typing import Callable, Any
def doublewrap(decorator_func: Callable[..., Callable[..., Any]]) -> Callable[..., Any]:
    """Return a decorator that can be used *with* or *without* parentheses.

    Args:
        decorator_func: A callable that expects the function to decorate as
            its first positional argument followed only by *optional*
            positional or keyword arguments.

    Returns:
        A new decorator that forwards the call in the correct form so that
        ``decorator_func`` can be applied either directly or after receiving
        its own optional parameters.
    """

    @functools.wraps(decorator_func)
    def wrapper(*args, **kwargs):
        if len(args) == 1 and callable(args[0]):
            return decorator_func(args[0])
        else:

            def real_decorator(func):
                return decorator_func(func, *args, **kwargs)
            return real_decorator
    return wrapper

from collections import deque
def extract_realtime_model(graph: dict[str, list[str]]) -> list[str]:
    if 'input' not in graph or 'output_realtime' not in graph:
        return []
    queue = deque([('input', ['input'])])
    visited = set()
    shortest_path = None
    while queue:
        (node, path) = queue.popleft()
        if node == 'output_realtime':
            if shortest_path is None or len(path) < len(shortest_path):
                shortest_path = path
            elif len(path) == len(shortest_path) and path < shortest_path:
                shortest_path = path
        if node in visited:
            continue
        visited.add(node)
        if node in graph:
            for neighbor in graph[node]:
                new_path = path + [neighbor]
                queue.append((neighbor, new_path))
    if shortest_path is None:
        return []
    else:
        return shortest_path

def blackjack_outcome(player: list[int], dealer: list[int]) -> float:

    def calculate_hand_score(hand):
        score = sum(hand)
        aces = hand.count(1)
        while aces > 0 and score + 10 <= 21:
            score += 10
            aces -= 1
        return score
    player_score = calculate_hand_score(player)
    dealer_score = calculate_hand_score(dealer)
    player_natural_blackjack = len(player) == 2 and (player[0] == 1 and player[1] == 10 or (player[0] == 10 and player[1] == 1))
    if player_score > 21:
        return -1
    elif dealer_score > 21:
        return 1
    else:
        if player_natural_blackjack and player_score == 21:
            if dealer_score != 21 or len(dealer) > 2:
                return 1.5
            elif dealer_score == 21 and len(dealer) == 2:
                return 0
        if player_score > dealer_score:
            return 1
        elif player_score < dealer_score:
            return -1
        else:
            return 0

import numpy as np
def leaky_relu(z, a=0.01):
    """Apply the Leaky ReLU activation to every element in *z*.

    Args:
        z: A scalar, list (possibly nested) or ``numpy.ndarray`` of numbers.
        a: Optional float in [0,1) — the slope for negative inputs. Defaults to 0.01.

    Returns:
        A Python list with the same structure as *z* where each value has been transformed
        by the Leaky ReLU activation.
    """
    if isinstance(z, np.ndarray):
        result = np.where(z >= 0, z, a * z)
        return result.tolist()
    elif isinstance(z, list):
        result = []
        for item in z:
            result.append(leaky_relu(item, a))
        return result
    elif z >= 0:
        return z
    else:
        return a * z

from typing import List
def score(hand: List[str]) -> int:
    """Return the Blackjack score of *hand* or 0 if the hand is bust.

    Args:
        hand: A list of card labels ('2'-'10', 'J', 'Q', 'K', 'A').

    Returns:
        An integer between 0 and 21 (inclusive).  If every possible value of
        the hand exceeds 21 the function returns 0.
    """
    score = 0
    aces = 0
    for card in hand:
        if card in ['J', 'Q', 'K']:
            score += 10
        elif card in ['2', '3', '4', '5', '6', '7', '8', '9', '10']:
            score += int(card)
        elif card == 'A':
            score += 11
            aces += 1
    while score > 21 and aces:
        score -= 10
        aces -= 1
    if score > 21:
        return 0
    else:
        return score

