def noam_learning_rate(step: int, model_dim: int=512, scale_factor: float=1.0, warmup_steps: int=4000) -> float:
    """Return the learning-rate given by the Noam schedule.

    The function must follow the formula described in the task description and
    round the result to 9 decimal places.
    """
    if step < 1:
        return 0.0
    lr = scale_factor * model_dim ** (-0.5) * min(step ** (-0.5), step * warmup_steps ** (-1.5))
    return round(lr, 9)

import numpy as np
def get_initializer(name: str):
    """Return a weight-initialisation callable associated with *name*.

    The callable must have the signature
        initializer(shape: tuple[int, ...], scale: float = 1.0) -> np.ndarray
    and create a numpy array filled according to the requested strategy.

    Supported strategies (case-insensitive):
        - "zeros"
        - "ones"
        - "normal"
        - "glorot_uniform"

    If *name* is not recognised the function must raise `ValueError`.
    """
    name = name.lower()
    if name == 'zeros':

        def initializer(shape: tuple[int, ...], scale: float=1.0) -> np.ndarray:
            return np.zeros(shape)
        return initializer
    elif name == 'ones':

        def initializer(shape: tuple[int, ...], scale: float=1.0) -> np.ndarray:
            return np.ones(shape) * scale
        return initializer
    elif name == 'normal':

        def initializer(shape: tuple[int, ...], scale: float=1.0) -> np.ndarray:
            return np.random.normal(0, scale, shape)
        return initializer
    elif name == 'glorot_uniform':

        def initializer(shape: tuple[int, ...], scale: float=1.0) -> np.ndarray:
            if len(shape) < 2:
                raise ValueError('Glorot uniform initialization requires at least 2D shape')
            fan_in = shape[0]
            fan_out = shape[1] if len(shape) > 1 else 1
            if fan_out == 1:
                raise ValueError('Glorot uniform initialization requires at least 2D shape')
            limit = scale * np.sqrt(6 / (fan_in + fan_out))
            return np.random.uniform(-limit, limit, shape)
        return initializer
    else:
        raise ValueError(f'Unsupported initializer: {name}')

import heapq
from collections import defaultdict
def huffman_encode(tokens: list[str]) -> dict[str, str]:
    """Generate a Huffman code for the supplied tokens.

    The function must return a dictionary mapping **every** unique token in
    *tokens* to its binary Huffman code.  All codes must be prefix-free and
    consist only of the characters ‘0’ and ‘1’.
    """
    if not tokens:
        return {}
    frequency = defaultdict(int)
    for token in tokens:
        frequency[token] += 1
    if len(frequency) == 1:
        return {list(frequency.keys())[0]: '0'}
    heap = [[weight, [token, '']] for (token, weight) in frequency.items()]
    heapq.heapify(heap)
    while len(heap) > 1:
        lo = heapq.heappop(heap)
        hi = heapq.heappop(heap)
        for pair in lo[1:]:
            pair[1] = '0' + pair[1]
        for pair in hi[1:]:
            pair[1] = '1' + pair[1]
        heapq.heappush(heap, [lo[0] + hi[0]] + lo[1:] + hi[1:])
    huffman_code = sorted(heap[0][1:], key=lambda p: (len(p[-1]), p[0]))
    huffman_dict = {}
    for p in huffman_code:
        huffman_dict[p[0]] = p[1]
    return huffman_dict
