import numpy as np
import math
def entropy(y):
    """Compute the base-2 Shannon entropy of a vector of integer labels.

    Parameters
    ----------
    y : list[int] | np.ndarray
        One-dimensional iterable containing non-negative integer class labels.

    Returns
    -------
    float
        The entropy value rounded to 4 decimal places.
    """
    label_counts = {}
    for label in y:
        if label in label_counts:
            label_counts[label] += 1
        else:
            label_counts[label] = 1
    total_samples = len(y)
    if total_samples == 0:
        return 0.0
    entropy_value = 0.0
    for count in label_counts.values():
        p_i = count / total_samples
        if p_i > 0:
            entropy_value -= p_i * math.log(p_i, 2)
    return round(entropy_value, 4)

from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool) -> PyTuple[bool, bool]:
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def is_space_continuous(space: Space, is_composite: bool) -> bool:
        """Helper function to check if a space is continuous."""
        if is_composite:
            if isinstance(space, Tuple):
                return all((isinstance(sub_space, Box) for sub_space in space.spaces))
            elif isinstance(space, Dict):
                return all((isinstance(sub_space, Box) for sub_space in space.spaces.values()))
        return isinstance(space, Box)
    cont_action = is_space_continuous(env.action_space, tuple_action)
    cont_obs = is_space_continuous(env.observation_space, tuple_obs)
    return (cont_action, cont_obs)

import re
from typing import Any, Dict, Optional
class KernelBase:
    """Minimal base class for all kernels.  Do NOT modify this class."""

    def __init__(self, **kwargs: Any) -> None:
        self.params: Dict[str, Any] = kwargs

    def set_params(self, state: Dict[str, Any]):
        """(Re)sets parameters from a previously stored summary dict."""
        hp = state.get('hyperparameters', {})
        self.params.update({k: v for (k, v) in hp.items() if k != 'id'})
        return self

    def summary(self) -> Dict[str, Any]:
        """Dictionary description that can be fed back to *initialize_kernel*."""
        return {'hyperparameters': {'id': self.__class__.__name__, **self.params}}
class LinearKernel(KernelBase):

    def __init__(self):
        super().__init__()
class PolynomialKernel(KernelBase):

    def __init__(self, degree: int=3, coef0: float=1.0):
        super().__init__(degree=degree, coef0=coef0)
class RBFKernel(KernelBase):

    def __init__(self, gamma: float=1.0):
        super().__init__(gamma=gamma)
def initialize_kernel(param: Optional[Any]=None) -> KernelBase:
    """Create and return a kernel object from various representations.

    Args:
        param: None (default LinearKernel), an existing KernelBase instance, a
            string description such as 'RBF(gamma=0.5)', or a dictionary with a
            'hyperparameters' entry as produced by KernelBase.summary().

    Returns:
        KernelBase: the newly created or forwarded kernel instance.
    """
    if param is None:
        return LinearKernel()
    elif isinstance(param, KernelBase):
        return param
    elif isinstance(param, str):
        param = param.strip().lower()
        if param == 'linear':
            return LinearKernel()
        match = re.match('rbf\\(gamma=([0-9\\.]+)\\)', param)
        if match:
            gamma = float(match.group(1))
            return RBFKernel(gamma=gamma)
        match = re.match('polynomial\\(degree=([0-9]+),\\s*coef0=([0-9\\.]+)\\)', param)
        if match:
            degree = int(match.group(1))
            coef0 = float(match.group(2))
            return PolynomialKernel(degree=degree, coef0=coef0)
        else:
            raise NotImplementedError(f'Unknown kernel string: {param}')
    elif isinstance(param, dict):
        if 'hyperparameters' not in param:
            raise ValueError("Dictionary must contain 'hyperparameters' key")
        hp = param['hyperparameters']
        if 'id' not in hp:
            raise ValueError("Hyperparameters dictionary must contain 'id' key")
        id = hp['id']
        if id == 'LinearKernel':
            return LinearKernel()
        elif id == 'PolynomialKernel':
            degree = hp.get('degree', 3)
            coef0 = hp.get('coef0', 1.0)
            return PolynomialKernel(degree=degree, coef0=coef0)
        elif id == 'RBFKernel':
            gamma = hp.get('gamma', 1.0)
            return RBFKernel(gamma=gamma)
        else:
            raise NotImplementedError(f'Unknown kernel id: {id}')
    else:
        raise NotImplementedError(f'Unsupported input type: {type(param)}')

def accuracy(y_true, y_pred):
    """Compute accuracy."""
    correct = sum((1 for (t, p) in zip(y_true, y_pred) if t == p))
    total = len(y_true)
    return round(correct / total, 4)
def precision(y_true, y_pred):
    """Compute precision."""
    tp = sum((1 for (t, p) in zip(y_true, y_pred) if t == 1 and p == 1))
    fp = sum((1 for (t, p) in zip(y_true, y_pred) if t == 0 and p == 1))
    denominator = tp + fp
    if denominator == 0:
        return 0.0
    return round(tp / denominator, 4)
def recall(y_true, y_pred):
    """Compute recall."""
    tp = sum((1 for (t, p) in zip(y_true, y_pred) if t == 1 and p == 1))
    fn = sum((1 for (t, p) in zip(y_true, y_pred) if t == 1 and p == 0))
    denominator = tp + fn
    if denominator == 0:
        return 0.0
    return round(tp / denominator, 4)
def f1(y_true, y_pred):
    """Compute F1 score."""
    tp = sum((1 for (t, p) in zip(y_true, y_pred) if t == 1 and p == 1))
    fp = sum((1 for (t, p) in zip(y_true, y_pred) if t == 0 and p == 1))
    fn = sum((1 for (t, p) in zip(y_true, y_pred) if t == 1 and p == 0))
    precision_denominator = tp + fp
    recall_denominator = tp + fn
    if precision_denominator == 0 or recall_denominator == 0:
        return 0.0
    precision_val = tp / precision_denominator
    recall_val = tp / recall_denominator
    f1_score = 2 * (precision_val * recall_val) / (precision_val + recall_val)
    return round(f1_score, 4)
def get_metric(name: str):
    """Return an evaluation metric function by *name*.

    Supported names:
    - 'accuracy'
    - 'precision'
    - 'recall'
    - 'f1'

    If the name is invalid, the function should return -1.
    The returned metric functions must accept two equal-length iterables
    (y_true, y_pred) containing only 0s and 1s and return the metric value
    rounded to 4 decimal places.
    """
    metrics = {'accuracy': accuracy, 'precision': precision, 'recall': recall, 'f1': f1}
    return metrics.get(name, -1)

import numpy as np
def gbdt_regression(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, n_estimators: int=10, learning_rate: float=0.1, max_depth: int=3) -> list[float]:
    """
    Gradient Boosting Decision Tree (GBDT) regressor.

    Parameters
    ----------
    X_train : np.ndarray
        2-D array with shape (n_samples, n_features) containing the training
        features.
    y_train : np.ndarray
        1-D array of length n_samples containing the training targets.
    X_test : np.ndarray
        2-D array with shape (m_samples, n_features) containing the test
        features to predict.
    n_estimators : int, default=10
        Number of boosting iterations.
    learning_rate : float, default=0.1
        Shrinkage factor applied to each tree's prediction.
    max_depth : int, default=3
        Maximum depth of every individual regression tree.

    Returns
    -------
    list[float]
        Predictions for every sample in *X_test*, rounded to 4 decimal places.
    """
    y_pred = np.full(X_test.shape[0], np.mean(y_train))
    residuals = y_train.copy()
    for _ in range(n_estimators):
        tree = CARTRegressionTree(max_depth)
        tree.fit(X_train, residuals)
        y_pred_tree = tree.predict(X_test)
        y_pred += learning_rate * y_pred_tree
        residuals -= learning_rate * tree.predict(X_train)
    return [round(pred, 4) for pred in y_pred]
class CARTRegressionTree:

    def __init__(self, max_depth: int):
        self.max_depth = max_depth
        self.tree = None

    def fit(self, X: np.ndarray, y: np.ndarray):
        self.tree = self._build_tree(X, y, 0)

    def predict(self, X: np.ndarray) -> np.ndarray:
        return np.array([self._predict_sample(x, self.tree) for x in X])

    def _build_tree(self, X: np.ndarray, y: np.ndarray, depth: int):
        if len(np.unique(y)) == 1 or depth == self.max_depth:
            return np.mean(y)
        (best_feature, best_threshold) = self._find_best_split(X, y)
        if best_feature is None:
            return np.mean(y)
        (left_idx, right_idx) = (X[:, best_feature] < best_threshold, X[:, best_feature] >= best_threshold)
        left_tree = self._build_tree(X[left_idx], y[left_idx], depth + 1)
        right_tree = self._build_tree(X[right_idx], y[right_idx], depth + 1)
        return {'feature': best_feature, 'threshold': best_threshold, 'left': left_tree, 'right': right_tree}

    def _find_best_split(self, X: np.ndarray, y: np.ndarray):
        (best_feature, best_threshold) = (None, None)
        best_mse = np.inf
        for feature in range(X.shape[1]):
            thresholds = np.unique(X[:, feature])
            for threshold in thresholds:
                (left_idx, right_idx) = (X[:, feature] < threshold, X[:, feature] >= threshold)
                if not left_idx.any() or not right_idx.any():
                    continue
                left_mse = np.mean((y[left_idx] - np.mean(y[left_idx])) ** 2)
                right_mse = np.mean((y[right_idx] - np.mean(y[right_idx])) ** 2)
                mse = (np.sum(left_idx) * left_mse + np.sum(right_idx) * right_mse) / len(y)
                if mse < best_mse:
                    (best_mse, best_feature, best_threshold) = (mse, feature, threshold)
        return (best_feature, best_threshold)

    def _predict_sample(self, x: np.ndarray, tree):
        if isinstance(tree, (int, float)):
            return tree
        (feature, threshold, left, right) = (tree['feature'], tree['threshold'], tree['left'], tree['right'])
        if x[feature] < threshold:
            return self._predict_sample(x, left)
        else:
            return self._predict_sample(x, right)

import numpy as np
def bayesian_linear_regression_pred(X: np.ndarray, y: np.ndarray, X_new: np.ndarray, mu: float | np.ndarray=0.0, sigma: float=1.0, V: float | np.ndarray | None=None, fit_intercept: bool=True) -> list[float]:
    """
    Bayesian linear regression with known variance.

    The function returns the MAP predictive means for every row in
    `X_new`.  See the task description for full details.
    """
    if fit_intercept:
        X = np.hstack((np.ones((X.shape[0], 1)), X))
        X_new = np.hstack((np.ones((X_new.shape[0], 1)), X_new))
    if isinstance(mu, float):
        M = X.shape[1]
        mu = np.full(M, mu)
    else:
        mu = np.array(mu)
    if V is None:
        V_inv = np.eye(X.shape[1])
    elif isinstance(V, float):
        V_inv = np.eye(X.shape[1]) / V
    elif isinstance(V, np.ndarray) and V.ndim == 1:
        V_inv = np.diag(1 / V)
    else:
        V_inv = np.linalg.inv(V)
    XtX = np.dot(X.T, X)
    Sigma_p = np.linalg.inv(V_inv + XtX)
    Xt_y = np.dot(X.T, y)
    if isinstance(V_inv, np.ndarray):
        mu_p = np.dot(Sigma_p, np.dot(V_inv, mu) + Xt_y)
    else:
        mu_p = np.dot(Sigma_p, V_inv @ mu + Xt_y)
    y_pred = np.dot(X_new, mu_p)
    return [round(val, 4) for val in y_pred]

import numpy as np
def entropy(y: list[int]) -> float:
    """Compute the Shannon entropy of a label sequence.

    Args:
        y: A non-empty list of non-negative integers representing class labels.

    Returns:
        The Shannon entropy of the sequence, rounded to 4 decimal places.
    """
    label_counts = {}
    for label in y:
        if label in label_counts:
            label_counts[label] += 1
        else:
            label_counts[label] = 1
    total_labels = len(y)
    entropy_value = 0.0
    for label in label_counts:
        p_i = label_counts[label] / total_labels
        if p_i > 0:
            entropy_value -= p_i * np.log2(p_i)
    return round(entropy_value, 4)

def classification_accuracy(y_true: list, y_pred: list) -> float:
    """Compute the accuracy between the true and predicted class labels.

    Args:
        y_true (list): Ground-truth class labels.
        y_pred (list): Predicted class labels produced by a model.

    Returns:
        float: The accuracy score rounded to 4 decimal places.
              Returns -1 when the inputs have different lengths or when
              either of the inputs is empty.
    """
    if len(y_true) != len(y_pred):
        return -1
    if len(y_true) == 0:
        return -1
    correct_predictions = 0
    for (true, pred) in zip(y_true, y_pred):
        if true == pred:
            correct_predictions += 1
    accuracy = correct_predictions / len(y_true)
    return round(accuracy, 4)

import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    x = np.asarray(x)
    if order not in [0, 1, 2]:
        return -1
    sigmoid_value = 1 / (1 + np.exp(-x))
    if order == 0:
        result = sigmoid_value
    elif order == 1:
        result = sigmoid_value * (1 - sigmoid_value)
    else:
        result = sigmoid_value * (1 - sigmoid_value) * (1 - 2 * sigmoid_value)
    result = np.round(result, 4)
    if result.shape == ():
        return float(result.item())
    else:
        return result.tolist()

import numpy as np
def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """
    Compute the Radial Basis Function (RBF) kernel matrix.

    The function should follow the specifications given in the task
    description. It must return -1 on invalid input, otherwise a nested list
    containing the kernel matrix rounded to four decimal places.
    """
    X = np.array(X)
    if Y is not None:
        Y = np.array(Y)
    else:
        Y = X
    if X.shape[1] != Y.shape[1]:
        return -1
    C = X.shape[1]
    if sigma is None:
        sigma = np.sqrt(C / 2)
    elif isinstance(sigma, list):
        if len(sigma) != C:
            return -1
        sigma = np.array(sigma)
    elif isinstance(sigma, float):
        if sigma <= 0:
            return -1
    else:
        return -1
    (N, M) = (X.shape[0], Y.shape[0])
    kernel_matrix = np.zeros((N, M))
    for i in range(N):
        for j in range(M):
            if isinstance(sigma, float):
                dist_squared = np.sum((X[i] - Y[j]) ** 2) / sigma ** 2
            else:
                dist_squared = np.sum(((X[i] - Y[j]) / sigma) ** 2)
            kernel_matrix[i, j] = np.exp(-0.5 * dist_squared)
    return [[round(val, 4) for val in row] for row in kernel_matrix]

import math
def compute_ball_radius(centroid: list[float], X: list[list[float]]) -> float:
    """Compute the maximum Euclidean distance between *centroid* and the points in *X* (i.e., the Ball-tree node radius), rounded to 4 decimals.
    """
    if not X:
        return 0.0
    max_distance = 0
    for point in X:
        distance_squared = sum(((a - b) ** 2 for (a, b) in zip(centroid, point)))
        distance = math.sqrt(distance_squared)
        max_distance = max(max_distance, distance)
    return round(max_distance, 4)

