import numpy as np
from itertools import combinations_with_replacement
def polynomial_features(X: list[list[int | float]], degree: int) -> list[list[float]]:
    if degree < 0:
        return -1
    X = np.array(X, dtype=float)
    (n_samples, n_features) = X.shape
    result = []
    for x in X:
        row = []
        for total_degree in range(degree + 1):
            combos = list(combinations_with_replacement(range(n_features), total_degree))
            for combo in combos:
                monomial = 1.0
                for (feature_idx, power) in enumerate(combo):
                    monomial *= x[feature_idx] ** power
                row.append(monomial)
        result.append(row)
    return [row.tolist() for row in result]

