import numpy as np
def sigmoid_grad(z):
    """Return the derivative of the sigmoid function given its output.

    The input *z* already contains sigmoid values (numbers in [0,1]).  The
    derivative is computed as z*(1-z) element-wise.

    Args:
        z: float, list or NumPy ndarray of sigmoid outputs.

    Returns:
        Same structure as *z* (float or nested list) with each value replaced
        by its gradient, rounded to 4 decimal places.
    """
    if isinstance(z, (float, int)):
        return float(np.round(z * (1 - z), 4))
    elif isinstance(z, list):
        result = []
        for i in z:
            if isinstance(i, list):
                result.append(sigmoid_grad(i))
            else:
                result.append(float(np.round(i * (1 - i), 4)))
        return result
    elif isinstance(z, np.ndarray):
        return np.round(z * (1 - z), 4).tolist()
    else:
        return []

import numpy as np
def relu_grad(z):
    """Compute the element-wise derivative of the ReLU activation.

    The function should return 0 for every entry that is less than or equal to
    zero and 1 for every entry that is strictly greater than zero.  Scalars
    must yield a single integer; arrays/lists must yield a list of identical
    shape.
    """
    if isinstance(z, np.ndarray):
        if z.ndim == 0:
            return 1 if z > 0 else 0
        else:
            result = np.where(z > 0, 1, 0)
            return result.tolist()
    elif isinstance(z, list):
        result = []
        for elem in z:
            if isinstance(elem, list):
                result.append(relu_grad(elem))
            else:
                result.append(1 if elem > 0 else 0)
        return result
    elif isinstance(z, (int, float)):
        return 1 if z > 0 else 0
    else:
        raise ValueError('Input must be a scalar, list, or numpy array')

import numpy as np
def decision_boundary_grid(X: list[list[int | float]], W: list[int | float], b: float, grid_n: int=100) -> list[list[int]]:
    X = np.array(X)
    (min_x1, max_x1) = (np.min(X[:, 0]), np.max(X[:, 0]))
    (min_x2, max_x2) = (np.min(X[:, 1]), np.max(X[:, 1]))
    x1_plot = np.linspace(min_x1, max_x1, grid_n)
    x2_plot = np.linspace(min_x2, max_x2, grid_n)
    (x1_mesh, x2_mesh) = np.meshgrid(x1_plot, x2_plot)
    predictions = np.zeros((grid_n, grid_n))
    for i in range(grid_n):
        for j in range(grid_n):
            x1 = x1_mesh[i, j]
            x2 = x2_mesh[i, j]
            z = W[0] * x1 + W[1] * x2 + b
            if z >= 0:
                predictions[i, j] = 1
            else:
                predictions[i, j] = -1
    return predictions.tolist()

